css:
@-moz-document domain("naoentreaki.com.br") {
html{background-color: #141414}
.main-menu {background-color: #191919; box-shadow: 0 0 10px 0px black;}

.post-thumbnail img, .post-thumbnail video {border-radius: 18px;}

.uk-article-title a {color: #fff; font: 18px;}
.uk-article+.uk-article {margin-top: 1px; padding-top: 30px; border-top: 1px solid #141414;}

.button-muerte {background-color: #141414; border-radius: 8px; border-color: #141414; padding-left: 1.1rem; padding-right: 1.1rem;}
.button-arriba {background-color: #141414; border-radius: 8px; border-color: #141414; padding-left: 1.1rem; padding-right: 1.1rem;}

.button-muerte:active {background-color: #000000;}
.button-arriba:active {background-color: #000000;}

.area-comment {background-color: #141414; border-radius: 8px; border-color: #141414; padding-left: 1.1rem; padding-right: 1.1rem;  color: #fff;}
.area-comment:hover {background-color: #000000; border-radius: 8px; color: #fff;}

.post-fixed-bar {background: #141414;}

.uk-article-title {color: #fff;}

.uk-contrast .uk-button {color: #fff; border-color: #191919;}
.uk-contrast .uk-button:focus, .uk-contrast .uk-button:hover {background-color: #000; border-color: #000;}

.uk-button  {background-color: #141414; border-radius: 8px; border-color: #141414; padding-left: 0.8rem; padding-right: 0.8rem; color: #fff;}
.uk-button:hover {background-color: #000000; border-radius: 8px; color: #fff;}

.uk-link-muted, .uk-link-muted a, .uk-link-muted a:hover, .uk-link-muted:hover {color: #fff;}
.uk-text-muted {color: #fff!important;}

.uk-open>.uk-dropdown, .uk-open>.uk-dropdown-blank {background-color: #141414; border-color: #141414; border-radius: 8px; box-shadow: 0 0 10px 0px black;}
.uk-list-line>li:nth-child(n+2), .uk-list-striped>li:first-child, .uk-modal-footer {border-top: 1px solid #141414;}
hr {border-top: 1px solid #141414;}
}

@-moz-document domain("naoentreaki.com.br") {
.submenu {visibility:hidden;}
.main-menu #rx-js-search {visibility:hidden;}
.uk-grid+.uk-grid, .uk-grid-margin, .uk-grid>*>.uk-panel+.uk-panel {visibility:hidden;}

.uk-grid {margin-left: -35px;}
.uk-border-circle {border-radius: 8px;}

.nsfw-post  {border-radius: 18px;}
.icon-fire-comment {font-size: 8px;}
.uk-width-1-1 {border-radius: 18px;}

.uk-width-large-3-5, .uk-width-large-6-10 {padding-left:6rem;}


.button-muerte:hover  {background-color: #000000;}
.button-arriba:hover {background-color: #000000;}

.button-vote-active {background-color: #000000;}
 



}

@-moz-document domain("naoentreaki.com.br") {
.uk-modal-footer>:last-child, .uk-modal-header>:last-child {color: #fff;}
.uk-modal-header {border-bottom: #191919; border-radius: 0px 0px 8 9; background: #191919;  box-shadow: 0 0 10px 0px black;}
.uk-modal-dialog {background: #141414;}
.uk-form-stacked .uk-form-label {color: #fff;}
.uk-modal-footer {background: #141414;}
.nsfw-post-alert {background: #141414;}
b, strong {color: #fff;}
.uk-placeholder>:last-child {color: #fff;}
address, blockquote, dl, fieldset, figure, ol, p, pre, ul {color: #fff;}
.uk-placeholder {border: #141414; background: #141414;}
.uk-text-primary {color: #fff!important;}
.uk-link, a {color: #fff;}
.uk-link:hover, a:hover {color: #fff;}

.uk-form-controls>:first-child {background-color: #141414; border-color: #141414;}
.uk-form input:not([type]):focus, .uk-form input[type=text]:focus, .uk-form input[type=password]:focus, .uk-form input[type=email]:focus, .uk-form input[type=url]:focus, .uk-form input[type=search]:focus, .uk-form input[type=tel]:focus, .uk-form input[type=number]:focus, .uk-form input[type=datetime]:focus, .uk-form input[type=datetime-local]:focus, .uk-form input[type=date]:focus, .uk-form input[type=month]:focus, .uk-form input[type=time]:focus, .uk-form input[type=week]:focus, .uk-form input[type=color]:focus, .uk-form select:focus, .uk-form textarea:focus {border-color: #141414; background: #141414; color: #fff;}

.nsfw-post-alert input[type=checkbox] {background: #191919; border-color: #191919;}
.uk-form input[type=checkbox]:checked:before, .uk-form input[type=checkbox]:indeterminate:before {color: #000;}

}

@-moz-document domain("naoentreaki.com.br") {
.uk-panel-box-primary, .uk-panel-box-secondary {background-color: #141414; color: #fff; border-color: #141414;}
.uk-form-blank:focus {border-color: #141414!important;}
.nzn-footer-copy, .nzn-footer-group, .nzn-footer-site {border-top: 1px solid #121212;}
.nzn-footer-group {background: #121212;}
.nzn-footer-copy {background: #121212;}

.nzn-footer-list>li>a {color: #fff;}

.uk-alert-danger {background: #191919; color: #fff; border-color: #191919;}    

.uk-article-meta {color: #fff;}

.uk-panel-title {color: #fff;}


}

@-moz-document domain("naoentreaki.com.br") {





}
