css:
@-moz-document domain(music.yandex.ru), domain(music.yandex.com), domain(music.yandex.kz), domain(music.yandex.by), domain(music.yandex.kz), domain(music.yandex.ua), url-prefix(https://yastatic.net/tableau/tableau.html), url-prefix(https://widget.tickets.yandex.ru/)
{
.track__ver {
	color: inherit !important;
}

.d-track__title {
	text-overflow: clip !important;
	display: inline-block !important;
	flex: none !important;
}

[data-b="18"], .bar-below__circles *,  .notify, .no-ads, div[data-b="2"],  .multi-auth__plus, .ads-block__no-ads, .ads-block, .bar-below, .d-overhead-mobile, .d-overhead {
    display: none !important;
}


.playlist__texts, .playlist__title-cover {
	display: inline-block !important;
    width:auto !important;
    max-width: 100% !important;
}

.playlist__title-cover {
    height: 10px !important;
}

 .bar-below__content {
    background: inherit !important;
}
}
