}
.mainbody {
	border:none;
}
.logo {
	display:none;
}
.head {
	background:#FFF;
}
#bodyDiv>#mainDiv{
	top:150px;
}
.info {
	background:#FFF url(http://www.hdu.edu.cn/asset/home/images/logo.png) no-repeat;
	background-size:auto 40px;
	background-position:50px 5px;
	width:100%;
}
.info, .info ul, .info ul li {
	height:50px;
	line-height:50px;
}
.info ul li {
	font-size:14px;
	font-weight:400;
	background:none;
}
.info ul li:nth-of-type(1) {
	border-right:1px solid #AAA;
}
.info ul li a:hover {
	background:none;
	color:#666;
}
/*菜单样式*/
 .nav {
	border-bottom: #DDD 1px solid;
	margin-bottom: 100px;
	width: 90%;
	padding-left: 0;
	margin-left: 50px;
	margin-right: 15%;
}
.nav, .nav li {
	height: 48px;
	line-height:48px;
	background: #FFF;
	text-align:center;
}
.top_link {
	background: #FFF!important;
}
.nav li:hover a.top_link span {
	transition: color .2s;
	border-bottom: 3px solid #1890ff!important;
	color: #1890ff!important;
}
.nav li a.top_link span {
	height: 48px!important;
	cursor: pointer;
	margin: 0;
	text-align:center;
	padding: 0 20px!important;
	position: relative;
	display: block;
	white-space: nowrap;
	line-height: 48px;
	-webkit-box-sizing: border-box;
	box-sizing: border-box;
	margin: 0;
	outline: none;
	padding-left: 0;
	list-style: none;
	color: rgba(0, 0, 0, .65);
	background: #fff!important;
	-webkit-transition: background .3s, width .2s;
	transition: background .3s, width .2s;
	color: rgba(0, 0, 0, .65);
	font-weight:400;
}
.nav :hover ul.sub {
	top: 48px;
	background: #FFF;
	padding: 5px;
	border: none;
	box-shadow: 0 0 5px #888888;
	border-radius:4px;
	width:calc(100% + 40px);
	left:-20px;
}
.nav :hover ul.sub li {
	outline:none;
	border-radius:4px;
	width:100%;
	height: 40px;
	font-size:14px;
}
.nav :hover ul.sub li a {
	height: 40px;
	line-height: 40px;
	text-indent: 0;
	text-align:center;
	color: #000;
	overflow:hidden;
	text-overflow:ellipsis;
	white-space: nowrap;
}
.nav :hover ul.sub li:hover a {
	transition: color .2s;
	color:#0087ff;
	background:#FFF;
}
.nav li:nth-of-type(9) ul.sub li:nth-child(even), .nav li:nth-of-type(6) ul.sub li:nth-child(9) {
	display:none;
}
.location em {
	top: 100px;
	background: #FFF url(data:image/png;base64,) no-repeat;
	background-size:15px 15px;
	box-shadow: 0 3px 3px #DDD;
	background-position:50px 12px;
	position: absolute;
	line-height: 40px;
	left: 0;
	padding-left: 70px;
	width: 100%;
	color:#0A0A0A;
	height: 40px;
}
/*内容样式*/
