css:
@-moz-document domain("app.grammarly.com") {
body {
    color: #d7d7d7;
}

/*Links*/
._c9ce55-link,
._c9ce55-username {
    font-weight: 600;
}

/*Main Content*/
body,
._b198bc-main {
    background-color: #323439;
}

/*Sidemenu*/
._c9ce55-main {
    background-color: #24262b;
}

._a499ad-tabs,
._04aad7-delete {
    border-color: #232428;
}

/*My Grammarly Section*/
._5a78f1-banner {
    background-color: #24262b;
    box-shadow: none;
    border-radius: .25rem;
}

/*Documents*/
._4fdedb-itemWrapper,
._4fdedb-itemWrapper:hover {
    box-shadow: none;
}

._4fdedb-item {
    background-color: #24262b;
    border-radius: .25rem;
    box-shadow: none;
    overflow: hidden;
}

._4fdedb-new ._4fdedb-add,
._4fdedb-actions > div {
    color: #e2534f;
    opacity: 1;
}

._4fdedb-actions {
    background-color: #2a2c31;
    border: none;
}

._4fdedb-title {
    color: #fff;
}

._4fdedb-content {
    color: #d7d7d7;
    font-weight: 500;
}

/*Profile Sectiom*/
/*Headers*/
._0218f4-header,
._04aad7-label {
    color: #fff;
}

/*User Info*/
._04aad7-editTable {
    color: #d7d7d7;
    font-weight: 600;
}

/*Language Preference Block*/
._2cdf1d-diffTable {
	background-color: #24262b;
    border-color: #232428;
    box-shadow: 0 .25rem 1.25rem 0 rgba(0,0,0,.2);
}

._2cdf1d-diffSeparator {
    border-color: #232428;
}

/*Headers*/
._2cdf1d-diffTitle,
._2cdf1d-diffCellTitle,
._2cdf1d-exampleSubtitle {
    color: #fff;
}

/*Descriptions*/
._9bec40-description,
._2cdf1d-smallDescription,
._2cdf1d-diffCellDescr,
._2cdf1d-exampleItem {
    color: #d7d7d7;
    font-weight: 600;
}

/*Buttons*/
._d0e45e-transparent,
._7f3d9f-appsBtn,
._5a78f1-install {
    background: #e2534f;
    border-radius: .25rem;
    color: #fff;
    transition-property: background-color, transform;
    transition-duration: 100ms;
    transition-timing-function: ease-in-out;
}

._d0e45e-transparent:hover,
._d0e45e-transparent:active,
._d0e45e-transparent[disabled],
._7f3d9f-appsBtn:hover,
._7f3d9f-appsBtn:active,
._7f3d9f-appsBtn[disabled],
._5a78f1-install:hover,
._5a78f1-install:active,
._5a78f1-install[disabled] {
    background: #f77371;
    border-color: #f77371;
    color: #fff;
}

._d0e45e-transparent:active,
._7f3d9f-appsBtn:active,
._5a78f1-install:active {
    transform: translateY(2px);
}

._7f3d9f-active {
    border-radius: .25rem;
    font-weight: 600;
}

/*remind me later, dismiss*/
._5a78f1-button {
    font-weight: 600;
}

/*Search Button*/
._f33c0e-btn {
    background-size: cover;
    height: 1rem;
    top: 50%;
    right: 1rem;
    transform: translateY(-50%);
    width: 1rem;
}

/*Loader*/
._6f2b1cc8-spinner-item {
    background-color: #fff!important;
}

/*Input*/
._9bec40-inputWrap input,
._f33c0e-input {
    background-color: #24262b;
    border-radius: .25rem;
    border: none;
    color: #fff;
    height: 40px;
    font-weight: 600;
}

._9bec40-inputWrap {
    align-items: center;
    display: flex;
}

._f33c0e-search {
    background-color: transparent;
    background-color: #24262b;
    border-radius: .25rem;
    border: none;
}

/*Personal Dictionary*/
._9bec40-letterGroup,
._9bec40-letterGroup:last-child {
    border-color: #232428;
}

/*Alert*/
._c52c18-notification._c52c18-show {
    border-radius: 0 0 .25rem .25rem;
    box-shadow: none;
    overflow: hidden;
}

._c52c18-notification ._c52c18-content {
    background-color: #ffcf00;
    color: #000;
    font-weight: 600;
}

/*Apps Section*/
._7f3d9f-app {
    background-color: #24262b;
}

._7f3d9f-title {
    color: #fff;
}

._7f3d9f-text {
    color: #d7d7d7;
}

/*Grammarly Editor*/
/*Loader*/
._7a7bbbfc-view-baseLoaderClass,
._d52f59d8-loader-wrapper,
._50e8c067-loader-documentTitle,
._2443845c-loader-documentTitleWrap:before {
    background-color: #323439;
}

._2434e46e-loader-loader {
    background-image: linear-gradient(-179deg,#323439,#7a8194 30%,#323439 60%)
}

._6eef73be-loader-documentLines {
    background-image: repeating-linear-gradient(180deg,hsla(0,0%,100%,0),hsla(0,0%,100%,0) 1rem,#323439 0,#323439 2rem);
}

/*Header Background*/
._b17ed844-editor-toolbarTitleContainer {
    background: none;
}

/*Titles*/
._81cb6eea-editor-title,
._f0b953d7-typography-header1 {
    color: #fff;
}

/*Text*/
._360a492d-denali-editor-editor p {
    color: #d7d7d7;
    font-weight: 400;
}
}
