css:
@-moz-document domain("notabug.io") {
#header {
    border-bottom: 1px solid #5f99cf;
    background-color: #746467;
}

body {
    font: normal x-small verdana,arial,helvetica,sans-serif;
    background-color: #262626;
}

.thing .title {
    color: #dedede;
}

  .title:visited
  {
    color: #ABABAD !important;
  }

.tabmenu li a {
    padding: 2px 6px 0;
    background-color: #262626;
}

a {
    color: #8cb3d9;
}

.tabmenu li.selected a {
    color: #ff4500;
    background-color: #262626;
    border: 1px solid #5f99cf;
    border-bottom: 1px solid #fff0;
}

.pagename a {
    color: #8cb3d9;
}

.side {

    background-color: #262626;

}

.titlebox h1 a {

    color: #ddd;
}

.md {

    color: #ddd;

}

input, textarea, .linkinfo .shortlink input, .new-comment .usertext-body, .morelink a, .morelink:hover a, .fancy-toggle-button a, .usertext button {
    background: #444 !important;
    color: #8cb3d9 !important;
}


.md .-blocks > :first-child, .md .-cells > :first-child, .md .-headers > :first-child, .md .-lists > :first-child, .md > :first-child, .md blockquote > :first-child, .md h1 > :first-child, .md h2 > :first-child, .md h3 > :first-child, .md h4 > :first-child, .md h5 > :first-child, .md h6 > :first-child, .md ol > :first-child, .md p > :first-child, .md pre > :first-child, .md table > :first-child, .md td > :first-child, .md th > :first-child, .md ul > :first-child {
    color: #a0a0a0;
}


*, ::before, ::after {
    border: none !important;
    color: #777;
}

.mlhn, .morelink:hover .nub {

    background-image: url(data:image/png;base64,);
    display: none;

}

.mlhn, .morelink .nub {

    background-image: url(data:image/png;base64,);
    display: none;

}

.link .usertext-body .md {
    background-color: #373737 !important;
    border: 1px solid #369;
}

#header-bottom-right {
    background-color: #333333;
}

.expando-button {
    background: #fff0 none no-repeat scroll 50%;
        background-image: none;
}

.chat-modal {
    background-color: #212121;
    border: 1px solid #5f99cf;

}

}
