css:
@-moz-document url-prefix("http://online.sim.polbeng.ac.id/") {
h1, h2{
	display:none;
}
#header{
	content:"aa";
	background-image:url(http://polbeng.ac.id/images/polbeng.png);
	background-repeat:no-repeat;
	background-position:center;
}
.splitcontentleft{
	display:none;
}
.splitcontentright{
	float:none;
	padding:10px;
	margin:auto;
	margin-bottom:100px;

}
.splitcontentright table.formlogin{
	border:1px solid blue;
}
.splitcontentright table.formlogin td{
	padding:5px;
}
table.formlogin tr:nth-child(1){
	height:40px;
}
input, select{
	padding:8px;
	border:1px solid #5395ef;
	border-radius:4px;
}
input[type=button]{
	min-width:100px;
	background-color:white;

}
#footer p{
	display:none;
}
#footer{
	text-align:center;
}
#footer:after{
	content:"@ 2019";
	
}
body{
	background:aliceblue !important;
}
#container{
	border:1px solid blue ;
	border-radius:10px;
	
}
#footer{
	border-radius:0 0 10px 10px;
}
#header{
	border-radius:10px 10px 0 0;
}
.mainmenu > ul ul{
	opacity:0;
	transition:0.5s;
	top:-5px;
}

.mainmenu > ul > li:hover > ul{
	opacity:1;
	height:inherit;
	top:30px;
}
}
