css:
@-moz-document regexp("^.*:8080\\/tfs\\/.*$") {
/* i really want this to be global */

.board-view {
	background: #555;
}
  
.hub-content, .main-content {
    background: #222;
    color: #ccc;
}

.grid-gutter .grid-gutter-cell .grid-gutter-drop .grid-gutter-menu,
.hub-pivot-content,
.header-post11 #navWrapper,
.header-post11 #header-row,
.toolbar .menu-bar .menu-item.hover,
.workitemcontrol .menu-bar .menu-item.hover,
.ui-tabs .ui-tabs-nav,
.content-section
{
	background: #222;
}


.grid-gutter-row {
	background: #222 !important;
}

            
.blank-message {
    background: #332233;
    color: #CC9900;
}
  
.node-content {
	color: #ccc !important;
}

.grid-row, 
.modal-dialog label, 
.hub-title, .hub-pivot a,
.text, 
.pivot-filter-item a, 
.work-item-view legend,
.taskboard-cell .witRemainingWork {
	color: #ccc;
}
  
.board-tile {
	background: #333344 !important;
}

.taskboard-cell .witTitle {
	color: #999;
}
    
.tbTile .tbTileContent {
    background: #332233;
    border: none;
}

.tbTile .tbTileContent .witTitle {
  color: #999966;
}


.header-post11 .header-search,
.header-post11 .header-search > .search-text,
.splitter > .handleBar, .splitter > .handleBar-clone
{
background-color: #111;
}

.sidebar-search .search-input-wrapper,
.tree-view > ul.tree-children li.selected > a > div.node-content, .tree-view.focus > ul.tree-children li.selected > a > div.node-content, li.tree-drag-tile > a > div.node-content,
.tree-view > ul.tree-children li.focus > a > div.node-content, 
.tree-view > ul.tree-children li.hover > a > div.node-content, 
.tree-view > ul.tree-children li.context-menu-active > a > div.node-content 
{
    background-color: #222;
    border: 1px solid #111;
}

.grid-gutter-row-normal {
	border: 1px solid transparent;
}

.grid-row-current {
	border: 1px solid #332233;
}

.hub-splitter.splitter.horizontal > .leftPane {
	border-right: 1px solid #111;
}

.build-detail-view .header {
    background-color: #222;
    border-bottom: 1px solid #111;
}

.toolbar {
    border-top: 1px solid transparent;
    border-bottom: 1px solid #333;
}

.hub-pivot {
	color: #444;
}

.hub-pivot {
	background: #2a2a2a;
}

.add-panel {
    background-color: #222;
    border-left-color: #111;
}

.combo {
    border: 1px solid #333;
    background: #282828;
    min-height:23px;
}

.ui-dialog-buttonpane {
    background-color: #222;
	border: 1px solid #222;
}

input {
    background: #282828;
    color: #ccc;
}

.ui-dialog {
	background-color: #262626;
    box-shadow: #111 0 0 270px;
}

body {
    color: white;
    background-color: #222;
}

.ui-widget-content {
	border: 1px solid #333;
}

.ui-dialog .ui-dialog-titlebar-close {
	background-color: #666;
}
            
.grid-row:hover {
	background: #442233;
}

.grid-row-current,
.splitter > .handleBar,
.splitter > .handleBar-clone {
	background: #333;
}

.grid-row-selected, .grid-row-selected-blur {
	background: #332233 !important;
}

.ui-dialog .ui-dialog-titlebar {
    background-color: #333;
    border-bottom: 1px solid #111;
}

.splitter > .handleBar.hover,
.visual-progress-total-unallocated
{
    background-color: #444;
}

.versioncontrol-explorer-view .source-path {
    border: 1px solid #111;
    background-color: #222;
}


.ui-widget-overlay {
    background: #332233;
}

.splitter.toggle-button-enabled.collapsed > .handleBar {
    background-color: #111;
}

.queue-build select {
    border: 1px solid #111;
}

input, textarea, select {
    background-color: #282828;
}

.queue-build input[type='text'] {
    border: 1px solid #111;
}

.group-info-input,
.workitem-info-bar,
.ui-dialog .ui-dialog-title,
.ui-tabs .ui-tabs-nav li a,
.ui-tabs .ui-tabs-nav li.ui-tabs-selected a {
	color: #fff;
}

input, button, textarea, select {
	color: #ddd;
}

.grid-row-current:hover,.grid-row-current.grid-row-selected {
    border: 1px solid #332233;
}

.triage-view .splitter.horizontal .work-item-form {
    border-left: none;
}

.query-folder-tree .folder.top-separator {
    border-top: 1px solid #333;
}

.tree-view > ul.tree-children li > a > div.node-content {
    min-height: 28px;
    margin-bottom: none;
}

.tree-view > ul.tree-children li.selected > a > div.node-content, .tree-view.focus > ul.tree-children li.selected > a > div.node-content, li.tree-drag-tile > a > div.node-content {
    border: 1px solid #332233;
    background: #332233;
}   
.tree-view > ul.tree-children li.focus > a > div.node-content, .tree-view.focus > ul.tree-children li.focus > a > div.node-content, li.tree-drag-tile > a > div.node-content {
    border: 1px solid #332233;
}

.richeditor-container {
    border: solid #333;
    border-width: 1px 0 0 0;
}

button, button.ui-state-default, button.ui-button {
    color: #ababab;
    background-color: #2a2a2a;
    border: 1px solid #555;
}

button:hover, button:focus {
    border: 1px solid #777;
    background-color: #777;
    color: #000;
}

button[disabled], button[disabled]:hover, button[disabled]:focus {
    border: 1px solid #444!important;
    background-color: #444!important;
    color: #333!important;
}
.capacity-pane {
    border-left-color: #333;
}
.capacity-pane>div {
    border-bottom-color: #333;
}
.combo-drop-popup {
 	background: #222;  
    border: 1px solid #333;
}

.menu.sub-menu {
	border-color: #777;   
    background: #222;
}

.menu-item-separator>.separator {
	background: #333!important;    
}

.menu.sub-menu>.menu-item.hover,
.menu.sub-menu>.menu-item.hover>.text,
.items>li:hover,
.items>li:active,
.items>li:focus,
.grid-header-column:hover,
.virtualizing-list-view>.items>li.selected,
.menu-item:not(.menu-item-separator):hover, 
.menu-item:not(.menu-item-separator):focus,
.menu.sub-menu>.menu-item:not(.menu-item-separator):hover, .menu-item:hover>.text,
.menu.sub-menu>.menu-item:not(.menu-item-separator):active, .menu-item:active>.text,
.menu.sub-menu>.menu-item:not(.menu-item-separator):focus, .menu-item:focus>.text {
	background: #332233!important;
	border-color: #332233!important;
}

::-webkit-scrollbar {
	width: 6px;
    height: 6px;
}
 
::-webkit-scrollbar-thumb {
  	background: #555;
}
::-webkit-scrollbar-thumb:hover {
  	background: #999;
}

::-webkit-scrollbar-track {
	background: #2a2a2a;
}

::-webkit-scrollbar-corner {
    background: #222;
}
  
.virtualizing-list-view>.items>li {
    color: #ccc;
}

.burndown-chart {
    filter: invert(85%);
}

.message-content {
	color: #ccc;
}
  
.vc-history-list .results-container .group-header {
    background-color: #2a2a2a;
}
}
