css:
@-moz-document domain(okoun.cz) {

#body {
    /*background-color: antiquewhite*/
}

/* font trochu vetsi */
.head .menu, .board .main .settings, .content, .item .meta, body.userlist .main .main, body.boardlist .main .main {
    font-size: 123%!important;
}

.actions{
    font-size: 105%!important;
}

/* FB kill */
.main .actions .share {display:none !important;}

/* reklamy fuc */
#adwrapper-lb, #adwrapper-bs {display:none !important}

/* uzsi pravy sidebar */
.main .main { width: 85%  !important; }

.main .ctx { width: 13%  !important; }

/* prispevky */
.item a.link {
    padding-left: unset; 
    background-image: unset; 
    background-position: unset;
}


.item .meta {
    margin-bottom: 0;
    font-size: 90%;
    padding-right: 0em;
}

.head .user .login input.text {
    width: 9em;
    border: 0;
    height: 24px;
}

.welcome, .item .content .confirm.reply, .item .actions .share:after, .item.new .actions .share:after {
     background-color: transparent;
}

.room .item.new, .room .item  {
     border: 0px; 
     border-bottom: 1px solid; 
}

.head h1 a {
     background-image: none;
}

.main {
    margin-left: 10px;
}

.pager ul li, .pager ul li.older, .pager ul li.oldest, .pager ul li.newer, .pager ul li.newest, .index .news .yui-g.first .yui-u  {
    background-color: unset; 
    background-image: none;
    font-size: large !important;
}
.pager ul { font-size: large }

.search input.text, .board .main .search input.text.authors {
    border: 0;
    height: 22px;
    padding-top: 2px;
    padding-bottom: 0px;
}

.boardlist h3 {
    font-size: 10px !important;
    margin-bottom: 0px;
    font-weight: inherit;
}

.boardlist .item {
    margin-bottom: 0px;
	width: 120%;
}

.post.content {
    /* background-color: #FFC180; */
    background-color: unset !important;
}

.user b {
    font-size: small;
    font-weight: normal;
}

  /* rozdeleni sloupcu */
.main .ctx {
    width: 20%;
    font-size: 9pt;
}
.main .main {
    width: 79%;
    font-size: 10pt !important;
}

/* ramecek prispevku */
.listing .item {
     padding-top: 0; 
     padding-bottom: 0; 
}

/* titulek audirka */
h2 {
    font-family: sans-serif; 
}

/* popisek audirka*/
div.descr {
    font-size: 10pt !important;
    display: contents;
/*    color: chocolate;*/
}


/* button s oblidenosti klubu*/
.yui-button.top {
    float: inherit;
    margin-bottom: 0.5em;
}

#body {
    margin: 0px;
    padding: 3px;
}

body {
   padding: 0;
}
}
