css:
@-moz-document domain("mail.protonmail.com") {
/* scrollbar */

::-webkit-scrollbar-track {
	background: transparent;
}

::-webkit-scrollbar {
	width: 6px;
}

::-webkit-scrollbar-thumb {
	background: #505061;
}

/* material */

*:not(li):not(.atom-loaderContainer):not(.atomLoader-item):not(.atomLoader-item2):not(.atomLoader-item3):not(.sidebar-btn-back) {
	border: none !important;
}

.pm_toolbar {
    box-shadow: none;
}

.navigation > li.active, .navigation > li:hover {
    border-top: 5px solid #ddd !important;
}

#pm_sidebar, .pm_toolbar, .headerSecuredDesktop-container {
    background: #505061;
}

/* settings */

#pm_settings, #pm_settings table, #pm_settings tbody, #pm_settings th, #pm_settings td, #pm_settings tr {
	background: #1c1c1c;
    color: #ddd;
}

#pm_settings table > * {
	border: 1px solid #2b2b2b !important;
}

#pm_settings code {
	color: #bbb;
}

#pm_settings a {
	color: #aaa;
}

.vpn-ressourcesSection-clients-list {
	background: #1c1c1c !important;
    box-shadow: none !important;
}

.vpn-ressourcesSection-clients-item > a {
	background: #2b2b2b !important;
    border-radius: 3px;
}

.pm_form input[type=email], .pm_form input[type=text], .pm_form input[type=search], .pm_form input[type=checkbox], .pm_form .customCheckbox-mask {
	background: #2b2b2b;
    color: #ddd !important;
}

.pm_select {
	background: #2b2b2b;
    box-shadow: inset 0 2px 5px rgba(0,0,0,.15) !important;
}

.pm_select * {
    color: #ddd !important;
}

.as-sortable-item-handle:before {
	transition: all 0.1s;
}

.settingsDashboard-plans:after, .settingsDashboard-plans:before {
	background: transparent;
}

.visual-label > img {
	filter: invert(100) hue-rotate(180deg);;
}

.pm_toggle {
	background: #2b2b2b;
    box-shadow: none;
}

.pm_toggle .knob {
	background: #505061 !important;
}

.pm_toggle *:not(.knob) {
	background: transparent !important;
}

.pm_button:not(.link) {
	background: #2b2b2b;
}

.pm_button.link {
    color: #9397cd !important;
}

.pm_button:focus, .pm_button:hover {
    color: #dfdfdf;
}

.alert {
	background: #2c2c2c;
    color: #ddd !important;
}

.alert.alert-success {
	background: #060;
}

.alert.alert-info {
	background: #043248;
}

/* toolbar */

.pm_buttons > .pm_trigger, .pm_buttons > button, .pm_buttons > a, .pm_buttons > .fa, .chooseLayoutBtns-container > *, .paginator > * * {
	background: #1c1c1c !important;
    color: #ddd !important;
}

.paginator * {
	box-shadow: none !important;
}

.readUnread-container.disabled *, .pm_buttons.disabled > * {
	box-shadow: inset 0 0 50rem 0 rgba(255, 255, 255, 0.2) !important;
    border-radius: 0;
}

.elements-selector-trigger-dropdown {
    color: #ddd !important;
}

.pm_dropdown {
	background: #1c1c1c;
    color: #ddd;
}

.pm_dropdown > button:hover {
	background: #1c1c1c !important;
    color: #aaa !important;
}

/* sidebar */

.conversation-wrapper {
	background: #1c1c1c;
}

.conversation-wrapper * {
    color: #ddd !important;
}

.conversation, .conversation.unread {
	background: #3c3c3c !important;
}

.conversation.read {
	background: #2b2b2b !important;
}

.conversation.active {
	background: #505061 !important;
}

/* content */

#pm_placeholder, #conversationHeader, #pm_latest, #pm_latest *:not(.storageBar):not(.storageBar-progress), #conversationHeader * {
	background: #1c1c1c;
    color: #ddd;
}

.storageBar {
	background: #2b2b2b;
}

.storageBar-progress {
	background: #3c3c3c;
}

#storageStatus * {
    color: #ddd !important;
}

#conversation-view, #pm_thread, #pm_main {
	background: #1c1c1c;
}

.summary, .details, .summary *, .details *:not(small):not(.fa):not(.pm_trigger):not(.message-actionBtn-reply):not(.message-actionBtn-replyall):not(.message-actionBtn-forward) {
	background: #505061 !important;
    color: #ddd !important;
}

#conversation-view .message .summary .time:after {
    background: linear-gradient(90deg,rgba(230, 234, 240, 0) 0, #505061)
}

}
