css:
@-moz-document domain("filter.meetcircle.com") {
* {
    color: #fff !important;
    background-color: #000 !important;
    font-family: mono !important;
}
header,
#content {
    display: none !important;
}
.container,
.loaded,
#warning_panel,
.warning,
.filter {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    width: 100% !important;
    height: 100% !important;
    max-height: initial !important;
}
.warning,
.filter {
    padding-top: 300px !important;
}
.warning div span,
.filter div span {
    position: relative !important;
    top: 40px !important;
    color: transparent !important;
    background: transparent !important;
}
.warning_close a {
    pointer-events: none !important;
    text-decoration: none !important;
    opacity: 1 !important;
    font-weight: 700 !important;
    font-size: inherit !important;
    position: absolute;
    left: 0;
    text-align: center;
    color: transparent !important;
    background-color: transparent !important;
    width: 100% !important;
}
.warning_close a:after {
    position: absolute;
    left: 0;
    top: 0;
    text-align: center;
    width: 100%;
    content: "[ REDACTED ]";
    color: #fff !important;
}
