css:
@-moz-document domain("www.swbfgamers.com") {
body {
    background-color: #141c1e;
    color: #c0c0c0;
}
#header {
    border-radius: 1em;
    background-size: auto 100%;
}
#mainarea {
    background-color: #141c1e;
    border-top: none;
}
#toolbar {
    background: none;
    border-top: none;
}
#nav {
    background-color: #030303;
    border-radius: 1em;
    overflow: hidden;
}
#nav li {
    background: none;
    background-color: #030303;
    color: #79bbbb;
}
#nav li:hover {
    background-color: #08222a;
    color: #a5fdfd;
}
#nav li a:hover {
    color: #a5fdfd;
}
div#wrapper {
    border-left: none;
    border-right: none;
    border-top: none;
    border-bottom: none;
}
div.cat_bar {
    background: none;
    background-color: #030303;
    color: #79bbbb;
    cursor: default;
}
div.cat_bar:hover {
    background-color: #0c3846;
    color: #9ff2f2;
}
.catbg,
.catbg2,
tr.catbg td,
tr.catbg2 td,
tr.catbg th,
tr.catbg2 th,
.catbg,
h3.catbg {
    background: none;
    background-color: #000;
    color: inherit;
    cursor: inherit;
}
h3.catbg:hover {
    background-color: #171717;
}
.windowbg {
    background-color: #475e68;
}
.windowbg span.topslice {
    background: none;
    background-color: transparent;
}
.windowbg span.topslice span {
    background: none;
}
.windowbg span.botslice {
    background: none;
    background-color: #e76ff9
}
.windowbg span.botslice span {
    background: none;
}
.windowbg2 {
    background-color: #515151;
}
.windowbg2 span.topslice {
    background: none;
}
.windowbg2 span.topslice span {
    background: none;
}
.windowbg2 span.botslice {
    background: none;
}
.windowbg2 span.botslice span {
    background: none;
}
td#sp_left,
td#sp_center,
td#sp_right {
    color: white;
}
a,
.bbc_color a,
a:link,
visited {
    color: #e169ff;
    text-decoration: none;
}
a:hover,
.bbc_color a:hover {
    color: #fec3ff;
    text-decoration: none;
}
.bbc_color strong span {
    color: white;
}
.smalltext {
    font-size: 10px;
    color: #b7b7b7;
}
blockquote.bbc_standard_quote {
    background-color: #577881;
    color: white;
    border-radius: 0.5em;
    border-color: #14c5da;
}
img {
    border-radius: 0.25em;
}
}
