@-moz-document domain("youtube.com") {
/* ------Interface Geral------ */

/*-----Logo do Youtube - iGTube-----*/
/* Logo do iGTube */
  #logo-icon-container > *, .ytp-chrome-controls .ytp-right-controls a.ytp-youtube-button > * , #player .ytp-watermark > *, #logo-icon > svg {
    display: none !important;
  }
  #logo-container .logo, .footer-logo-icon, #logo-icon-container, #yt-logo-svg, #logo-icon {
    background-image: url("https://i.imgur.com/CzQw3GY.png") !important;
    background-size: auto !important;
    background-position: center center !important;
    background-repeat: no-repeat !important;
    width: 120px !important;
    height: 40px !important;
  }


  /* logo em sites secundarios */
  .ytp-chrome-controls .ytp-right-controls a.ytp-youtube-button {
    background-image: url("https://i.imgur.com/CzQw3GY.png") !important;
    background-size: 80% auto !important;
    background-repeat: no-repeat !important;
    background-position: center center !important;

  }



/*-----Detalhes -----*/
/* fundo */

ytd-app, #page-manager > ytd-watch-flexy, ytd-app[background-color-update_]{ 
     background-image: linear-gradient(rgba(0, 0, 0, 0.7), rgba(0, 0, 0, 0.9)), url("https://i.imgur.com/IW84T7X.jpg") !important;
    background-attachment: fixed !important;
    background-size: cover !important;
    background-position: center center !important;
}

/* -----correção de erro partes sem o fundo----- */
