    --yt-live-chat-message-highlight-background-color: rgba(0, 0, 0, 0) !important;
    --yt-live-chat-background-color: rgba(0, 0, 0, 0) !important;
    --yt-live-chat-action-panel-background-color: rgba(0, 0, 0, 0) !important;

