/* Transparência da barra do canal*/
#tabs-container.ytd-c4-tabbed-header-renderer, #channel-header.ytd-c4-tabbed-header-renderer, #bg.ytd-interactive-tabbed-header-renderer{
	background-color: rgba(0, 0, 0, 0.64) !important;
	fill: rgba(0, 0, 0, 0);
}
html:not(.style-scope){
    --yt-spec-general-background-a: rgba(0, 0, 0, 0.64);
}

#header > ytd-interactive-tabbed-header-renderer * {
	--ytd-interactive-tabbed-header-renderer-banner-url: none;
}


/* ----borda de todas as sub janelas arredondadas e transparentes----*/
