    background: rgba(188, 63, 51, .7) !important;
    border-right: 0px solid !important;
    border-left: 0px solid !important;
}
/* botões vermelhos quando celecionados */
#ACCOUNTOVER:hover, #sb-hide:hover{
    background: rgba(188, 63, 51, .8) !important;
}
/* linhas verrmelhas */
