       box-shadow: 0px 0px 20px #00cc00, 0px 0px 20px #00cc00, 0px 0px 20px #00cc00 !important;
}

/* like */
ytd-toggle-button-renderer.style-default-active[is-icon-button]:nth-of-type(1){
