*/

/* Overall Background */
.site {
  background-color: #3f3f3f !important;
}

/* Overal general text */
body, button, input, select, textarea {
  color: #f0dfaf !important;
}

/* Header title */
body:not(.search-results) .entry-summary {
  color: #c3bc9c;
}

/* Recent Post Dates */
.widget_recent_entries .post-date {
  color: #c3bc9c;
}

element.style {
  -webkit-text-fill-color: #afafaf;
}

#content .tablepress thead th,
#content .tablepress tfoot th {
  background-color: #c0bed1;
  color: #333
}

.tablepress .even td {
  background-color: #efefaf;
  color: #333
}

.tablepress .odd td {
  background-color: #f0dfaf;
  color: #333
}

/* Comment Section */
.comment-author {
  color: #c3bc9c;
}

.comment-notes,
.comment-awaiting-moderation,
.logged-in-as,
.form-allowed-tags {
  color: #c3bc9c !important;
}

.comment-form label {
  color: #c3bc9c !important;
}

.post-navigation .meta-nav {
  color: #c3bc9c !important;
}

/* Custom site panels */
.vc_custom_1419328663991 {
  background-color: #4f4f4f !important;
}

/* Hyperlinks */
a:link {
  color: #8cd0d3 !important;
}

a:visited {
  color: #c0bed1 !important;
}

a:hover {
  color: #9c9ccc !important;
}

a:active {
  color: #ffd7a7 !important;
}
}
