css:
@-moz-document domain("edge-gamers.com") {
.body_wrapper, .floatcontainer, .smaller, .news-bar, #main {
    background-color: #1d1c1c;
}

.formcontrols .blockrow .dbtech_shouts .dbtech_vbshout_window .dbtech_vbshout_shout, .sticky, .nonsticky, .threadstats, .forum_info .blockbody, .toolsmenu, .forumrow, .pagination span.prev_next a, .pagination span.first_last a, .pagination span.selected a, .button, .pagination span a, em, #usercp_nav .blockbody, a.textcontrol, .userprof_button, #inlinemod_formctrls a.popupctrl span.ctrlcontainer, .settings_form_border, .awardcatcolstyle_center, .awardcatcolstyle_left, .postbitlegacy .postfoot .textcontrols, .postbitlegacy .postfoot .textcontrols a.editpost, .postbitlegacy .postfoot .textcontrols a.quickreply, .postbitlegacy .postfoot .textcontrols a.newreply, .postbitlegacy .postfoot .textcontrols a.multiquote, .dbtech-thanks-button-control, .postlinking div, .postbitlegacy .postfoot .textcontrols a.reputation, .pollresultsblock, #pollinfo .blockrow, .threadbit .alt, #pollinfo {
