}

.postbit .posthead, .postbitlegacy .posthead, .eventbit .eventhead, .announcements .announcerow, .announcerow .announcement, .announcerow .announceinfo {
    background: #171616 none repeat-x;
}

#secondary .widget, .advertisement_above_footer, .wgo_block .blockbody, .navlinks, .textbox, textarea, select, .formcontrols .blockrow .dbtech_shouts .dbtech_vbshout_window .dbtech_vbshout_frame, .formcontrols .blockrow .dbtech_shouts .dbtech_vbshout_tabs.alt, .editor_controls .popupmenu div.popupctrl, .posts div, .posts .postbody, .posts .postdetails, .postbitlegacy .userinfo, .postbit .postfoot .textcontrols, .eventbit .eventfoot .eventcontrols, .postbit .postfoot .textcontrols a.quickreply, .postbitlegacy .postfoot .textcontrols a.forwardpost, .postbit .postfoot .textcontrols a.forwardpost, .postbit .postfoot .textcontrols a.newreply, .postbit .postfoot .textcontrols a.multiquote, .thread_info .blockbody, .blockfoot, .blocksubfoot, address, caption, cite, code, dfn, th, var, optgroup, .formcontrols .blockrow .dbtech_shouts .dbtech_vbshout_tabs.alt2, .popupbody li a, .popupbody li label, .popupbody, #sidebar_container.member_summary, li.activitybit, #activity_tab_container > div, .activitystream_block dd, dd.userprof_moduleinactive, .userprof_headers, .body_wrapper, .floatcontainer, .smaller, .news-bar, #main, dl.stats dt, #sidebar_container dl.stats, #sidebar_container .blockbody.userprof_content, #sidebar_container .userprof_content .time, .userprof_content .time, .profile_editor_border, .profile_content .actionbuttons, blockquote, .popupbody li.formsubmit, .userprof_content, li.userprof_content, #forum_icon_legend dl, .memberaction_body.popupbody, #sidebar_container .underblock, .formcontrols .section, .requestlist > li {
    background-color: #292929;
}

textarea, input[type=text], input[type=email], input[type=password], #sidebar_container.member_summary .textcontrol, .blockrow, .toplinks .nonotifications .popupbody a {
    background-color: #292929 !important;
}

.cke_skin_kama .cke_wrapper.cke_ltr, .cke_skin_kama .cke_wrapper.cke_rtl {
    background: #424242 url(https://www.edge-gamers.com/forums/clientscript/ckeditor/skins/kama/images/sprites.png) repeat-x 0 -1950px;
}

.threadlisthead span.threadstats {
    background: #232323 none repeat-x !important;
}

.notices {
    background-color: #5b5b5b;
}

.blockbody.settings_form_border, .formcontrols .blockrow, .forumbit_nopost .forumbit_nopost .forumrow, #sidebar_container>ul, .wgo_block .blockbody, .threadbit .sticky, .threadbit .nonsticky, .forum_info .blockbody, .forum_info .blockhead, .toolsmenu, .postbit, .postbitlegacy, .eventbit, .thread_info .blockbody, span.cke_skin_kama, .thread_info .options_block .options_correct, .thread_info .options_block2 .options_correct, .popupbody, input[type=text], input[type=email], input[type=password], textarea, select, #sidebar_container.member_summary, #sidebar_container .userprof_content .blockrow, .userprof_headers_border, .member_summary dd.avatar img, .member_summary .friends_mini .friends_list li .image_friend_link, .profile_editor_border, .profile_content .actionbuttons, .userprof_content_border, li.userprof_content, .awardcatcolhead, .awardbox, .forumhead, .wgo_block .blockhead, .editor_controls .popupmenu div.popupctrl {
    border: 1px solid #25333c;
}

.forumbit_post .forumrow {
    border: 1px solid #25333c;
    border-top: none;
}

.forumbit_post:first-child .forumrow {
    border: 1px solid #25333c;
}

.awardcatcolhead, .awardbox {
    border-top: none !important;
}

.seperator {
    border-right: 1px solid #282828 !important;
}

.wgo_block .blockhead {
    border-bottom: none;
}

.postbitlegacy .postbody, .eventbit .eventdetails .eventbody {
    border-left: 1px solid #25333c;
}

.postbit .posttitle, .postbitlegacy .title {
    border-bottom: 1px solid #25333c;
}

#navtabs .popupmenu ul li a {
    border-bottom: 1px solid #444;
}

.signature, .blocksubhead, .popupbody li, .userprof_editor {
    border-top: 1px solid #25333c;
}

.formcontrols .blockrow .dbtech_shouts .dbtech_vbshout_tabs.alt {
    border-bottom: 1px solid #292929;
    background-color: #343434;
}

.pagination span a.popupctrl, a.popupctrl, .pagination span.prev_next a, .pagination span.first_last a, .pagination span.selected a, .button, .input, .actionbuttons .group .button, .pagination span a, a.textcontrol, .as-tabs dd, .as-tabs dd.userprof_moduleinactive, dd.userprof_module, dd.userprof_moduleinactive, dd.userprof_content, dd.userprof_module a, dd.userprof_moduleinactive a, dd.userprof_content a, .userprof_button {
    border: 1px solid #5a7f97;
}

.formcontrols .blockrow .dbtech_shouts .dbtech_vbshout_tabs.alt2 {
    border: 1px solid #2f4351;
    border-bottom: none;
}

.memberaction.popupmenu a.popupctrl, .navtabs li, .toplinks .nonotifications a.popupctrl, .navtabs li a.popupctrl, .popupmenu a.popupctrl, .formcontrols .section .blockrow{
    border: none;
}

.formcontrols .section .blockrow {
    border-bottom: 1px solid #25333c;
}

.formcontrols .section .blockrow:last-child {
    border-bottom: none;
}

#sidebar_container .blockrow,.navlinks, .wgo_block .blocksubhead {
    border-top: 1px solid #25333c;
}

.widget_post_bit {
    border-top: 1px dotted #25333c;
}

#sidebar_container.member_summary .mainblock a.avatar .avatarcontainer img {
    border: 3px solid #292929;
    outline: none;
}

#view-mentions .member_content .subsection, #view-thanks .member_content .subsection {
    border-top: none;
}

#sidebar_container>ul, .wgo_block, .footer, .newcontent_textcontrol, .forum_info .blockhead, .forum_info .blockbody, .thread_info .blockbody, .thread_info h4, .notices li, #sidebar_container.member_summary, .memberaction_body.popupbody, .announcements .announcerow {
    box-shadow: -2px 2px 2px #464646;
}
    
h1, h2, h3, h4, h5, h6, p, strong, div, span, dd, label, .widget_post_content, .pollquestionmain, .time, .wgo_block .blocksubhead, li, blockquote, .userprof_content, dl.stats dt, .profile_content .blockbody .stats dd, .userprof_title, #sidebar_container .blocksubhead, .lastedited, .member_summary dl.stats dd, #sidebar_container .userprof_content .blockrow, .userprof_button, .formcontrols .blockrow .description, select, .above_postlist .pagination_top .postpagestats, .above_threadlist .threadpagenav .threadpagestats, .popupbody li label, .popupbody li label:hover, .popupbody li.formsubmit .submitoptions label, .popupbody li input.searchbox {
    color: #ccc;
}

.restore strong, li.restore a[href='https://www.edge-gamers.com/forums/showthread.php?318139-Interested-in-joining-the-tech-team'], .restore h2, .restore h1{
    color: #3e3e3e !important;
}

h1 {
  	font-weight: bold;
}

a, a:visited, .restore a {
    text-decoration: underline;
}

a, span, .restore a, .widget_featured_posts .article-content .entry-title a, .button {
    color: #aeaeae !important;
}

a:visited, .widget_featured_posts .article-content .entry-title a:visited, .restore a:visited {
    color: #7f7f7f !important;
}

[role='textbox'] {
    background-color: #292929 !important;
    color: #ccc !important;
}

#sidebar_container .block {
    margin-bottom: 0;
}

#footer_morecopyright:before {
    content: "Night Theme made by Scrya";
}
}
