css:
@-moz-document domain("2ch.hk"), domain("2ch.pm") {
.moneymoneymoney,.moneymoneymoney+hr,.postrekl-hr,.top-user-boards,.board-banner,.logo>br,.rmenu,.menu-links-mob li,.rules-area,.kupi-passcode-suka,.message-sticker-btn,.bb_type_boardstats,.option-area .box:nth-of-type(2),.mail,td.label,.toolbar-area>.desktop,.adminbar__menu+hr,.boardstats,.reply-label-wrapper>.desktop,a[title=" Стикер"],a[title=" Стикер"]+span,.fa-star,.fa-star-o,.replypage .postbtn-exp,.replypage .oppost .postbtn-hide,.postbtn-report,.postpanel+.desktop,.thread-nav-mob+hr,.replypage .thread-nav:nth-last-of-type(2),.replypage .thread-nav>a[href*="catalog"],.news,.norm-reply+.postform-hr,.postform+.postform-hr,.qr-vk-public,#qr-e-mail,.postscontainer:first-of-type,.hnews,.hdescription-stats,.ui-corner-all,.mainlogo,.mainpage footer,.mainpage .adminbar,.hboardlist>.column:nth-child(2)>ul>br:nth-last-of-type(2)~*,.welcomefriend,.catalog>.centered+hr,.adminbar-bottom hr,.board-info a,.nb,#plashque,a[href^="/market."],a[href^="/m."],a[href^="/contacts."],a[href^="/donate."],a[href^="/makaba/"],a[href^="/abu/res/"],a[href^="/boardrequest."],a[href^="/ads."],a[href^="/price."],.adminbar__menu>a[href*="/catalog."],#mobile-plashque,.thread-nav a[href="/tracker.html"],#fmenu>li:last-of-type,#fmenu>li:nth-of-type(2),.rules,.footer__info,.moneymoneymoney~.nb+hr,.logo img,.adminbar-bottom,.adminbar>hr,.adminbar__slink[href^="/userboards."],.adminbar__slink[href^="/tracker."],.replypage .adminbar__slink[href*="/catalog."],.post_type_reply:before,.qr_reply .qr__footer,.post_type_oppost .post__number,.hidefm,.cntnt__left,.hr_prerekl,.prerekl-hr,#adminbar__more,.pager a[href*="/catalog."],.pager strong+.button-mob,.pager strong+.button-mob+.button,.postform__sticker-btn{display:none!important}
.board-list-mob,.send-mob,.reply-label.mobile,.alert:last-of-type{display:block}
.catalog .adminbar .desktop:nth-last-of-type(2)+li,#LakeNavForm,.adminbar__exp{display:inline-block!important}
.board-list-mob{display:block;float:right;font-size:13px}
.button-mob#submit{width:100%!important;}
#captcha-value{width:221px!important}
.captcha-row,.footer{text-align:center}
.mainpage,header,.adminbar,.posts,.content__right{background:inherit}
header>.adminbar{position:fixed;top:0;left:0;padding:8px;z-index:99999;width:100%;border-bottom:1px solid rgba(0,0,0,.1);text-align:left;border-color:var(--theme_default_border)}
body{padding-top:38px}
.replypage{padding-bottom:40px}
#ABU-alertbox,.alert{top:37px}
.bb.favorites,.bb_type_favorites{right:0;border-bottom:none;border-right:none}
.replypage .bb.favorites{bottom:35px}
.replypage .bb{bottom:35px}
.message-byte-len{opacity:.1;pointer-events:none}
.reply,.modal{background-image:none}
.reply,.post_type_reply,#qr-submit{width:100%}
.post_type_reply{margin:0}
.board-info,.thread-nav,#ABU-getnewposts,.centered>span[style="float:left"]{font-size:0!important}
.replypage .thread-nav *,.thread-nav form,#ABU-getnewposts *,.board-speed{font-size:12px}
.catalog{padding-bottom:38px}
.catalog>.centered{position:fixed;bottom:0;left:0;width:100%;z-index:99999;background:inherit;padding:8px;border-top:1px solid rgba(0,0,0,.1);border-color:var(--theme_default_border)}
.catalog #csearch,.adminbar__boards,.adminbar__menu{padding:0}
.catalog>.centered *{font-size:13px}
.catalog .img-block img{max-width:230px;height:auto}
.thread-nav>*{margin-right:7px}
.posts,#posts-form{max-width:980px;margin:auto}
div[id^="preview"]{max-width:980px;width:auto;border-color:var(--theme_default_border)!important}
.ABU-refmap>em{font-style:normal}
a,.a-link-emulator{text-decoration:none!important}
.search>input[name="find"]{position:fixed;top:8px;z-index:100000;right:120px/*Для Нейтрона, под другие стили подгоняй сам*/;font-size:13px;padding:0 2px}
.images-multi,.images-multi .image,.thread-nav>*:last-child,.hdescription{margin:0!important}
.img.preview{max-width:200px;height:auto}
.replypage .thread-nav:first-of-type{position:fixed;bottom:0;background:inherit;width:100%;left:0;padding:8px;border-top:1px solid rgba(0,0,0,.1);z-index:99999;border-color:var(--theme_default_border)}
div[style="float: right; margin: 0 0 0.5em 0;"]{float:left!important}
.nav-arrows,.na{left:10px}
.down-nav-arrow,.na_type_down{bottom:40px}
.post{padding:1px 6px 3px}
.qr{z-index:100001}
.reply .reflink:before{float:right;margin:0!important}
.pager,.boardstats,.favorites,.dropd-board-btn,.dropd-board-list{border-color:rgba(0,0,0,.1)!important;border-color:var(--theme_default_border)!important}
.thread-nav>span[style="float:right"],.autorefresh,.form-files-limits,.menu-links-mob,.board-list-mob,.posttime,.filesize,.bb-footer,.reflink,.qr-settings,.ophui,.board-tags,#report-form,.mess-post,.footer,.ABU-refmap,.centered,.box-header,.hboardlist{cursor:default}
.thread-nav>span[style="float:right"]>*,input[type="checkbox"],.option-area .box,#LakeNavForm,#filter_select{cursor:pointer}
.adminbar,.reply-label,.logo,.captcha-box,.images-area,.option-area,.toolbar-area,.symbol-counter,.posttime,.images,.postpanel,.reflink:before,.ABU-refmap,.media-expand-button,.thread-nav,.bb,.qr-settings,.ophui,#report-form,.mess-post,.expand-large-comment,.nav-arrows,.footer,img,.centered,.box-header,.hboardlist{user-select:none}
.cntnt__right,.cntnt{background:inherit}
.cntnt__left_sticky{top:36px;height:calc(100vh - 71px)}
.cntnt__left{border-color:rgba(0,0,0,.1);border-color:var(--theme_default_border)}
.reply-posts-marker .post,.watched-posts-marker .post{border-right:2px #f00;padding:1px 5px 3px}
.post_type_watched,.post_type_replied{border-right-color:#F60;border-right:2px var(--theme_default_link)}
.reply-posts-marker .post,.post_type_replied{border-right-style:dashed}
.watched-posts-marker .post,.post_type_watched{border-right-style:solid}
.postform__raw.captcha{text-align:center}
.pace>*{z-index:100000!important}
.adminbar{margin-bottom:0;text-align:left}
.adminbar__boards{position:static;transform:none;background:transparent;display:inline}
.adminbar__menu{display:inline}
.adminbar__menu,.adminbar__boards{border:0}
.adminbar__menu>a,.adminbar__menu>.adminbar__exp>a{margin-right:5px}
#LakeNavForm,.post__number{float:right}
.style-switcher{font-size:1em}
.hidden-post-box{padding:1px 5px 1px 24px}
.hidden-post-box .like-div,.hidden-post-box .dislike-div,.alert{display:none}
.reply.hidden-thread-box,.hidden-post-box{border-width:1px!important}
.post__message{margin:2px 0 4px 17px}
.post__images_type_single+.post__message{margin-top:0}
.post__number,.post__reflink{color:var(--theme_default_alttext)}
select{border-color:var(--theme_default_btnborder);color:var(--theme_default_inputtext);background:var(--theme_default_altbtnbg)}
hr{border-color:var(--theme_default_border)}
.qr{background:var(--theme_default_bg)}
.newpost form>.postform__raw_flex:first-of-type{display:none}
#posts-form:before,.tti_info:before{content:'';border-top:1px solid var(--theme_default_border);width:100%;display:block;margin:8px 0}
.cntnt__right{padding-top:0}
.cntnt__header{padding:0}
.adminbar__menu>a[href="/"]{font-size:0}
.adminbar__menu>a[href="/"]:before{display:inline-block;height:14px;content:url(https://2ch.hk/makaba/templates/img/favicon.ico);vertical-align:text-bottom}
}
