@-moz-document domain(lolzteam.net) {

  body
{
 background: rgb(48, 48, 48);
}

#lzt-logo {
    background: url(https://i.imgur.com/QIHfPtb.png) no-repeat;
}

#header {
    border-top: 4px solid #e22f1d;
}

.navPopup .listPlaceholder::-webkit-scrollbar-thumb, .brmsTabContent::-webkit-scrollbar-thumb {
    background: #c73829;
