}

.button.primary {
    background-color: #c73829;
}

.button.primary:hover {
    background: #f23926;
}

.BRMSContainer.BRMSContainerDark ul.brmsTabNav>li.current>a {
    box-shadow: inset 0px -3px 0px 0px #fc3e2b;
}

.marketIndexItem--Title {
        /*color: rgb(228, 15, 15);*/
    color: #fc3e2b;
}

.marketIndexItem--price {
        background: #c73829;
}

Badge.PlusXGamesLabel {
    background: #fc3e2b;
}

.info-separator {
    background: #fc3e2b;
}

.primaryControls:before {
    color: #fc3e2b;
}

.mainc {
    color: #fc3e2b;
}

.numberStyle {
    color: #fc3e2b;
}

.marketIndexItem-Badge.PlusXGamesLabel {
    background: #c73829;
}

#searchBar .primaryControls:before {
    color: #fc3e2b;
}

.navTabs .navTab.PopupOpen .navLink {
    color: #fc3e2b;
}

.navTabs .navLink .itemCount:hover {
    background-color: #fc3e2b;
}

.navTabs .navLink .itemCount {
    background-color: #fc3e2b;
}

.listItemText a {
    color: #fc3e2b;
}

#allnotifications {
    color: #fc3e2b;
}

.node .nodeText .nodeTitle, .node .nodeText .nodeTitle a {
    color: #fc3e2b;
}

.forumSearchThreads--Link--Icon {
    background: #c72f21;
    color: rgb(39,39,39);
}


.forumSearchThreads--Link:hover .forumSearchThreads--Link--Icon {
    background: #a93024;
    color: rgb(39,39,39);
}

.forumSearchThreadsMenu .discussionListItem--replyCount {
    margin-left: 6px;
    color: #a93024;
}

.forumSearchThreads--pinButton.pinned .forumSearchThreads--Link--Icon {
    background: #a93024;
    color: #000;
}

.scrollBar::-webkit-scrollbar-thumb, .chosen-results::-webkit-scrollbar-thumb {
    background: #d43a2a;
    border-radius: 2px;
}

.scrollBar::-webkit-scrollbar-thumb:hover
{
 	background: #fc3e2b;   
}

.PageNav a.currentPage {
    background-color: #bb3729;
}

.breadcrumb .crust:last-child a.crumb {
    color: rgb(214, 58, 42);
}

#pageDescription>a, #pageDescription>a>abbr {
    color: rgb(214, 58, 42);
}

.messageUserBlock .extraUserInfo dl>.label {
    color: rgb(188, 53, 45);
}

.hashPermalink {
    color: rgb(188, 53, 44);
    padding: 0 0 0 5px;
}

.message .item>.icon:hover {
color: rgb(188, 53, 44);
}

.navTabs .navTab.account .navLink .accountUsername {
    color: #d6d6d6;
}

.fa fa-bell:hover
{
	color: #fc3e2b;
}

webkit-scrollbar-thumb:hover
{
 	 color: #fc3e2b;  
}

.scrollBar::-webkit-scrollbar-thumb:active,.chosen-results::-webkit-scrollbar-thumb:active
{
	background: #fc3e2b;
}

.conversationItem.active {
    background: rgb(51, 51, 51);
    color: #f5f5f5;
}

.conversationItem.active:hover {
    background: rgb(51, 51, 51);
    color: #f5f5f5;
}

.xenOverlay .formOverlay .heading {
    color: rgb(218, 64, 49);
}

input#redactor_spoiler_title.textCtrl:focus {
    font-size: 13px;
    color: rgb(214, 214, 214);
    background-color: rgb(48, 48, 48);
    padding: 0 10px;
    border: 1px solid #fc3e2b;
    border-radius: 1px;
    line-height: 30px;
    outline: none;
    box-sizing: border-box;
    height: 30px;
}

.universalSearchForm:before {
    color: rgb(252, 62, 44);
}

.conversationItem .avatar.isOnline::before {
    background-color: rgb(98, 216, 95);
}

.fa-envelope:hover {
    color: rgb(252, 62, 44);
}

.breadBoxTop {
    padding: 5em 0 2em;
    margin: 0em auto 0em;
}

.tabs li.active {
    box-shadow: inset 0px -2px 0px 0px rgb(200, 56, 42);
}

.page_counter .count {
    font-size: 19px;
    color: rgb(218, 63, 48);
    padding-bottom: 3px;
    line-height: 21px;
}

.post-actions>.dottes-style {
    color: rgb(230, 70, 55);
}

.messageSimple .item.like>.icon {
    background-image: url(https://i.imgur.com/sqsA0p2.png);
}

.messageSimple .item.CommentPoster>.icon {
    background-image: url(https://i.imgur.com/rY7Tz9M.png);
}

.message .item.like>.icon {
    background-image: url(https://i.imgur.com/sqsA0p2.png);
}

.message .item.ReplyQuote>.icon {
    background-image: url(https://i.imgur.com/rY7Tz9M.png);
}

.message .item.XITag>.icon {
    background-image: url(https://i.imgur.com/eIBJmXs.png);
}

.message .item.quickConversationButton>.icon {
    background-image: url(https://i.imgur.com/DVcTMbq.png);
}

.searchResult .title>a {
    color: rgb(210, 55, 52);
}

.emCtrl, .messageContent a {
    color: rgb(228, 88, 86);
}

.footerLegal a:hover, .footerLegal a:active, .footer a:hover, .footer a:active {
    color: rgb(239, 68, 66);
}

.footerLegal a:hover, .footerLegal a:active, .footer a:hover, .footer a:active {
    color: rgb(239, 68, 66);
    text-decoration: none;
}

.navLink.NoPopupGadget:hover
{
 	   color: red;
}

.privateControls .item.control {
    color: rgb(188, 53, 45);
}

body[dir="LTR"]
{
 	background: rgb(39, 39, 39);  
}

input[type="checkbox"]:checked:after {
    background-color: rgb(200, 56, 42);
    border-color: rgb(200, 56, 42);
}

.counter {
    color: rgb(252, 62, 44);
}

input[type="radio"]:before {
    background: rgb(200, 56, 42);
}

.xenOverlay .section .heading, .xenOverlay .sectionMain .heading, .xenOverlay .errorOverlay .heading {
        background: rgb(200, 56, 42);
}

.navigationSideBar a.selected {
    background: rgb(48, 48, 48);
    -webkit-box-shadow: inset 4px 0px 0px 0px rgb(200, 56, 42);
    -moz-box-shadow: inset 4px 0px 0px 0px rgba(35,168,108,1);
    box-shadow: inset 4px 0px 0px 0px rgb(200, 56, 42);
}

img.marketLogo {
    content: url('https://i.imgur.com/igVhFMV.png')
}
img.marketLogo:after {
    content: url('https://i.imgur.com/igVhFMV.png')
}

.negativeFeedbackCount {
    background: #505050;
}

.positiveFeedbackCount {
    background: #c8382a;
}

.marketItemView--Container h4 {
    color: rgb(200, 56, 42);
}

.textHeading, .xenForm .sectionHeader {
    color: rgb(200, 56, 42);
}

h4.tofrule.linksection {
    color: #dc3d2e;
}

.conversationItem.active .unread-message {
    background: rgb(214, 57, 43);
}


.xenOverlay .formOverlay .textCtrl:focus, .xenOverlay .formOverlay .textCtrl.Focus {
    border-color: #a83025;
}

element.style {
    color: rgb(226, 48, 30);
}

.importantMessage a {
    font-weight: bold;
    color: rgb(226, 48, 30);
}

.DismissParent .DismissCtrl {
    color: #F44336;
}

.message .item.unlike>.icon {
    background-image: url(https://i.imgur.com/EaxZLXU.png);
    filter: none;
}

.contestThreadBlock .memberCounter>.backgroundCounter {
    background: rgb(200, 56, 42);
}

.username .style88 {
    background: linear-gradient(90deg, #e61616 0%, #d64178 100%, #1681b5);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    color: #FF512F;
    font-weight: bold;
    font-size: 16px;
}

.messageSimple .item.unlike>.icon {
    background-image: url(https://i.imgur.com/EaxZLXU.png);
    filter: none;
}

.navTabs .navTab.PopupClosed .navLink:hover { 
color: #fc3e2b; 
}

