css:
@-moz-document domain("youtube.com") {
.html5-scrubber-button, .ytp-scrubber-button {
   background: radial-gradient(ellipse at center, #aaaaaa 25%,#fafafa 55%) !important;
   width: 16px !important;
   height: 16px !important;
   left: 0px !important;
   border: none !important;
   margin-top: -1px !important;
}

.ytp-big-mode .ytp-scrubber-button {
   background: radial-gradient(ellipse at center, #aaaaaa 25%,#fafafa 55%) !important;
   width: 20px !important;
   height: 20px !important;
   left: 0px !important;
   border: none !important;
   margin-top: -1px !important;
}

.ytp-bound-time-left, .ytp-bound-time-right {
    position: absolute;
    display: none;
    top: -29px;
    z-index: 29;
    background-color: #000001;
    text-align: center;
    color: #000001;
    padding: 5px 9px;
    opacity: 0;
    -moz-transition: opacity .1s ease-in;
    -webkit-transition: opacity .1s ease-in;
    transition: opacity .1s ease-in;
}

.watch-view-count {
    line-height: 24px;
    max-height: 24px;
    text-align: right;
    font-size: 19px;
    color: #111;
    white-space: nowrap;
    margin-bottom: 2px;
}

.html5-video-player {
    position: relative;
    width: 100%;
    height: 100%;
    overflow: hidden;
    outline: 0;
    font-family: "YouTube Noto",Roboto,Arial,Helvetica,sans-serif;
    color: #fff;
    text-align: left;
    direction: ltr;
    font-size: 11px;
    line-height: 1.3;
    -webkit-font-smoothing: antialiased;
    -webkit-tap-highlight-color: rgba(0,0,0,0);
    touch-action: manipulation;
    -ms-high-contrast-adjust: none;
}

.ytp-time-duration {
    color: #888;
}

.ytp-time-separator {
	color: #999;
}

.ytp-progress-bar-container {
    display: block;
    position: absolute;
    width: 100%;
    bottom: 30.5px;
    height: 5px;
}

.ytp-big-mode .ytp-progress-bar-container {
    bottom: 52.5px;
    height: 8px;
}

.ytp-scrubber-container {
    position: absolute;
    top: -4px;
    left: -6.5px;
    z-index: 43;
}

.ytp-progress-bar {
    display: block;
    position: absolute;
    width: 100%;
    height: 7px;
}

.ytp-chrome-controls {
    height: 33px;
    line-height: 33px;
    font-size: 115%;
    font-weight: 300;
    text-align: left;
    direction: ltr;
    background-image: -moz-linear-gradient(top,#292929 0,#252525 100%);
    background-image: -ms-linear-gradient(top,#292929 0,#252525 100%);
    background-image: -o-linear-gradient(top,#292929 0,#252525 100%);
    background-image: -webkit-gradient(linear,left top,left bottom,color-stop(0,#292929),color-stop(100%,#252525));
    background-image: -webkit-linear-gradient(top,#292929 0,#252525 100%);
    background-image: linear-gradient(to bottom,#292929 0,#252525 100%);
}

.ytp-time-display {
    font-size: 109%;
    display: inline-block;
    vertical-align: top;
    padding: 0 5px;
    white-space: nowrap;
    line-height: 35px;
   background-image: linear-gradient(to bottom,#292929 0,#252525 100%);
}

.ytp-time-display {
    background-image: linear-gradient(to bottom,#292929 0,#252525 100%);
}

.ytp-swatch-background-color {
    background-image: -moz-linear-gradient(top,#ef0000 0,#660000 100%);
    background-image: -ms-linear-gradient(top,#ef0000 0,#660000 100%);
    background-image: -o-linear-gradient(top,#ef0000 0,#660000 100%);
    background-image: -webkit-gradient(linear,left top,left bottom,color-stop(0,#c40000),color-stop(100%,#660000));
    background-image: -webkit-linear-gradient(top,#ef0000 0,#660000 100%);
    background-image: linear-gradient(to bottom,#ef0000 0,#660000 100%);
}

.ytp-load-progress {
    z-index: 33;
    background: rgba(162, 162, 162, 0.7);
}

.ytp-chrome-bottom {
   width: 100% !important;
   height: 33px;
   line-height: 33px;
   left: 0px;
   background-image: linear-gradient(to bottom,#292929 0,#252525 100%);
}

.ytp-chrome-top {
   width: 100% !important;
   opacity: 30%;
   line-height: 33px;
   left: 0px;
   background-image: linear-gradient(to bottom,#292929 0,#252525 100%);
}

.html5-video-player a {
    color: #999;
    text-decoration: none;
    -moz-transition: color .1s cubic-bezier(0.0,0.0,0.2,1);
    -webkit-transition: color .1s cubic-bezier(0.0,0.0,0.2,1);
    transition: color .1s cubic-bezier(0.0,0.0,0.2,1);
    outline: 0;
}

.ytp-gradient-top, .ytp-gradient-bottom{
   background: transparent !important;
}

.ytp-chrome-top, .ytp-chrome-bottom{
   text-shadow: none;    
}

.ytp-big-mode .ytp-title-text {
    padding-top: 10px;
    font-size: 23px;
}

.ytp-big-mode .ytp-progress-bar-padding {
    height: 10px;
}

.ytp-big-mode .ytp-watch-later-icon {
    width: 44px;
    height: 44px;
    margin-top: -8px;
}

.ytp-big-mode .ytp-share-icon {
    width: 44px;
    height: 44px;
    margin-top: -8px;
}

.ytp-big-mode .ytp-chrome-top .ytp-watch-later-button, .ytp-chrome-top .ytp-watch-later-button {
    width: 44px;
    height: 44px;
}

.ytp-big-mode .ytp-chrome-top .ytp-share-button, .ytp-chrome-top .ytp-share-button {
    width: 44px;
    height: 44px;
}

.ytp-big-mode .ytp-cards-button-icon {
    width: 44px;
    height: 44px;
    margin-top: -8px;
}

.ytp-big-mode .ytp-chrome-top .ytp-cards-button, .ytp-chrome-top .ytp-cards-button {
    width: 44px;
    height: 44px;
}

/*buttons and stuff*/

