    background-image: -webkit-linear-gradient(top,#292929 0,#252525 100%);
    background-image: linear-gradient(to bottom,#292929 0,#252525 100%);
}

.ytp-playlist-menu-button.ytp-button {
    width: 44px;
    height: 44px;
    margin-top: -8px;
}

.ytp-chrome-controls .ytp-button {
    height: 100%;
    background-image: -moz-linear-gradient(top,#292929 0,#252525 100%);
    background-image: -ms-linear-gradient(top,#292929 0,#252525 100%);
    background-image: -o-linear-gradient(top,#292929 0,#252525 100%);
    background-image: -webkit-gradient(linear,left top,left bottom,color-stop(0,#292929),color-stop(100%,#252525));
    background-image: -webkit-linear-gradient(top,#292929 0,#252525 100%);
    background-image: linear-gradient(to bottom,#292929 0,#252525 100%);
}



.ytp-volume-slider-track {
    background-image: -moz-linear-gradient(top,#ef0000 0,#660000 100%);
    background-image: -ms-linear-gradient(top,#ef0000 0,#660000 100%);
    background-image: -o-linear-gradient(top,#ef0000 0,#660000 100%);
    background-image: -webkit-gradient(linear,left top,left bottom,color-stop(0,#c40000),color-stop(100%,#660000));
    background-image: -webkit-linear-gradient(top,#ef0000 0,#660000 100%);
    background-image: linear-gradient(to bottom,#ef0000 0,#660000 100%);
    border-radius: 2px;
}

.ytp-volume-slider-handle{
	height: 15px;
	width: 7px;
    background-image: linear-gradient(to bottom,#fff 0,#eee 100%);
}

.ytp-big-mode .ytp-volume-slider-handle{
	height: 15px;
	width: 7px;
    background-image: linear-gradient(to bottom,#fff 0,#eee 100%);
    
}

.ytp-volume-slider-handle:before{
    background-image: -moz-linear-gradient(top,#ef0000 0,#660000 100%);
    background-image: -ms-linear-gradient(top,#ef0000 0,#660000 100%);
    background-image: -o-linear-gradient(top,#ef0000 0,#660000 100%);
    background-image: -webkit-gradient(linear,left top,left bottom,color-stop(0,#c40000),color-stop(100%,#660000));
    background-image: -webkit-linear-gradient(top,#ef0000 0,#660000 100%);
    background-image: linear-gradient(to bottom,#ef0000 0,#660000 100%);
    border-radius: 2px;
}
   
.ytp-volume-slider-handle:after{
    background-image: -moz-linear-gradient(top,#292929 0,#252525 100%);
    background-image: -ms-linear-gradient(top,#292929 0,#252525 100%);
    background-image: -o-linear-gradient(top,#292929 0,#252525 100%);
    background-image: -webkit-gradient(linear,left top,left bottom,color-stop(0,#292929),color-stop(100%,#252525));
    background-image: -webkit-linear-gradient(top,#292929 0,#252525 100%);
    background-image: linear-gradient(to bottom,#292929 0,#252525 100%);
    border-radius: 2px;
}
   
.ytp-volume-slider{
    background-image: -moz-linear-gradient(top,#292929 0,#252525 100%);
    background-image: -ms-linear-gradient(top,#292929 0,#252525 100%);
    background-image: -o-linear-gradient(top,#292929 0,#252525 100%);
    background-image: -webkit-gradient(linear,left top,left bottom,color-stop(0,#292929),color-stop(100%,#252525));
    background-image: -webkit-linear-gradient(top,#292929 0,#252525 100%);
    background-image: linear-gradient(to bottom,#292929 0,#252525 100%);
    border-radius: 2px;
}

div.ytp-panel.ytp-quality-menu {
    text-align: center;
}

.ytp-quality-menu .ytp-menuitem-label {
    text-align: center;
}

.ytp-swatch-color {
    color: #fff;
}

.video-time, .video-time-overlay {
    position: absolute;
    right: -2px;
    bottom: 0px;
}

.html5-video-player:not(.ytp-touch-mode) ::-webkit-scrollbar-track {
    background-image: -moz-linear-gradient(top,#292929 0,#252525 100%);
    background-image: -ms-linear-gradient(top,#292929 0,#252525 100%);
    background-image: -o-linear-gradient(top,#292929 0,#252525 100%);
    background-image: -webkit-gradient(linear,left top,left bottom,color-stop(0,#292929),color-stop(100%,#252525));
    background-image: -webkit-linear-gradient(top,#292929 0,#252525 100%);
    background-image: linear-gradient(to bottom,#292929 0,#252525 100%)
}


.ytp-quality-menu .ytp-menuitem:hover {
    white-space: nowrap;
    background-image: -moz-linear-gradient(top,#ef0000 0,#660000 100%);
    background-image: -ms-linear-gradient(top,#ef0000 0,#660000 100%);
    background-image: -o-linear-gradient(top,#ef0000 0,#660000 100%);
    background-image: -webkit-gradient(linear,left top,left bottom,color-stop(0,#c40000),color-stop(100%,#660000));
    background-image: -webkit-linear-gradient(top,#ef0000 0,#660000 100%);
    background-image: linear-gradient(to bottom,#ef0000 0,#660000 100%);
}

.ytp-settings-menu {
	position: absolute;
    overflow: hidden;
    border-radius: 3px;
    background-image: -moz-linear-gradient(top,#292929 0,#252525 100%);
    background-image: -ms-linear-gradient(top,#292929 0,#252525 100%);
    background-image: -o-linear-gradient(top,#292929 0,#252525 100%);
    background-image: -webkit-gradient(linear,left top,left bottom,color-stop(0,#292929),color-stop(100%,#252525));
    background-image: -webkit-linear-gradient(top,#292929 0,#252525 100%);
    background-image: linear-gradient(to bottom,#292929 0,#252525 100%);
    opacity: 99%;
    -webkit-transition: opacity .1s;
}

div#ytp-id-18.ytp-popup.ytp-settings-menu {
    text-align: center;
}


div.ytp-time-display.notranslate {
	background-image: linear-gradient(to bottom,#292929 0,#252525 100%);   
}

span.ytp-time-current {
	position: relative;
    bottom: 2px;
    background-image: linear-gradient(to bottom,#292929 0,#252525 100%);
}

span.ytp-time-separator {
	position: relative;
    bottom: 2px;
    background-image: linear-gradient(to bottom,#292929 0,#252525 100%);
}

span.ytp-time-duration {
	position: relative;
    bottom: 2px;
    background-image: linear-gradient(to bottom,#292929 0,#252525 100%);
}

.ytp-ad-progress {
    z-index: 36;
    background: #fc0;
    background-image: linear-gradient(to bottom,#fc0 0,#bd9700 100%);
}

.ytp-progress-bar-padding {
    position: absolute;
    width: 100%;
    height: 5px;
    bottom: 0;
    z-index: 28;
    background-image: linear-gradient(to bottom,#292929 0,#252525 100%);
}

.ytp-progress-bar-container:hover:not([aria-disabled=true]) .ytp-progress-bar-padding {
    height: 7px;
    bottom: 0px;
    background-image: linear-gradient(to bottom,#292929 0,#252525 100%);
}

.ytp-big-mode:not(.ytp-touch-mode) .ytp-progress-bar-container:hover:not([aria-disabled=true]) .ytp-progress-bar-padding {
    height: 7px;
    bottom: 0px;
}
.ytp-big-mode:not(.ytp-touch-mode) .ytp-progress-bar-padding {
    height: 6px;
    bottom: 0px;
}

.ytp-big-mode .ytp-chrome-controls {
    height: 28px;
    line-height: 28px;
}

.ytp-big-mode .ytp-time-display {
    line-height: 28px;
    font-size: 15px;
}

.ytp-big-mode .ytp-chrome-bottom {
    height: 28px;
    padding-top: 0px;
}

.ytp-big-mode .ytp-progress-bar {
    position: absolute;
    bottom: -25px;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: 31;
    outline: none;
}
}
