css:
@-moz-document regexp("https?:\\/\\/gendai\\.ismedia\\.jp\\/articles\\/-\\/\\d+.*") {
/*======================================
  gendai ismedia ja jp compact
  ======================================*/
/*--- unwanteds ---*/
[id^="GB_"],
[class$="_ad"],
[class*="_popIn_recommend_article_ad"],
dummy#dummy.dummy {display: none !important;}

/*--- generic containers ---*/
h1 {margin: 0;}
body {width: auto; font-family: initial; font-weight: initial; letter-spacing: initial;}

/*--- header ---*/

/*--- footer ---*/
.elementNarrowBanner {width: auto; min-width: auto; height: 1.4em;}

.copyContainer {width: auto; padding: 0;}
.copyContainer_left {width: auto;}
.copyContainer_left .family-img {width: auto; height: auto;}
.copyContainer_left .family-list {margin: 0;}
.copyContainer_left .family-list li {margin-bottom: 0; font-size: initial; letter-spacing: initial;}
.copyContainer_center {margin-top: 0; width: 3.5em; height: 4em;}
.copyContainer_right {width: auto; font-weight: normal; font-size: initial;}
.copyContainer_right br {content: "";}


/*--- main contents ---*/
[class^="blockContainer_"] {box-sizing: border-box; -moz-box-sizing: border-box;}
.blockContainer {width: auto; margin: 0; padding: 0;}

.blockContainer_left2 {width: calc(100% - 300px - 1em); margin: 0 0.5em;}
.blockContainer_right1 {width: 300px; padding-bottom: 0 !important; min-height: auto; border-left: 1px dotted gray;}

.articleHeader {margin-bottom: 0; padding-bottom: 0;}
.elementSectionHeadingsWithSuffix {height: auto; margin-bottom: 0;}
.article_title {letter-spacing: initial; line-height: initial;}
.article_author-small {width: auto; height: auto; margin: 0;}
.article_author-small .article_author-small_name {height: auto; margin: 0; line-height: initial; letter-spacing: initial;}
.article_author-small .article_author-small_title {height: auto; margin: 0; line-height: initial; letter-spacing: initial;}
.article_author-small .article_author-small_profileLink {width: auto; height: auto; margin: 0;}

.articleHeaderSns {margin-top: 0;}
.articleContents {margin: 0; letter-spacing: initial;}
.frame {margin: 0.5em 0; padding: 0; line-height: 1.4em;}
.articleContents p {font-size: initial; line-height: 1.4em; margin: 0 0 0.5em 0; text-indent: 1em;} /* 記事内テキスト */
.articleContents .figure-center {width: auto; margin: 0;} /* 記事内写真 */

.nextpageTitle {width: auto; min-height: auto; padding: 0;}
.pagination {width: auto; height: auto; margin: 0.5em 0 0 0;}
.articleBottomSns {width: auto; margin: 0.5em 0 0 0;}

.blockContainer::before {content: ""; display: block; height: 0.5em}
.blockContainer {border-top: 1px dotted gray;}
.blockContainer.dontprint>div {clear: both; margin: 0 0.5em;}

.elementArticleItem {width: 25%; min-height: auto; margin: 0; padding: 0;}
.elementArticleItem a img {margin-bottom: 0 !important;}

._popIn_recommend_container {width: auto; padding: 0;}
._popIn_recommend_articles, ._popIn_infinite_page {display: inline;}
._popIn_recommend_article {
 display: block; box-sizing: border-box; width: 25%; height: 12em;
 margin: 0 !important; padding: 0 0.5em;
 clear: none; float: left;
 }
._popIn_recommend_article>div {margin: 0;}


}
