css:
@-moz-document url-prefix("https://www.biask.com/") {
.wenda dl {
    padding: 6px 0;
}

.wenda dl dt {
    height: 30px;
    line-height: 30px;
    margin-bottom: 4px;
}

dl .foot_bq {
    margin-top: 0px;
    display: none;
}

.wenda dl dd p.bold {
    line-height: 24px;
    font-size: 16px;
    color: #4d4d4d;
    margin-bottom: 0px;
    font-family: "HiraginoSansGB-W3";
}

.wenda dl dd p.bold a {
    font-size: 16px;
    color: #778087;
    line-height: 28px;
    font-family: "Helvetica Neue", "Luxi Sans", "DejaVu Sans", Tahoma, "Hiragino Sans GB", "Microsoft Yahei", sans-serif;
}

.wenda dl dt a {
    font-size: 12px;
    color: #b3b3b3;
    font-family: "HiraginoSansGB-W6";
    font-weight: bold;
    margin-right: 15px;
    display: inline-block;
    height: 30px;
    line-height: 30px;
}
.wenda dl dt span {
    color: #d3d3d3;
    font-size: 12px;
    margin-right: 15px;
    font-family: "HiraginoSansGB-W3";
}

.suspension {
    position: relative;
}

.details_box ul li {
    border-bottom: 1px solid #EFEFEF;
    padding: 8px 0;
}
}
