css:
@-moz-document domain("letterboxd.com") {

/* ----------------------------------- HOME ----------------------------------- */
#content {
    background: #e8f4f9 top repeat-x;
}

/* WELCOME */
.title-hero {
    color: #000;
}
.title-hero a, .title-maxi a {
    color: #000;
}

/* SUBTITLE*/
.sub-heading {
    color: #000000a3;
}
.body-text a {
    color: #000;
}

/* HOME TEXT */
body {
    color: #000000;
}

/* MOVIE TITLE HOME */
.review-tile>.header>.details .headline-2>a {
    color: #000;
}

/* LIST TITLE */
.title-2 a, .title-3 a, .title-4 a {
    color: #000;
}

/* SHOWDOWNS TITLE */
.content-teaser .title-1 a, .content-teaser .title-2 a {
    color: #000;
}

/* SECTION TITLE */
.section-heading a {
    color: #000;
}

/* LATEST NEWS */
.no-mobile .content-teaser.-split {
    background: #e8f4f9;
}

/* ----------------------------------- PROFILE ----------------------------------- */

/* USERNAME */
.title-0, .title-1, .title-2, .title-3, .title-4 {
    color: #000;
}

/* FAVORITE MOVIE SECTION TITLE */
.section-heading {
    color: #000;
}

/* MOVIE TITLE */
.film-detail-content .headline-2>a, .film-detail-content .headline-3>a {
    color: #000;
    margin-right: 5px;
}

/* TIMELINE DIARY */
.logged-summary li dl dd a {
    color: #000;
}
/* TIMELINE ACTIVITY */
.timeline li .target {
    color: #000;
}

/* ----------------------------------- FILMS ----------------------------------- */

/* MENU */
.sub-nav li.selected a {
    color: #008623;
}

/* ----------------------------------- DIARY ----------------------------------- */

/* MOVIE TITLE */
.film-table .headline-3 a {
    color: #000000a6;
}

/* ----------------------------------- NETWORK ----------------------------------- */

/* USERNAME */
.person-table tbody h3 a {
    color: #000;
}

/* ----- PROFILE - MOVIE TITLE (https://letterboxd.com/<USER>/film/<TITLE>/) ------ */

h2 .film-title-wrapper a {
    color: #000000a6;
}

/* ----------------------------------- MOVIE PAGE ----------------------------------- */


.backdrop-wrapper>.backdropmask:before {
    content: "";
    display: block;
    position: absolute;
    z-index: 0;
    pointer-events: none;
    width: 1200px;
    top: 0;
    left: 50%;
    -webkit-transform: translateX(-50%);
    transform: translateX(-50%);
    height: 675px;
    background-repeat: no-repeat;
