}

/* MOVIE TITLE */
.film-title, .headline-1, .headline-2, .headline-3 {
    color: #000;
}

#featured-film-header .headline-1 {
    text-shadow: #000 0 0 0;
}
/* YEAR */
#featured-film-header p:not(#user-actions-menu) small a {
    color: #000;
}

/* DIRECTED BY */
#featured-film-header p:not(#user-actions-menu) {
    color: #000;
    text-shadow: #000 0 0 0; 
}

#featured-film-header p:not(#user-actions-menu) a {
    color: #000;
}
}
