css:
@-moz-document domain("www.refinery29.com") {
html,
body {
    background-color: #000000;
}
div.r29-article {
    background-color: #000;
}
div.about-section {
    background-color: #000;
    color: #595959;
}
article.r29-article-container {
    background-color: #000000;
    color: #000000;
}
button,
figure,
form,
h1,
h2,
h3,
h4,
header {
    color: #595959;
}
button.pink {
    background-color: #2a2a2a;
}
div.about-wrapper {
    background-color: #000;
}
div.footer-nav {
    background-color: #000;
    color: #e5059a;
}
div.card {
    background-color: #310021;
}
div.slideshow-wrapper .slideshow .intro-slide .img-container,
.slideshow-wrapper .slideshow .section-image .img-container,
.slideshow-wrapper .slideshow .static-intro-slide .img-container {
    background-color: #2a2a2a;
}
div.title {
    color: #808080
}
div.r29-article.slideshow-article {
    background-color: #000000;
}
div.section-text {
    color: #595959;
}
div.slideshow-wrapper .slideshow-nav {
    background-color: #2f2a00;
    color: #a1a1a1;
}
div.slideshow-wrapper .see-all-slides .slide-thumbnails {
    background-color: #2a2a2a;
    border-color: #2a2a2a;
}
div.news-ticker {
    background-color: #000;
}
div.featured-stories {
    background-color: #000;
}
div.cta-bar {
    background-color: #000;
}
div.module.page-header {
    background-color: #000;
}
div.module.page-header h1 {
    color: #a1a1a1;
}
div.card.product {
    background-color: #000;
}
div.row.row-ad {
    background-color: #000;
}
div.product-list-navigation {
    background-color: #000;
    color: #a1a1a1;
}
div.product-list-navigation .filter-bar {
    background-color: #000;
}
div.name {
    color: #e5059a;
}
div.product-list-navigation .filter-bar .category-select .dropdown > ul > li,
.product-list-navigation .filter-bar .collection-select .dropdown > ul > li,
.product-list-navigation .filter-bar .color-select .dropdown > ul > li,
.product-list-navigation .filter-bar .department-select .dropdown > ul > li,
.product-list-navigation .filter-bar .type-select .dropdown > ul > li {
    background-color: #2a2a2a;
}
div.product-list-navigation .filter-bar .filter-sort-wrapper .dropdown > div {
    background-color: #2a2a2a;
}
header.module-header.shopping-module .container .title {
    background-color: #000;
}
span.featured-name {
    color: #a1a1a1 !important;
}
span.story-from {
    color: #5b2600;
}
div.nav-left i svg {
    color: #a1a1a1;
}
header.condensed-header {
    background-color: #2a2a2a;
    border-bottom-color: #595959;
}
footer.global-footer {
    background-color: #000000;
}
}
