css:
@-moz-document regexp(".*gitbooks.io.*") {
/*Sidebar*/
.book-summary {    
    background-color: #2d3238;
    border: none;
    color: #d7d7d7;
}

#book-search-input {
    background-color: #24292e;
    border-bottom: 1px solid #000;
    color: #fff;
    margin-top: -2px;
}

#book-search-results .search-results .search-results-title {
    opacity: 1;
}

span > .articles {
    margin-top: .5rem;
}

.book-summary ul.summary li a,
.book-summary ul.summary li span {
    color: #d7d7d7;
}

.book-summary ul.summary li a:hover {
    transition: color 100ms ease-in-out;
    color: #fff;
    text-decoration: none;
}

.book-summary ul.summary li.active {
    background: #2f343a;
    background: linear-gradient(to right, #40464c, #3b4147, #363c42, #32373d, #2d3238 100%);
    border-radius: .25rem;
}

/*Main Body*/
.book-body {
    background-color: #343a40;
    color: #d7d7d7;
}

/*Body Header and Navigation*/
.book-header {
    background-color: #24292e;
    border-bottom: 1px solid #000;
}

.book-header .btn:hover {
    color: #fff;
}

.navigation:hover {
    color: #fff;
}

.book-header h1 {
    transition: opacity 200ms ease-in-out;
}

.dropdown-menu {
    background-color: #24292e;
    border-color: #000;
}

.dropdown-menu .dropdown-caret .caret-inner,
.dropdown-menu .dropdown-caret .caret-outer {
    margin: 0;
}

.dropdown-menu .dropdown-caret .caret-outer {
    border-bottom-color: #000;
}

.dropdown-menu .dropdown-caret .caret-inner {
    border-bottom-color: #24292e;
}

.dropdown-menu .buttons .button:hover {
    color: #fff;
}

/*Markdown*/
.markdown-section {
    color: #d7d7d7;
}

.markdown-section h1,
.markdown-section h2,
.markdown-section h3,
.markdown-section h4,
.markdown-section h5,
.markdown-section h6 {
    color: #fff;
}

.markdown-section img {
    border-radius: .5rem;
}

.markdown-section blockquote {
    background: #2f343a;
    background: linear-gradient(to right, #2f343a 75%, #30353b, #31373d, #33383e, #343a40);
    border-color: #24292e;
    color: #fff;
}

/*Code Markdown*/
.markdown-section code {
    background-color: #2d3238;
}

.markdown-section pre {
    background-color: #24292e;
    border-radius: .5rem;
}

/*Scrollbars*/
::-webkit-scrollbar {
    width: .75rem;
}
::-webkit-scrollbar-thumb {
  background-color: #24292e;
  border-radius: .25rem;
}
::-webkit-scrollbar-track {
  background-color: #2d3238;
}
}
