css:
@-moz-document regexp(".*fmovies.*") {
* {
    font-weight: normal!important;
}

html {
    background-color: #343a40;
	color: #d7d7d7;
}

header {
    background: #24292e!important;
}

#search form {
    background-color: #3f454a;
    border-color: #000;
}

/*Search Results Dropdown*/
.ui-autocomplete {
    background-color: #24292e!important;
    border: 1px solid #000;
    border-radius: .25rem;
}

#menu .sub-menu {
    background-color: #343a40;
}

#menu .sub-menu li {
    padding: 0;
}

.has-children:hover {
    background-color: #343a40!important;
    border-radius: .25rem .25rem 0 0;
}

#menu .sub-menu a {
    box-sizing: border-box;
    display: block;
    color: inherit!important;
    padding: .5rem 1rem;
    width: 100%;
}

#menu .sub-menu a:hover {
    color: #fff!important;
}

.ui-menu-item:nth-of-type(2n) {
    background-color: #2d3238;
}

.ui-menu-item a {
    display: flex;
    align-items: center;
}

.ui-menu-item .info .name {
    color: #fff;
}

.ui-menu-item .info .meta span {
    font-weight: normal;
}

.ui-state-focus {
    background-color: rgba(0,0,0,.3)!important;
}

span.ui-helper-hidden-accessible > div {
    align-items: center;
    background-color: #24292e;
    border-radius: 0 0 0 .5rem;
    box-sizing: border-box;
    color: #b2b9bf;
    display: flex;
    height: 66px;
    padding: 0 1rem 0 0;
    position: absolute;
    right: 0;
    text-align: right;
    top: 0;
    width: 212px;
    z-index: 9999999;
}

/*TV Show / Movie Block*/
.col2 ul li > figure {
    border-radius: .25rem .25rem .5rem .5rem;
}

.col2 ul li h3 {
    background: rgba(36,41,46,.9)!important;
    box-sizing: border-box;
    padding: .125rem .375rem;
    width: 100%;
}

/*Tooltips*/
.col2 .tooltip {
    background-color: #24292e;
    color: inherit;
    font-size: 1rem;
    border-radius: .5rem;
}

.col2 .tooltip .inner {
    align-items: center;
    display: flex;
}

.col2 .tooltip .inner .fa-plus {
    cursor: pointer;
}

.col2 .tooltip .inner span:nth-of-type(2) {
    background-color: #319c80;
}

.col2 .tooltip a:hover {
    color: #00ffbc;
}

.col2 .tooltip .watch-now {
    background-color: #319c80;
}

.col2 .tooltip .watch-now a {
    color: #d7d7d7;
}

.col2 .tooltip .watch-now a:hover {
    color: #fff;
}

/*Search Results Page*/
.border:after {
    border-color: #3eb495;
}

/*Pagination*/

.pagination li {
    margin-right: 0!important;
}

.pagination li a {
    border: none;
    border-top: 1px solid #2f3235;
    border-bottom: 1px solid #2f3235;
}

.pagination li:first-child a {
    border-radius: .5rem 0 0 .5rem;
    border-left: 1px solid #2f3235;
}

.pagination li:last-child a {
    border-radius: 0 .5rem .5rem 0;
    border-right: 1px solid #2f3235;
}

.pagination li:only-child a {
    background-color: #3f454a;
    border-radius: .5rem!important;
}

.pagination li:nth-of-type(2n) a {
    background-color: #3f454a;
}

/*Movie Page*/
.breadcrumb a {
    color: #d7d7d7;
}

/*Servers*/
.col3-l .eps {
    background-color: #3f454a;
    border-color: #000;
}

.col3-l .server {
    border-color: #343a40;
    color: inherit;
}

.col3-l .server .title {
    align-items: center;
    display: flex;
}

.col3-l .server .title .fa-server {
    margin-right: .5rem;
}

.col3-l .server .choose-this-server {
    border-radius: .25rem!important;
    position: absolute;
    right: 1rem;
    transition: background-color 100ms ease-in-out;
}

.col3-l .server .choose-this-server.current,
.col3-l .server .choose-this-server:hover {
    background-color: #319c80;
    color: #fff;
}

.col1 {
    background-color: #319c80;
}

.col3-btn {
    background-color: #319c80;
    cursor: pointer;
}

.col3-btn:hover {
    background-color: #39ad8e;
}

/*Movie Info*/
.col3-l .detail-r {
    color: #d7d7d7;
}

.col3-l .detail-r .more {
    background-color: #3f454a;
    border-radius: .25rem;
}

.col3-l .detail-l img {
    border-radius: .5rem;
}

/*Disqus Movie Comments*/
.comment .border,
.comment .border::after {
    border: none;
}

#disqus_thread {
    background-color: #3f454a;
    border-radius: .5rem;
    padding: 1rem;
}

/*Footer*/
footer {
    background-color: #24292e;
    color: inherit;
    font-weight: normal;
    width: 100%;
}

footer a {
    color: inherit;
}

footer b {
    color: #3eb495;
    font-weight: normal;
}

footer .sitename {
    color: #3eb495!important;
    font-size: 1.5rem;
    letter-spacing: 0;
}

footer .text-lighter {
    color: #b2b9bf;
    font-size: 1rem;
}

.copyright {
    color: #b2b9bf;
}

/*Ads*/
.ads-728,
.mgbox,
#__cedato_root_container {
    display: none!important;
    visibility: hidden!important;
}
}
