css:
@-moz-document regexp("https?://forums.alliedmods.net/(?!archive).*") {
/*
* Name:        AlliedModders Dark
* Author:      Maxximou5
* Website:     https://maxximou5.com
* GitHub:      https://github.com/maxximou5/
* Repository:  https://github.com/maxximou5/alliedmodders-dark
* Userstyle:   http://userstyles.org/styles/
* License:     https://creativecommons.org/licenses/by-sa/4.0/
* Version:     v1.0.5 (2018-09-13)
*/

html,
body {
    color: #c5c5c5 !important;
    background: #101010 !important;
}
.page {
    color: #c5c5c5 !important;
    background: #101010 !important;
}
.page > div {
    padding: 0 10% 0px 10% !important;
}
.vbmenu_control {
    color: #c5c5c5 !important;
    background-color: #222 !important;
    border-color: #101010 !important;
}
legend {
    color: #3171b6 !important;
}
/* Threads */
html body div div.page div table.tborder thead tr {
    background-color: #101010 !important;
}
.thead {
    color: #c5c5c5 !important;
    background: #0d0d0d !important;
}
.tcat {
    color: #c5c5c5 !important;
    background: #222 !important;
}
.tborder {
    color: #000000 !important;
    background: #222 !important;
    border: none !important;
    border-spacing: 0 !important;
}
.tborder > tbody > tr > td {
    color: #c5c5c5 !important;
    border-top: 2px solid #101010 !important;
}
.tinside {
    border: none !important;
}
.ttop {
    border: none !important;
}
.tbottom {
    border: none !important;
}
.tfoot {
    color: #c5c5c5 !important;
    background-color: #101010 !important;
}
.tinside .alt2, .tinside .alt3, .tbottom .alt2, .tbottom .alt3, .tbottom .thead, .ttop .alt2, .ttop .alt3, .ttop .thead {
    border-color: #424242 !important;
}
.alt1, .alt2, .alt3, .alt4, .alt1Active {
    color: #c5c5c5 !important;
    background: #303030 !important;
}
.alt1, .alt1Active {
    color: #c5c5c5 !important;
    background: #222 !important;
}
.alt2, .alt2Active {
    color: #c5c5c5 !important;
    background: #222 !important;
}
.alt3, .alt3Active {
    color: #c5c5c5 !important;
    background: #222 !important;
}
html body div div.page div form#qrform table.tborder tbody#collapseobj_quickreply tr td.alt1 {
    color: #c5c5c5 !important;
    background-image: -webkit-gradient(linear, left top, left bottom, from(#222), to(#222)) !important;
    background-image: -webkit-linear-gradient(top, #222, #222) !important;
    background-image: -moz-linear-gradient(center top, #222, #222) !important;
    background-image: -o-linear-gradient(top, #222, #222) !important;
    background-color: #222 !important;
    border-color: #242424 !important;
    box-shadow: 0 10px 4px #0F0F0F !important;
}
/* Thread Menu */
.vbmenu_popup {
    background: #222 !important;
    border: 1px solid #0A0A0A !important;
}
.vbmenu_option {
    color: #c5c5c5 !important;
    background-color: #101010 !important;
}
.vbmenu_hilite {
    color: #c5c5c5 !important;
    background-color: #222 !important;
}
/* Posts */
.quote {
    color: #c5c5c5 !important;
    background-color: #101010 !important;
}
.bpost {
    background-color: #101010 !important;
    border: none !important;
    padding: 3px !important;
}
div.alt2[dir=ltr], pre.alt2[dir=ltr] {
    background: #CCCCCC !important;
    border: 1px solid #0A0A0A !important;
    color: black !important;
}
.panel {
    color: #c5c5c5 !important;
    background-color: #10101010 !important;
    background: none !important;
    border: none !important;
}
.panelsurround {
    color: #c5c5c5 !important;
    background-image: -webkit-gradient(linear, left top, left bottom, from(#222), to(#222)) !important;
    background-image: -webkit-linear-gradient(top, #222, #222) !important;
    background-image: -moz-linear-gradient(center top, #222, #222) !important;
    background-image: -o-linear-gradient(top, #222, #222) !important;
    background-color: #222 !important;
    border-color: #242424 !important;
    box-shadow: 0 0 5px #0F0F0F !important;
}
textarea, .bginput {
    background-color: #303030 !important;
}
.vBulletin_editor {
    background: #303030 !important;
}
.vBulletin_editor {
    border: none !important;
}
/* Donation bar creates stupid extra space... */
body > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > table:nth-child(3) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(2) > div:nth-child(3) > div:nth-child(1) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1) > div:nth-child(1) {
    border: none !important;
}
/* Login bar has a default white background */
body > div:nth-child(1) > div:nth-child(1) > div:nth-child(1) > table:nth-child(3) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(2) > div:nth-child(1) {
    background-color: #222 !important;
}
html body div div.page div table tbody tr td form#notices table.tborder {
    border-top: 2px solid #ce4844 !important;
}
}

@-moz-document regexp("https?://forums.alliedmods.net/archive/.*") {
html,
body {
    color: #c5c5c5 !important;
    background: #101010 !important;
}
div.pagebody {
    color: #c5c5c5 !important;
    background: #101010 !important;
}
div.posttop {
    color: #c5c5c5 !important;
    background: #101010 !important;
}
div.posttext {
    color: #c5c5c5 !important;
    background: #222 !important;
}
#navbar {
    color: #c5c5c5 !important;
    background: #101010 !important;
}
a {
    text-decoration: none !important;
    color: #3171b6 !important;
}
a:visited {
    color: #3D5D80 !important;
}
}

@-moz-document regexp("https?://((sm.)|(www.)?)((alliedmods.net)|(sourcemod)?)\\/(.*(php.*))?"), regexp("https?://((www.)?)sourcemod.net\\/(.*(php.*))?") {
html,
body {
    color: #c5c5c5 !important;
    background: #101010 !important;
}
#nav b {
    color: #c5c5c5 !important;
}
#container {
    color: #c5c5c5 !important;
    background: #222 !important;
}
#content a:link, #content a:visited {
    color: #3171b6 !important;
}
.wrapper {
    color: #c5c5c5 !important;
    background: #222 !important;
}
.news {
    color: #c5c5c5 !important;
    background-color: #101010 !important;
    border: 0px #242424 !important;
    box-shadow: 0 10px 4px #0F0F0F !important;
}
.dapi {
    color: #c5c5c5 !important;
}
h2.title {
    color: #c5c5c5 !important;
}
/* credits.php */
.well {
    color: #c5c5c5 !important;
    background-color: #101010 !important;
    border: 0px #242424 !important;
    box-shadow: 0 10px 4px #0F0F0F !important;
}
/* newstats.php */
html body div.wrapper table.container tbody tr td#txt.each table tbody tr td table {
    color: #c5c5c5 !important;
    background-color: #101010 !important;
    border: 0px #242424 !important;
    box-shadow: 0 10px 4px #0F0F0F !important;
}
html body div.wrapper table.container tbody tr td#txt.each table tbody tr td table tbody tr td {
    color: #c5c5c5 !important;
}
}

@-moz-document regexp("https?://(((sm.)?alliedmods)|((www.)?sourcemod))?.net/new-api/.*") {
html,
body {
    background: #101010 !important;
    color: #c5c5c5 !important;
}
.header-link > a {
    color: #337ab7 !important;
}
.bs-callout {
    background-image: -webkit-gradient(linear, left top, left bottom, from(#222), to(#222)) !important;
    background-image: -webkit-linear-gradient(top, #222, #222) !important;
    background-image: -moz-linear-gradient(center top, #222, #222) !important;
    background-image: -o-linear-gradient(top, #222, #222) !important;
    border-top: none !important;
    border-bottom: none !important;
    border-right: none !important;
    box-shadow: 0 0 5px #0F0F0F !important;
}
.file:hover {
    color: #337ab7 !important;
}
.sidebar {
    background: #101010 !important;
    color: #c5c5c5 !important;
}
.panel {
    margin-bottom: 20px !important;
    background-color: #fff !important;
    border: none !important;
    border-radius: 4px !important;
    -webkit-box-shadow: 0 1px 1px rgba(0, 0, 0, .05) !important;
    box-shadow: 0 1px 1px rgba(0, 0, 0, .05) !important;
}
.nav > li > a:focus,
.nav > li > a:hover {
    text-decoration: none !important;
    background: #101010 !important;
    color: white !important;
}
.popover {
    background: #222 !important;
}
.popover-title {
    background-image: -webkit-gradient(linear, left top, left bottom, from(#222), to(#222)) !important;
    background-image: -webkit-linear-gradient(top, #222, #222) !important;
    background-image: -moz-linear-gradient(center top, #222, #222) !important;
    background-image: -o-linear-gradient(top, #222, #222) !important;
    background-color: #222 !important;
    border-color: #242424 !important;
    color: #acacac !important;
    box-shadow: 0 0 5px #0F0F0F !important;
}
.form-control {
    background: #222 !important;
    height: 44px !important;
    vertical-align: top !important;
    border-radius: 2px !important;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.16), 0 0 0 1px rgba(0, 0, 0, 0.08) !important;
    transition: box-shadow 200ms cubic-bezier(0.4, 0.0, 0.2, 1) !important;
    border: 1px solid #222 !important;
    border-top-color: rgb(34, 34, 34) !important;
    border-right-color: rgb(34, 34, 34) !important;
    border-bottom-color: rgb(34, 34, 34) !important;
    border-left-color: rgb(34, 34, 34) !important;
}
.form-control:focus {
    background: #222 !important;
    height: 44px !important;
    vertical-align: top !important;
    border-radius: 2px !important;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.16), 0 0 0 1px rgba(0, 0, 0, 0.08) !important;
    transition: box-shadow 200ms cubic-bezier(0.4, 0.0, 0.2, 1) !important;
    border: 1px solid #222 !important;
    border-top-color: rgb(34, 34, 34) !important;
    border-right-color: rgb(34, 34, 34) !important;
    border-bottom-color: rgb(34, 34, 34) !important;
    border-left-color: rgb(34, 34, 34) !important;
}
.panel-body {
    background-color: #222 !important;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.16), 0 0 0 1px rgba(0, 0, 0, 0.08) !important;
    transition: box-shadow 200ms cubic-bezier(0.4, 0.0, 0.2, 1) !important;
}
.table-hover > tbody > tr:hover {
    background-color: #282828 !important;
}
.table-bordered > tbody > tr > td,
.table-bordered > tbody > tr > th,
.table-bordered > tfoot > tr > td,
.table-bordered > tfoot > tr > th,
.table-bordered > thead > tr > td,
.table-bordered > thead > tr > th {
    border: 1px solid #242424 !important;
    border-top-width: 1px !important;
    border-bottom-width: 1px !important;
    border-top-style: solid !important;
    border-top-color: rgb(34, 34, 34) !important;
}
.breadcrumb {
    background-image: -webkit-gradient(linear, left top, left bottom, from(#222), to(#222)) !important;
    background-image: -webkit-linear-gradient(top, #222, #222) !important;
    background-image: -moz-linear-gradient(center top, #222, #222) !important;
    background-image: -o-linear-gradient(top, #222, #222) !important;
    background-color: #222 !important;
    border-color: #242424 !important;
    color: #acacac !important;
    box-shadow: 0 0 5px #0F0F0F !important;
}
pre.syntax {
    background-image: -webkit-gradient(linear, left top, left bottom, from(#222), to(#222)) !important;
    background-image: -webkit-linear-gradient(top, #222, #222) !important;
    background-image: -moz-linear-gradient(center top, #222, #222) !important;
    background-image: -o-linear-gradient(top, #222, #222) !important;
    background-color: #222 !important;
    border-color: #242424 !important;
    color: #acacac !important;
    box-shadow: 0 0 5px #0F0F0F !important;
}
}

@-moz-document regexp("https?://wiki.alliedmods.net/.*") {
body {
    color: #c5c5c5 !important;
    background: #222 !important;
}
#content .external {
    color: #3D5D80 !important;
}
div#content {
    color: #c5c5c5 !important;
    background: #101010 !important;
    border: none !important;
    box-shadow: 0 0px 10px #0F0F0F !important;
}
div#content h1,
div#content h2,
div#content h3,
div#content h4 {
    color: #c5c5c5 !important;
}
div#mw-head {
    color: #c5c5c5 !important;
    background: #222 !important;
}
div#mw-panel div.portal div.body ul li a {
    color: #3171b6 !important;
}
div#mw-panel div.portal div.body ul li a:visited {
    color: #3D5D80 !important;
}
div.vectorTabs {
    background-image: none !important;
}
div.vectorTabs span > a {
    color: #c5c5c5 !important;
    background: #222 !important;
}
div.vectorTabs li.new a, div.vectorTabs li.new a:visited {
    color: #c5c5c5 !important;
    background: #222 !important;
}
div.vectorTabs li.selected a, div.vectorTabs li.selected a:visited {
    color: #8C8C8C !important;
}
div.vectorTabs li.selected {
    color: #c5c5c5 !important;
    background: #222 !important;
}
div#footer {
    color: #c5c5c5 !important;
}
div#footer ul li {
    color: #c5c5c5 !important;
}
.et2th {
    background-color: #1A1A1A !important;
}
.et2td {
    background-color: #222 !important;
}
.catlinks {
    color: #c5c5c5 !important;
    background-color: #222 !important;
    border: 0px #242424 !important;
    box-shadow: 0 0px 10px #0F0F0F !important;
}
#toc, .toc {
    color: #c5c5c5 !important;
    background-color: #222 !important;
    border: 0px #242424 !important;
    box-shadow: 0 0px 10px #0F0F0F !important;
}
#mw-page-base {
    background: none !important;
}
a {
    text-decoration: none !important;
    color: #3171b6 !important;
}
a:visited {
    color: #3D5D80 !important;
}
a.new, #p-personal a.new {
    color: #ce4844 !important;
}
}
