css:
@-moz-document domain(etf2l.org) {
:root {
    --etf2l-bg: #232529;
    --etf2l-bg2: #292b2f;
    --etf2l-link: #00BBFF;
    --etf2l-hover: #266CB7;
}
a:link,
a:visited {
    color: var(--etf2l-link);
    text-decoration: none!important;
    border-bottom: none;
}
a:hover {
    color: var(--etf2l-hover)!important;
    text-decoration: underline!important;
    border-bottom: none;
}
#header {
    background-image: url(https://i.imgur.com/YVu4Nn2.png);
    background-position: center;
    background-color: var(--etf2l-bg);
}
body,
#footer,
.postmeta,
#rs-discuss .breadcrumbs,
.rowgroup-odd tr,
#rs-discuss > div.post-wrapper > div.post-block-odd,
div.rec_search,
div.rec_search_filter,
div.rec,
.rowgroup-odd tr,
.fix tr:nth-child(odd) td,
.events li ul li a,
.events li ul li,
.fix,
#activity-widget #the-comment-list .comment-item{
    background-color: var(--etf2l-bg);
}
#sidebars,
.rowgroup tr,
#rs-discuss > div.post-wrapper > div.post-block,
.fix tr:nth-child(even) td {
    background-color: var(--etf2l-bg2);
}
#eventcontent h2,
.post h4,
.post h2,
#sidebars h2,
small,
strong,
.calendar tr.head > td,
.events li,
div.rec h2,
h2,
.wrap,
table.form-table tbody tr th,
h1{
    color: white;
}
#footer a,
.post a,
#sidebars a,
.dropped a:visited {
    color: var(--etf2l-link);
}
blockquote,
textarea,
input,
.tablesorter tbody tr td,
.calendar tr td{
    background-color: var(--etf2l-bg);
    color: white;
}
#login-3 ul li a:link,
#login-3 ul li a:visited{
    color: var(--etf2l-link);
}
#search-3 form input {
    background-color: var(--etf2l-bg2);
    border: 1px solid black;
}
::placeholder {
    color: #AAA7A7;
}
#wrapper {
    background-color: var(--etf2l-bg2);
    background-image: none;
}
#menu-help li a:hover {
    text-decoration: none!important;
    color: #9FBBC5!important;
}
#content {
    background-color: var(--etf2l-bg2);
    color: white;
}
.widebg {
    background-color: var(--etf2l-bg2)!important;
    background-image: none!important;
}
ul#eventtabs li a,
ul#eventtabs li a:hover {
    background-color: var(--etf2l-bg);
    text-decoration: none!important;
}
ul#eventtabs li.active a {
    background-color: var(--etf2l-bg2);
    text-decoration: none!important;
}
.widget_table tbody td {
    border-bottom: none;
}
#sidebarSponsor {
    background-color: #343639;
}
#rs-discuss .searchpanel input {
    color: white;
    background-color: var(--etf2l-bg);
    border: 2px solid #484c53;
}
#rs-discuss .category {
    background-color: #363940;
}
div.rec_search_filter {
    background-color: var(--etf2l-bg)!important;
}
a:link strong {
    color: var(--etf2l-link);
    text-decoration: none!important;
    border-bottom: none;
}
a:visited strong {
    color: var(--etf2l-link);
    text-decoration: none!important;
    border-bottom: none;
}
a:hover strong {
    color: #126D8E!important;
    text-decoration: none!important;
    border-bottom: none;
}
#content span {
    color: white!important;
}
.fix tr.blue:nth-child(odd) td {
    background-image: none!important;
    background-color: #273044;
}
.fix tr.blue:nth-child(even) td {
    background-image: none!important;
    background-color: #2c364c;
}
.fix tr.red:nth-child(odd) td {
    background-image: none!important;
    background-color: #372121;
}
.fix tr.red:nth-child(even) td {
    background-image: none!important;
    background-color: #402626;
}

#message,
.update-nag{
    background-color: var(--etf2l-bg2);
    color:white;
}
select,
textarea{
    background-color: var(--etf2l-bg2);
    color:white;
    border: 1px solid #484c53;
}
.widefat tr:nth-child(odd) td{
    background-color: var(--etf2l-bg2);
    color:white;    
}
.widefat tr:nth-child(even) td {
    background-color: var(--etf2l-bg);
    color:white;
}
table.widefat{
    background-color:black;
    border:1px solid #484c53;
}
table.widefat thead tr th{
    color:white;
}
.postbox {
    background-color:var(--etf2l-bg);
    border:1px solid #484c53;
}
.postbox .hndle {
    border-bottom:2px solid #484c53;
}
#dashboard-widgets h3 {
    color:white;
}
#future-posts ul span, #published-posts ul span {
    color:white;
}
}
