css:
@-moz-document domain("www.rockpapershotgun.com") {
.leaderboards,
.leaderboards .ad-desktop,
.leaderboards .ad-desktop > *,
.leaderboards .ad-desktop iframe,
.ad-leaderboard-last,
.ad-leaderboard-low, 
.ad-leaderboard-last iframe {
    max-height: 15vh;
}

.ads-enabled .page, .page {
    width: 100%;
    max-width: 1920px;
    padding: 0 0.5%;
}

.container-760,
.container-690 {
    max-width: none;
}

.blog-post,
.spotlight,
.spotlight-bar,
.video-hub,
.video-hub.in-article,
.article,
.have-you-played-widget,
.archive-item,
aside .small-list,
.game-spotlight,
.guides-wall,
#comments {
    background: white;
    padding: 1em;
    box-shadow: 0 0 50px rgba(0,0,0,0.1);
}

.blog-post > .icon {
    left: 0;
}

.blog-post .images.count-1,
.blog-post .image-grid.count-1,
.blog-post .image-grid.count-2,
.blog-post .image-grid.count-4 {
    float: left;
    width: auto;
    margin: 0 1em 0 0;
}

.blog-post .container-690 {
    display: table-cell;
}

.blog-post .images img {
    max-width: calc((100vw - 316px) / 2 - 4em);
}

@media screen and (min-width: 767px){
.blog-post {
    padding-bottom: 0;
}
}
.page>main {
  background-color: transparent;
}

#comments {
     padding-right: 316px;
}

.article main {
     max-width: 760px;
}

.video-hub.in-article .video-archive,
.game-spotlight:last-of-type {
    padding: 1em;
}

.page>main .below>div:last-of-type {
    padding-bottom: 1em;
}

.spotlight {
    padding: 0;
}

.spotlight-other {
    padding: 1em;
}
}
