css:
@-moz-document domain("overstalk.io") {
/** Full document **/
  body {
    background-image: url('http://i.imgur.com/c0nMT8F.png'), url('http://i.imgur.com/lyBbwL4.png');
    background-size: 100%;
    background-color: rgba(32, 37, 41, 1.0) !important; 
    background-repeat: no-repeat, repeat-y;
    background-attachment: fixed;
  }
  
  /** overstalk.io header **/
  .navbar {
     background-color: rgba(0, 0, 0, 0.35) !important;
  }
  .navbar .navbar-brand, li.nav-item.active > a, .navbar-text {
    color: white !important;
  }
  li.nav-item > a {
    color: rgba(255,255,255,0.5) !important;
  }
  
  
  /** ALL links **/
  .os-post-header span a {
    font-weight: bold;
    text-shadow: 0px 0px 5px rgba(132, 137, 141, 1.0);
  }
  
  
  /** Title at the top **/
  .os-title {
    color: rgba(255, 255, 255, 0.7);
  }
  
  /** Website Checkboxes at the top **/
  .form-check-label {
    color: rgba(232, 237, 241, 0.6);
  }
  
  /** Upper part of a box (metainfo) **/
  .os-post.card-header.clearfix {
    background-color: rgba(12, 17, 21, 0.5); 
  }
  
  /** Highlight Kaplan/Goodman posts **/
  .os-post[data-post-author*=Kaplan] , .os-post[data-post-author*=Goodman] {
      border: 2px solid rgba(252, 257, 041, 0.8);
  }
  .os-post[data-post-author*=Kaplan] .os-author, .os-post[data-post-author*=Goodman] .os-author {
    color: rgba(252, 257, 041, 1.0);
    text-shadow: 0 0 10px black;
    font-weight: bold;
  }
  
  /** Lower part of a box (actual post) **/
  .card {
    color: rgba(0, 180, 255, 1.0);
    font-size: 1.0625rem;
    background-color: rgba(22, 27, 31, 0.9); 
  }

  .os-post-content blockquote {
    border-left: .25rem solid rgba(0, 180, 255, 0.3);
  }
}
