css:
@-moz-document url-prefix("https://mariokartboards.com/"), url-prefix("https://www.mariokartboards.com/") {
/*header*/
.p-header {
    color: #dcdcdc;
    background: #00162b;
    background-image: url(https://i.imgur.com/D7CmCLN.png);
    background-position: center;
    box-shadow: inset 0px 0px 50px #000;
    background-repeat: no-repeat;
}

/*remove box-shadow from header*/
.p-header-inner {
    max-width: 1200px;
    padding: 0 10px;
    margin: 0 auto;
    box-shadow: none !important;
}

/*nav bar*/
.p-nav {
    color: #dcdcdc;
    background: #001d3c;
    border-top: 2px solid #003e63;
    box-shadow: inset 0px 2px 5px #000c19;
}

.p-nav-list .p-navEl.is-selected .p-navEl-link {
    padding-right: 15px;
    background-color: #002c5a !important;
    color: #a7a7a7 !important;
    box-shadow: inset 0px 2px 5px #000c19 !important;
}

.p-navgroup-link.p-navgroup-link--user {
    background-color: #001d3c;
    overflow: hidden;
    white-space: nowrap;
    word-wrap: normal;
    text-overflow: ellipsis;
    max-width: 150px;
    box-shadow: inset 0px 2px 5px #000c19;
}

.p-navgroup-link.badgeContainer {
    background-color: #001d3c;
    box-shadow: inset 0px 2px 5px #000c19;
    border-left: none;
    opacity: 1;
    position: relative;
}

a.p-navgroup-link.p-navgroup-link--iconic.p-navgroup-link--search {
    background-color: #001d3c;
    box-shadow: inset 0px 2px 5px #000c19;
}

/*bottom nav bar*/
.p-sectionLinks {
    font-size: 13px;
    color: #a7a7a7;
    background: #000b15;
    border-bottom: 2px solid #0e2133;
    border-top: 1px solid #00254c;
}

/*background*/
.p-body {
    min-height: 1px;
    background-color: #070707 !important;
    background-image: url(https://i.imgur.com/JUcrcPq.jpg);
}

/*body*/
.p-body-inner {
    background-color: #040910;
    box-shadow: 0px 17px 50px #32547d;
}

.block-body {
    background-color: #000b15;
}

h3.block-minorHeader {
    background-color: #000b15;
}

.block-header {
    padding: 4px 12px;
    margin: 0;
    font-weight: 500;
    text-decoration: none;
    font-size: 18px;
    color: #fff;
    border-bottom: 1px solid #011326;
    background: linear-gradient(0deg, #032b56, #00162b);
}

.p-footer {
    font-size: 13px;
    color: #3c3c3c;
    background: #000b15;
    border-top: 2px solid #13283d;
    box-shadow: inset 0px 3px 5px #000;
}

.block-footer {
    padding: 6px 10px;
    font-size: 12px;
    color: #b5b5b5;
    border-top: 1px solid rgba(247,247,247,0.1);
    background: linear-gradient(180deg, #001a35, #000b15);
}

.p-body-sidebar .block .message--simple .message-cell.message-cell--user {
    flex: 0 0 30px;
    background-color: #010c16;
    border: none;
}

textarea.input.input--fitHeight {
    resize: none;
    background-color: #00070c;
}

/*profile menu*/
.menu--structural.menu--right .menu-content {
    border-top-right-radius: 0;
    background-color: #00162b;
}

.menu-footer {
    padding: 6px 15px;
    font-size: 12px;
    color: #b5b5b5;
    border-top: 1px solid rgba(247,247,247,0.1);
    background: linear-gradient(180deg, #000b15, #00162b);
}

/*profile page*/
h2.block-tabHeader.block-tabHeader--memberTabs.tabs.hScroller {
    background-color: #001d3c;
}

.block-tabHeader .tabs-tab.is-active {
    font-weight: 400;
    color: #007bff;
    border-color: #e4e4e4;
}

.message-inner {
    display: flex;
    background-color: #010c16;
}

.block--messages .message-cell:first-child {
    border-radius: 0;
    border-top-left-radius: 3px;
    border-bottom-left-radius: 3px;
    background-color: #00070c;
}

.message-responseRow {
    margin-top: -1px;
    background: #00070c;
    border: 1px solid rgba(247,247,247,0.1);
    padding: 6px;
}

.memberHeader-main {
    background: #00050a;
    border-bottom: 1px solid rgba(247,247,247,0.1);
}

.button.button--link, a.button.button--link {
    background: #001d3c;
    color: #5698ff;
    border-color: rgba(242,242,242,0.1) rgba(255,255,255,0.1) rgba(255,255,255,0.1) rgba(242,242,242,0.1);
}

.block-row.js-replyNoMessages {
    background-color: #001d3c;
}

/*member page*/
.memberOverviewBlock-seeMore {
    background: #001d3c;
    border: 1px solid rgba(247,247,247,0.1);
    padding: 3px;
    text-align: center;
    margin-top: auto;
}

.input {
    font-size: 15px;
    color: #dedede;
    background: #060606;
    border-width: 1px;
    border-style: solid;
    border-top-color: rgba(255,255,255,0.1);
    border-right-color: rgba(247,247,247,0.1);
    border-bottom-color: rgba(247,247,247,0.1);
    border-left-color: rgba(255,255,255,0.1);
    border-radius: 4px;
    padding: 6px;
    display: block;
    width: 100%;
    line-height: 1.4;
    text-align: left;
    word-wrap: break-word;
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    -webkit-transition: all .25s ease;
    transition: all .25s ease;
}

h2.block-tabHeader.tabs.hScroller {
    background-color: #001d3c;
}

/*member card popup*/
.memberTooltip-info, .memberTooltip-actions {
    padding: 6px;
    background-color: #010c16;
}

.memberTooltip-header {
    display: table;
    table-layout: fixed;
    width: 100%;
    padding: 6px;
    background: #040404;
    border-bottom: 1px solid rgba(247,247,247,0.1);
}

.menu-row.menu-row--highlighted {
    color: #dedede;
    background: #040404;
}

/*account details page*/
.block-formSectionHeader {
    padding: 6px 10px;
    margin: 0;
    font-weight: 400;
    text-decoration: none;
    font-size: 17px;
    background-color: #001d3c;
    color: #dedede;
    border-top: 1px solid rgba(255,255,255,0.1);
    border-bottom: 1px solid rgba(240,240,240,0.1);
}

.blockLink.is-selected {
    font-weight: 700;
    background: #001d3c;
    border-left: 3px solid #dedede;
    padding-left: 7px;
}

/*alert menu*/
li.block-row.block-row--separated.block-row--alt {
    background-color: #000b15;
}

li.block-row.block-row--separated.block-row--highlighted {
    background-color: #001d3c;
    color: #dedede;
}

/*leftover stuff in threads and such*/
.block-filterBar {
    padding: 6px 10px;
    font-size: 13px;
    color: #5698ff;
    border-bottom: 1px solid rgba(255,255,255,0.1);
    background: linear-gradient(0deg, #010c16, #001d3c);
}

.has-js textarea.input[rows="1"][data-single-line] {
    overflow: hidden;
    resize: none;
    background-color: #060606;
}

.message .likesBar {
    margin-top: 10px;
    padding: 6px;
    border: none;
    border-left-color: transparent;
    border-radius: 5px;
    background-color: #00080e;
    box-shadow: 0px 0px 34px #00060a;
}

.message-userArrow:after {
    position: absolute;
    top: -9px;
    right: -10px;
    content: "";
    border: 9px solid transparent;
    border-left-width: 0;
    border-right-color: #010c16;
}

.message-cell.message-cell--user {
    background-color: #00070c;
}

.fr-toolbar.fr-top {
    border-top-color: #258bf1;
    background-color: #00070c;
}

.fr-box.fr-basic .fr-element {
    color: #dedede;
    background-color: #060606;
    padding: 10px;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    overflow-x: auto;
    min-height: 40px;
}

/*notices*/
.messageNotice {
    margin: 6px 0;
    padding: 6px 10px;
    color: #d2d2d2;
    background: #131b23;
    font-size: 12px;
    border-left: 2px solid #258bf1;
}

/*quotes*/
.bbCodeBlock-title {
    padding: 6px 10px;
    font-size: 13px;
    color: #3a83f5 !important;
    background: #001d3c !important;
}

.bbCodeBlock.bbCodeBlock--quote .bbCodeBlock-content {
    font-size: 13px;
    background-color: #040910;
}

.bbCodeBlock {
    margin: .75em 0;
    background: #323232;
    border: 1px solid rgb(37, 139, 241);
    border-left: 3px solid #258bf1;
}

.bbCodeBlock-expandLink {
    display: none;
    position: absolute;
    top: 87px;
    left: 0;
    right: 0;
    height: 75px;
    cursor: pointer;
    background: transparent;
    background: linear-gradient(to bottom, rgba(50,50,50,0) 0%, #00070c 70%);
}

/*spoiler*/
.bbCodeBlock-content {
    position: relative;
    padding: 6px 10px;
    background-color: #00070c;
}

/*best answer*/
.QT_best_answer.QT_message_background .message-cell--main {
    background: #0a2640;
}

.QT_best_answer.QT_message_background .message-userArrow:after {
    border-right-color: #0a2640;
}

/*new post*/
.message-newIndicator {
    font-size: 90%;
    color: #fff;
    background: #003e63;
    border-radius: 2px;
    padding-top: 1px;
    padding-right: 4px;
    padding-bottom: 1px;
    padding-left: 4px;
}

/*locked thread*/
.blockStatus {
    color: #dedede;
    background: #001d3c;
    border-width: 1px;
    border-style: solid;
    border-top-color: rgba(247,247,247,0.1);
    border-right-color: rgba(255,255,255,0.1);
    border-bottom-color: rgba(255,255,255,0.1);
    border-left-color: rgba(255,255,255,0.1);
    border-left: 3px solid #258bf1;
    border-radius: 4px;
    margin: 0;
    padding: 6px 0;
    font-size: 13px;
    text-align: left;
    -webkit-transition: all .25s ease;
    transition: all .25s ease;
}

/*stickied threads*/
.structItemContainer-group--sticky {
    background-color: #00080e;
    border-bottom: 2px solid #254881;
}

form.structItem {
    background-color: #000910 !important;
}

/*buttons*/
.button, a.button {
    display: inline-block;
    text-decoration: none;
    cursor: pointer;
    border: 1px solid transparent;
    white-space: nowrap;
    -webkit-transition: background-color .25s ease;
    transition: background-color .25s ease;
    font-size: 13px;
    border-radius: 4px;
    padding-top: 5px;
    padding-right: 10px;
    padding-bottom: 5px;
    padding-left: 10px;
    text-align: center;
    color: #67b4e2;
    background: #083258 !important;
    border-color: #3d88ff #70a8ff #70a8ff #3d88ff !important;
}

.button:hover, a.button:hover {
    color: #fff !important;
}

/*post new thread button*/
.button.button--cta, a.button.button--cta {
    color: #fff;
    background: #464646 !important;
    border-color: #a0a0a0 #cbcbcb #a0a0a0 #cbcbcb !important;
}

.button.button--cta:hover, a.button.button--cta:hover {
    color: #fff;
    background: #777777 !important;
    border-color: #a0a0a0 #cbcbcb #a0a0a0 #cbcbcb !important;
    transition: ease-in-out 0.3s;
}

/*twitter box*/
.SandboxRoot.env-bp-min .timeline-Tweet {
    padding: 10px;
    background-color: #000b15;
}

/*streams*/
.p-body-pageContent>.tabs--standalone:first-child {
    margin-bottom: 10px;
    background-color: #001d3c;
}

.tabs--standalone {
    padding: 0;
    margin-bottom: 20px;
    font-weight: 400;
    border-width: 1px;
    border-style: solid;
    border-top-color: rgba(247,247,247,0.1);
    border-right-color: rgba(255,255,255,0.1);
    border-bottom-color: rgba(255,255,255,0.1);
    border-left-color: rgba(255,255,255,0.1);
    font-size: 15px;
    color: #5698ff;
    background: #001d3c;
}

.rio-tiled-item.rio-featured {
    background-color: #001d3c !important;
    border: 1px solid #d0d0d0;
}

.rio-tiled-block .rio-tiled-item {
    background: #001d3c !important;
    border: 1px solid #d0d0d0;
}

.rio-tiled-item.rio-offline {
    background-color: #111417 !important;
}

.block-container.rio-showcase-container {
    background: #020508;
    box-shadow: inset 0px 0px 25px #255992;
    border-width: 1px;
    border-style: solid;
    border-color: #255992;
    border-radius: 4px;
    transition: all .25s ease;
}

/*donation text box*/
.blockMessage.campaign-description {
    background-color: #001d3c !important;
}

/*message block in some areas*/
.blockMessage {
    background-color: #000b15 !important;
}

/*footer bar in some areas*/
.formSubmitRow-bar {
    background-color: #000b15 !important;
}

/*queue*/
.formRow>dt {
    border-right: 1px solid transparent;
    background: #040910;
    border-color: rgba(247,247,247,0.1);
    text-align: right;
    width: 33%;
    padding: 15px 10px 15px 10px;
}

/*staff bar*/
.p-staffBar {
    font-size: 13px;
    color: #e4e4e4;
    background: #00060c;
    border-bottom: 1px solid #003e63;
}

/*reports*/
h2.block-tabHeader.tabs {
    background: #001d3c;
}

.block-row.block-row--alt {
    color: #dedede;
    background: #001d3c;
}

.label.label--accent {
    color: #dedede;
    background: #001d3c;
    border-color: #fff;
    border-color: #92c0eb;
}

/*scroll bar*/
::-webkit-scrollbar {
    width: 13px ! important;
    height: 10px ! important;
    background: #000000 ! important;
    border-bottom: 1px solid #044a8e !important;
    border-left: 1px solid #044a8e !important;
    border-top: 1px solid #044a8e !important;
    border-right: none !important;
}

*::-webkit-scrollbar-button {
display: none !important;
}

*::-webkit-scrollbar-thumb,
*::-webkit-scrollbar-track {
border-top: 1px solid rgb(2, 55, 97) !important;
border-bottom: 1px solid rgb(2, 55, 97) !important;
box-shadow: 0px 0px 10px rgb(3, 86, 160) ! important;
}

*::-webkit-scrollbar-thumb {
    background: #0356a0;
    box-shadow: inset 0px 0px 20px rgb(0,0,0) !important;
}

/*invisible*/
.username--invisible {
    display: none;
    color: #8c8c8c;
}
}
