css:
@-moz-document domain("www.rockpapershotgun.com") {
.leaderboards {
    height: 0;
}
.ad-desktop, .ad-hd, .ad-leaderboard-top-desktop {
    display: none;
    visibility: hidden;
}
.ads-enabled .page>aside, #skyscraper, #right-rail {
    display: none;
    visibility: hidden;
}
.ads-enabled .page>main .above {
    padding-right: 0;
    min-height: 0;
}
body, .d-header {
    color: #9a9a9a;
    background-color: #0c0c0c;
}
.header-desktop {
    background-color: #131313;
    border-bottom: 1px solid #191919;
}
input, #archive-filters input[type=text], #archive-filters select, input[type=radio]+label {
    background-color: #131313;
    color: #fff;
}
.footer {
    background-color: #131313;
}
.footer, .pager {
    border-top: 1px solid #191919;
}
.page>main {
    background-color: #131313;
}
.header-desktop .primary a, #archive-filters label, .page-title, .support-us-header-inner .title {
    color: #009C7C;
}
.header-desktop .secondary a {
    color: #008f72;
}
.title, .title a {
    color: #aaa;
}
.blog-post.feature .title a {
    color: #fff;
}
.comment .person {
    color: #ccc;
}
.section-title {
    color: #999;
}

.header-desktop .support-us .button {
    background-color: #652fd0;
}
.comment.administrator {
    border-left-color: #652fd0;
}
.comment.administrator .button {
    background-color: #652fd0;
    border-color: #652fd0;
    color: #fff;
}
.video-hub.in-article, .video-hub.in-article .video-archive .playlist-item, .billboard-container, #comments, .about-the-author, #recommendations, .comments-jump, .article main .wp-caption-text, .article main figcaption, .article-pager, .below, .spotlight-bar {
    border-bottom: 1px solid #444;
}
.about-the-author .name {
    color: #fff;
}
.about-the-author .details {
    border-left: 1px solid #999;
}
.article header .breadcrumb a {
    color: #999;
}
.article header .metadata .author-name a {
    color: #008f72;
    
}
.article-pager, .below {
    border-top: 1px solid #444;
}
.spotlight-bar .spotlight-bar-item {
    border-right: 1px solid #444;
}
.header-desktop .account {
    border-left: 1px solid #444;
}

.comment, .have-you-played-widget .mpu-container {
    border-left: 1px solid #444;
}    

.spotlight-container {
    border-top: 1px solid #444;
    border-bottom: 1px solid #444;
}

.spotlight-item, .video-hub .video-player, #archive-content>.section-title {
    border-bottom: 1px solid #444;
}

::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}
::-webkit-scrollbar-button {
  width: 0;
  height: 0;
}
::-webkit-scrollbar-thumb {
  background: #652fd0;
  border: 0 none;
  border-radius: 1px;
}
::-webkit-scrollbar-thumb:hover {
  background: #fff;
}
::-webkit-scrollbar-thumb:active {
  background: #009C7C;
}
::-webkit-scrollbar-track {
  background: #222;
  border: 0 none;
  border-radius: 1px;
}
::-webkit-scrollbar-track:hover {
  background: #292929;
}
::-webkit-scrollbar-track:active {
  background: #333333;
}
::-webkit-scrollbar-corner {
  background: transparent;
}

.button.disabled {
    border-color: #333;
    background-color: #333;
    color: #777;
}
.container-760, .article header, .article main {
    max-width: 1124px;
}
.container-690 {
    max-width: 1052px;
}
.article main iframe {
    width: 1124px;
    height: 632px;
}
#archive-filters {
    background-color: #101010;
}
#archive-filters input+label {
    color: #777;
}
.support-us-card .support-us-card-inner {
    background-color: #222;
}
}
