@-moz-document domain("muffwiggler.com") {
body {
    margin: 0;
    border: none;
    width: 100%;
}
body * {
    box-sizing: border-box;
}
input:not([type="submit"]):not(.button):not([type="button"]),
textarea,
select {
    background: rgba(255, 255, 255, 0.2) !important;
    border: none;
    padding: 5px;
    color: #85cfa4;
}
.maintitle, [style="color:#4A4646"] {
    color:#85cfa4 !important;
}
.bodyline > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) {
    display: block;
    position: absolute;
    left: 0;
}
.bodyline > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > a {
    width: 150px;
    height: 150px;
    border-radius: 50%;
    display: block;
    overflow: hidden;
    position: relative;
}
.bodyline > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > a > img {
    min-width: 100%;
    border: none;
    max-width: none;
    max-height: 150px;
    width: auto;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
}
.mainmenu {
    display: inline-block;
}
.forumline,
.bodyline table {
    max-width: 960px;
    margin-left: auto;
    margin-right: auto;
    height:auto;
}
.forumline {
    margin: 40px auto;
}
.bodyline {
    border: none;
    background: #062744;
}
table,
.forumline {
    border-collapse: collapse;
    border: none;
}
.happyface {
    background: transparent;
}
.happyface * {
    background-image: none;
}
a.forumlink:hover,
a.mainmenu:hover,
a.nav:hover,
a:link,
a:active,
a:visited,
a:hover,
a.topictitle:hover,
a:hover,
a.topictitle:hover td.cat,
td.catHead,
td.catSides,
td.catLeft,
td.catRight,
td.catBottom {
    text-decoration: none;
}
table[width="18"][height="18"] {
    width: 100%;
}
img[src*="templates"] {
    filter: invert(1) contrast(0.5) sepia(1) hue-rotate(150deg) contrast(1) saturate(1.5);
}
img[src*="templates/subMW/images/years/goo.gif"] {
    filter: invert(1) contrast(0.5) sepia(1) hue-rotate(100deg) contrast(1) saturate(1.5);
}
.postbody {
    color: #bbcbd9;
}
.name {
    color: #85cfa4;
}
th,
td {
    border: none;
    padding: 10px;
    outline: none;
    white-space: normal;
}
a:hover,
a.topictitle:hover,
a:hover,
a.topictitle:hover td.cat,
td.catHead,
td.catSides,
td.catLeft,
td.catRight,
td.catBottom {
    color: #e75a7c;
}
td.cat,
td.catHead,
td.catSides,
td.catLeft,
td.catRight,
td.catBottom,
td.rowpic {
    background: #062744;
    color: #fff;
    height: auto;
}
th.thHead,
td.catHead,
th.thHead,
th.thSides,
th.thTop,
th.thLeft,
th.thRight,
th.thBottom,
th.thCornerL,
th.thCornerR,
th.thSides,
td.catSides,
td.spaceRow,
th.thBottom,
td.catBottom,
th.thLeft,
td.catLeft,
td.rowpic {
    border: none;
    background-image: none;
}
.cattitle {
    color: #fff;
}
.postdetails {
    color: #84a0ba;
}
a.topictitle:visited {
    text-decoration: none;
    color: #84a0ba;
}
.topictitle,
h1,
h2 {
    color: #bbcbd9;
}
.gen,
.genmed,
.gensmall {
    color: #84a0ba;
}
.nav {
    color: #84a0ba;
}
.quote,
.code {
    background: #425b72;
    color: #84a0ba;
    border: none;
}
.header_image {
    width: 409px;
    overflow: hidden;
    padding-left: 100%;
    box-sizing: border-box;
    background: url('https://i.imgur.com/1UJXhRY.png') center center / contain no-repeat;
}
.mw {
    display: none;
}
hr {
    display: none;
}
.cat_image {
    display: block;
}
th.thRight,
td.catRight,
td.row3Right {
    border: none;
}
.helpline {
    color: #85cfa4;
}
input.button,
input.mainoption,
input.liteoption,
input[type="button"] {
    outline: none;
    border: none;
    color: #062744;
    background: #85cfa4;
}
input[type="textarea"],
input[type="text"],
select {
    outline: none;
    border: none;
    background-color: rgba(255, 255, 255, 0.2) !important;
}
}

@-moz-document domain("muffwiggler.com") {
@media screen and (max-width: 790px) {
    body > table,
    body > table tbody,
    body > table tbody tr,
    body > table tbody tr td {
        display: block;
        width: 100%;
        box-sizing: border-box;
    }
    .forumline,
    .bodyline table {
        display: block;
    }
    input {
        max-width: 100%;
    }
    form[action="posting.php"] > .forumline > tbody > tr:nth-of-type(3) > td td {
        display: inline-block;
        padding: 2px;
    }
    form[action="posting.php"] td[colspan="9"] {
        width: 100%;
    }
    textarea {
        max-width: 100%;
    }
    table[width="450"] {
        width: 100%;
    }
    .gen textarea.post {
        width: 100% !important;
    }
    table.forumline,
    .forumline tr {
        display: block;
        background: #304b64;
        width: 100%;
        position: relative;
    }
    .forumline,
    .bodyline table {
        width: 100%;
    }
    .forumline tbody {
        width: 100%;
        display: block;
    }
    .forumline tr::after {
        display: block;
        content: "";
        clear: both;
        float: none;
    }
    .forumline tr td {
        display: block;
        width: auto;
        height: auto;
    }
    .forumline tr td.rowpic {
        display: none;
    }
}
}

@-moz-document url("https://www.muffwiggler.com/forum/"), regexp("https://www\\.muffwiggler\\.com/forum/viewforum\\.php.*"), regexp("https://www\\.muffwiggler\\.com/forum/index\\.php.*") {
.forumline:last-of-type td.row1:nth-child(1) {
    position:static;
    width:100%;
}

@media screen and (max-width: 790px) {
    .forumline .row2,
    .forumline .row4 {
        display: none;
    }
    .forumline th.thTop,
    .forumline th.thCornerL,
    .forumline th.thCornerR {
        display: none;
    }
    .forumline tr:nth-of-type(odd) td {
        background: #1c3a55;
    }
    .forumline tr:nth-of-type(even) td {
        background: #304b64;
    }
    .forumline tr td.catHead,
    .forumline tr td.catLeft {
        background: #062744;
    }
    .forumline tr td.row3,
    .forumline tr td.row3Right {
        display: block;
        float: none;
        width: 100%;
        padding: 0;
        text-align: left;
        padding-left: 50px;
    }
    .forumline tr td.row3Right {
        padding-bottom: 10px;
    }
    .forumline td.row2:nth-child(3) {
        display: block;
        text-indent: -9000px;
        height: 0;
        width: 100%;
        clear: both;
        float: none;
        padding: 0;
    }
    .forumline td.row2:nth-child(3) * {
        display: none;
    }
    .forumline td.row1:nth-child(1) {
        position: absolute;
        left: 0;
        top: 0;
        height: 33px;
        width: 50px;
    }
    .forumline td.row1:nth-child(1) img {
        height: auto;
        max-width: 100%;
        min-height: 20px;
    }
    .forumline td.row1:nth-child(2) {
        padding-left: 50px;
    }
}
}

@-moz-document regexp("https://www\\.muffwiggler\\.com/forum/viewtopic\\.php.*") {
@media screen and (max-width: 790px) {
    th.thLeft {
        display: none;
    }
    th.thRight {
        width: 100%;
        float: none;
    }
    a.avatar_images {
        display: block;
        max-width: 80px;
        position: absolute;
        left: 0;
        top: 10px;
    }
    a.avatar_images img {
        max-width: 100%;
    }
    .postdetails {
        display: block;
        position: relative;
        padding-left: 90px;
        padding-top: 10px;
    }
    .postdetails br {
        height: 0;
        line-height: 0;
    }
    .forumline tr > td.row1:nth-child(2) table td {
        padding: 0 0 10px 0;
    }
    .forumline tr > td.row1:nth-child(2) table .postdetails {
        padding: 0;
    }
    .forumline tr > td.row1:nth-child(1) {
        padding-bottom: 0;
    }
    .forumline tr > td.row1:nth-child(1) br {
        line-height: 0;
    }
    .forumline tr > td.row1:nth-child(2) {
        padding-top: 0;
    }
    .forumline tr > td.row2:nth-child(2) table td {
        padding: 0 0 10px 0;
    }
    .forumline tr > td.row2:nth-child(2) table .postdetails {
        padding: 0;
    }
    .forumline tr > td.row2:nth-child(1) {
        padding-bottom: 0;
    }
    .forumline tr > td.row2:nth-child(1) br {
        line-height: 0;
    }
    .forumline tr > td.row2:nth-child(2) {
        padding-top: 0;
    }
    table.forumline,
    .forumline tr {
        background: transparent;
    }
}
}

@-moz-document regexp("https://www\\.muffwiggler\\.com/forum/posting\\.php.*") {
@media screen and (max-width: 790px) {
    .forumline > tbody > tr:nth-of-type(3) td {
        display: inline-block;
        padding: 2px;
    }
    textarea.post {
        width: 100%;
    }
    .forumline table {
        display: block;
    }
}
}
