css:
@-moz-document domain("cp.roleplay-gaming.net") {
html,
body
.navbar,
.row,
.wrapper,
#wrapper,
.page-wrapper,
.gray-bg,
.sidebar-collapse,
.navbar-static-side,
.footer,
.breadcrumb,
.vertical-timeline-content,
.dark-timeline .vertical-timeline-content,
.contact-box,
.modal-content,
.inmodal .modal-body,
.navbar-default .nav > li > a:hover,
.nav > li.active,
.metismenu li:hover,
.table .table {
    background-color: #212121;
    color: white;
}

.ibox,
.ibox-content,
.ibox-content .row,
.ibox-title,
.form-control,
.faq-item,
.faq-answer,
.dropdown-menu,
.contact-box.center-version > a,
.contact-box .contact-box-footer{
    background-color: #242424;
    color: white;
}
.btn-white {
    background-color: #212121;
    color: white;
    border: 1px solid #242424;
}
.dark-timeline .vertical-timeline-content::before,
.dark-timeline .vertical-timeline-block:nth-child(2n) .vertical-timeline-content::before {
    border-color: transparent #212121 transparent transparent;
}
.dropdown-menu,
.dropdown-menu > ul > li:hover,
.dropdown-menu > ul,
.nav > li.active {
    background-color: #242424;
    color: white;
}

.table-striped > tbody > tr:nth-child(2n) > td,
.table-striped > tbody > tr:nth-child(2n) > th {
    background-color: #242424;
}
.table-striped > tbody > tr:nth-child(2n+1) > td,
.table-striped > tbody > tr:nth-child(2n+1) > th {
    background-color: #212121;
}
.table-hover > tbody > tr:nth-child(2n) > td,
.table-hover > tbody > tr:nth-child(2n) > th {
    background-color: #242424;
}
.table-hover > tbody > tr:nth-child(2n+1) > td,
.table-hover > tbody > tr:nth-child(2n+1) > th {
    background-color: #212121;
}
.table-hover > therad > tr:nth-child(2n+1) > td,
.table-hover > thead > tr:nth-child(2n+1) > th {
    background-color: #212121;
}


.footer > div > strong::before {
    content: "Dark Design by iREQ | Control Panel ";
}
}
