css:
@-moz-document url-prefix("http://moonscript.org/") {
h1, .code-split .code-header {
    background: #080808;
}
h2 {
    border-bottom: 2px solid #333;
}
blockquote {
    background: #222;
    border: 1px solid #181818;
}
body, .code-split, .main > pre, .main {
    background: #111;
}
.output_status {
    background: #333;
}
.button {
    background: #181818;
    box-shadow: 0 4px 0 #333;
}
.rainbow_btn_wrap {
    background: #111;
    box-shadow: 0 4px 0 #282828;
}
.rainbow_btn_wrap:active, .button:active {
    background: #080808;
    box-shadow: 0 2px 0 #222;
}
.code_input:focus {
    border: 1px solid #888;
    box-shadow: 0 0 0 1px #888;
}
.main .message {
    border-bottom: 1px solid #222;
}
.code_output {
    color: #aaa;
}
body, blockquote p {
    color: #bbb;
}
.main p > code, .main li > code {
    background: #080808;
    color: #ccc;                            
}
h1, h2, h3, h4, .main h2, .main h3, .main h4, .code_output .l_atom {
    color: #ddd;
}
.current {
    color: #eee;
}
a > code {
    background: #080808;
    border: 2px solid #003064;
    color: #91c6ff;
}
p > code, li > code, a > code, h3 > code, .code_input {
    background: #080808;
    color: #ddd;
    border: 1px solid #555;
}
a:hover > code {
    background: #000;
    border: 2px solid #003064;
    color: #91c6ff;
}
.code-split, .main > pre {
    border: 1px solid #333;
}
}
