css:
@-moz-document url-prefix("https://www.thesaurus.com/browse/") {
html {
    -webkit-filter: invert(100%) hue-rotate(325deg) brightness(150%) contrast(70%) saturate(60%);
    filter: invert(100%) hue-rotate(325deg) brightness(150%) contrast(70%) saturate(60%);
}

nav[aria-label="Site Navigation"],
footer,
#thesaurus_serp_atf_728x90 /* banner ad */,
#thesaurus_serp_atf_300x250 /* top sidebar ad */,
.css-16tby2r, .ek7obj52 /* "see definition" link */,
.css-qvq67r, .ek69tv50 /* "explore dictionary.com" section */,
.css-lowbfq, .e1nz5my50, /* several copyright notices */
.css-183328w.e1l3hbp60, /* synonym of the day */
[data-testid="quiz-module"],
#example-creator,
[data-testid="grammar-coach-ad"],
.css-ye8wy1.e16d6do20, /* feedback tab */
.css-i3pbo.eome24q0, /* example sentences */
.css-1tmuahu.e1p0sp4a0 /* floating menu button of stuff we don't need */ {
    display: none;
}

/* hide, but we want the space */
.css-mxd2oe.e1eym3ob0, /* roget's thesaurus attribution */
.css-fwjwsq.ekc3hqz0 /* breadcrumb nav */ {
    opacity: 0;
}

/* center page layout */
.MainContentContainer, .css-1a48988, .e1h3b0ep0 {
    justify-content: center;
    padding: 0;
}

/* main column with synonyms etc. */
.css-bl7khi, .e1v16r9g0 {
    min-width: 800px;
}

/* sidebar which, at least with adblock, has nothing in it but takes up space */
.css-cfdi2a.evk9ulx0 {
    display: none;
}

/* container for all non-footer content */
.css-hpfk5j, .eavft172 {
    min-height: 100vh;
}
}
