css:
@-moz-document domain("map.projectzomboid.com") {
a {
	color: #59c;
}
a:hover {
	background: #222 !important;
}
select {
	background: #222;
	border: 1px solid #444;
	color: #59c;
}
option:nth-child(odd) {
	background: #111;
}
option:active {
	background: #333 !important;
}
optgroup {
	background: #444;
}
button {
	background: #222 !important;
	border: 1px solid #444 !important;
	color: #59c !important;
}
#footer,
#cookie-bar,
#btnLicense {
	display: none;
}
.sidebar {
	position: static;
	padding: 0;
	width: 200px;
	background: #111;
	border: none;
}
.panel-heading,
.panel {
	background: #333 !important;
	color: #eee !important;
}
.panel-default {
	border-color: #222;
}
.badge {
	background: #555;
}
.navbar-fixed-top {
	min-height: 0;
	margin: 0;
	height: 30px;
}
.navbar-header {
	height: 0;
}
.navbar-collapse {
	background: #333;
}
.navbar-collapse button,
.navbar-collapse select {
	width: 99%;
}
.navbar-brand {
	margin: 4px 10px !important;
	padding: 0;
}
.navbar-right li > * {
	margin: 4px 2px !important;
}
.container-fluid .row {
	position: fixed !important;
	top: 30px !important;
}
.main {
	width: 0;
	margin: 0;
}
}
