css:
@-moz-document domain("www.bigfooty.com")
{
@media (max-width: 480px) {
    .Responsive .messageUserBlock .extraUserInfo {
        display: inline-flex;
        padding-top: 3px;
    }
    dl.pairsColumns,
    dl.pairsJustified,
    .pairsColumns dl,
    .pairsJustified dl {
        display: none;
    }
    .messageUserBlock .extraUserInfo {
        background-color: transparent;
        padding: 0px 0px;
    }
    dl.pairsColumns,
    dl.pairsJustified,
    .pairsColumns dl,
    .pairsJustified dl {
        display: block;
    }
    .pairsJustified dd {
        padding-right: 5px;
    }
    dl.pairsJustified:nth-of-type(3) {
        display: none;
    }
    .pairsJustified dt {
        display: none;
    }
    .messageUserBlock .extraUserInfo dl {
        margin: 0px 0 0;
    }
    div.extraUserInfo > span:first-child {
        display: none;
    }
}

/* full site*/
.PanelScroller .scrollContainer,
.PanelScrollerOff .panel {
    display: none;
}
