css:
@-moz-document domain("teknoseyir.com") {
body {font-family: trebuchet ms;}
.navbar-default {background-color: #1B2836;border-color: #141D26;}
body {background-color: #243447;color: #8798A5;}
.widget, .ts-videos article, .home-videos-right, .liste, .col-left article, #comments, .social_badge, .main-head, .birsey_yaz {border-color: #152130;background-color: #1B2836;}
.home-videos-left h1, .home-videos-right h1, .widget_benzer_videolar h1, .widget_nostalji h1, .navbar-default .navbar-nav > li > a, .col-right .content a, .ts-videos h1 a, .col-left h1 a, .rightpane h1 a, .form_header a {color: #8798A5;}
.navbar-default .navbar-nav > li > a:focus, .navbar-default .navbar-nav > li > a:hover, .col-right .content a:hover, .ts-videos h1 a:hover, .social_badge a:hover, .social_badge .stats:hover, .comment-heading:hover, .post-actions a:hover, .comment-actions a:hover {color: #b0bec5;}
.video-page article {border-color: #1b2836;background-color: #1b2836;}
.username, a {color: #d14937;}
hr{border-top: 1px solid #152130;}
.comment-more {border-bottom: 2px solid #152130;color: #8798A5;}
.comment, #respond {border-bottom: 2px solid #152130;}
.stream-top .author, .takip_oneri .author {color: #bdc2c8;}
.comment-heading {color: #bdc2c8;}
.children li {border-top:none;}
.widget_benzer_videolar article, .widget_nostalji article {border-bottom: 1px solid #243447;}
a.reply-more {border-top: 1px solid #152130;border-bottom: 1px solid #152130;color: #bdc2c8;}
a.reply-more:hover {background-color: #243447;}
.col-right .content, .ts-videos .content, .main-head.videos {background: #1b2836;}
.birsey_yaz .progress, #post_form .progress {background-color: #d14937;}
.navbar-default {box-shadow: none;}
.home-videos .tabs > li > a[title="TeknoSeyir"], .home-videos .tabs > li > a[title="OtoSeyir"] {background-color: #1b2836!important;}
.navbar-brand {background: url("https://teknoseyir.com/wp-content/themes/ts/images/ts_logo_white.svg") no-repeat scroll 0 0 transparent;background-size: auto auto;background-size: 100%;margin-top: 9px;}
.home-videos .tabs > li > a[title="TeknoSeyir"] {background: url("https://teknoseyir.com/wp-content/themes/ts/images/ts_logo_white.svg") no-repeat scroll 0 0 transparent;background-size: auto auto;background-size: 90%;background-position: center;}
.home-videos .tabs > li > a[title="OtoSeyir"] {background: url("https://teknoseyir.com/wp-content/themes/ts/images/otoseyir_logo_white.png") no-repeat scroll 0 0 transparent;background-size: auto auto;background-size: 90%;background-position: center;}
.home-videos .tabs > li.active {border-bottom: 3px solid #d14937;}
.col-right .content, .video-page .content {background: #1b2836;}
.col-right .content a, .video-page h1 a {color: #8798a5;}
.col-right .content, .video-page .content {background: #1b2836;}
.col-right .content a, .video-page h1 a {color: #8798a5;}
.kampanya {border-bottom: 1px solid #243447;}
.woocommerce ul.products li.product, .woocommerce-page ul.products li.product {border: 1px solid #243447;}
.menu-drop {background: #1b2836;background-color: #1b2836;}
.menu-drop a, .menu-drop a:hover {color: #8798a5;}
ul.sub-menu {background-color: #1b2836;}
.dropdown-menu {background-color: #1b2836;}
.form-control {background-color: #1b2836;border: 1px solid #1b1515;}
.modal-content {background-color: #1b2836;}
.modal-header {border-bottom: 1px solid #1b1515;}
.user_stats {border-bottom: 1px solid #1b1515;border-top:1px solid #1b1515;}
.user_info .col-left article {border-bottom: 1px solid #1b1515;}
.modal-footer {border-top:1px solid #1b1515;}
.user_stats a > strong, h3.embed_heading > a {color: #d1cfcf;}
.embed {border: 1px solid #1b1515;}
#comment {background: #243447;border: 1px solid #1b1515;}
.dropdown-menu > li > a  {color: #696868;}
.dropdown-menu .divider {background-color: #1b1515;}
.author-header, .author-nav {border-color: #1b1515;background-color: #1b2836;}
.author-nav > li > a {border-bottom: 1px solid #1b1515;color: #696868}
.author-nav > li.active > a, .author-nav > li.active > a:hover, .author-nav > li.active > a:focus, .nav > li > a:focus, .nav > li > a:hover {background-color: #1b1515;color: #bab3b3;}
#post_content {background: #1b2836;border:1px solid #1b1515}
.post_form::after {border-bottom: 10px solid #1b1515;}
#title {border: 1px solid #1b1515;background: #1b2836;}
div.mce-toolbar-grp {border-bottom: 1px solid #1b1515;background: #1b2836;}
.wp-editor-container {border: 1px solid #1b1515;}
.close{color:#d14937;text-shadow:0 1px 0 #d14937;opacity:.7}
.close:focus,.close:hover{color:transparent;opacity:1}
.dropdown-menu > li > a:focus,.dropdown-menu > li > a:hover{color:#bab3b3;background-color:#1b1515}
.comment p{font-size:14px;padding:3px 0 0 4px}
.profile.wrapper{border-color:#1b1515;background-color:#1b2836}
.nav-tabs > li.active > a,.nav-tabs > li.active > a:focus,.nav-tabs > li.active > a:hover{color:#bab3b3;background-color:#1b1515;border:1px solid #1b1515;}
.nav-tabs.nav-justified > .active > a, .nav-tabs.nav-justified > .active > a:focus, .nav-tabs.nav-justified > .active > a:hover{border:1px solid #1b1515;}
.nav-tabs.nav-justified > li > a{border-bottom:1px solid #1b1515;}
.nav-tabs > li > a:hover {border:1px solid #1b1515;}
.table > thead > tr > th{border-bottom:2px solid #1b1515}
.table > tbody > tr > td, .table > tbody > tr > th, .table > tfoot > tr > td, .table > tfoot > tr > th, .table > thead > tr > td, .table > thead > tr > th{border-bottom:1px solid #1b1515}
ul.sub-menu li a.active {background-color: #1b1515;}
.navbar-default .navbar-nav > .open > a, .navbar-default .navbar-nav > .open > a:focus, .navbar-default .navbar-nav > .open > a:hover {background-color: #1b1515;color:#bab3b3}
.liste li.comment {border-bottom: 1px solid #243447;}
.comment-more:hover, .comment-more:focus {color: #bab3b3;background-color: #1b1515;}
div[style*="border: 1px solid rgb(204, 204, 204)"] {border: 1px solid #1b1515 !important;}
a:focus, a:hover {color: #ff2608;}
.comment p{font-size:14px;padding:5px;background:#243447;margin:4px 0}
.comment-heading{color:#bdc2c8;background:#702321;padding:2px 4px}
.comment-heading span{color:#bdc2c8;}
.comment-actions {text-align: right;margin: 9px 4px 0 0;}
.user-list .author{color:#bab3b3}
.user-list{border-bottom: 1px solid #152130;}
#yeni-icerik {background-color: #1b2836;border-color: #1b1515;}
#yeni-icerik:hover {background-color: #1b1515;}
.comment-body .content.parsed .icerik,  .comment-body .content.parsed{background: #243447;}
.fa.fa-star {color: #eea236;}
.widget_title {border-bottom: 1px solid #1b1515;padding: 0 4px 7px 4px;}
li.bildirim {color: #928989;border-bottom: 1px solid #1b1515;}
.dropdown-menu > li.bildirim a:hover, li.bildirim:hover {color: #bdc2c8;background: #152130;}
li.bildirim a {color: #7998a5;}
.bildirim_tumu a{background: #1b1515;}
.nav-icons > .open > a, .nav-icons > .open > a:hover, .nav-icons > .open > a:focus {color: #bdc2c8;background-color: #1b1515;}
.nav-icons > li > a:hover, .nav-icons > li > a:focus {color: #bdc2c8;}
a.reply-more:hover {color: #bdc2c8;}
.navbar-default .navbar-toggle {border-color: #1b1515;}
.navbar-default .navbar-collapse, .navbar-default .navbar-form {border-color: #1b1515;}
li.no_read {background: #243447;}
.fa.fa-youtube-play {color: #d14937;}
.fa.fa-thumbs-up-o {color: #80c14b;}
.fa.fa-comments {color: #5578f3;}
.woocommerce #respond input#submit,.woocommerce a.button,.woocommerce button.button,.woocommerce input.button{color:#fff!important;background-color:#114859!important;border-color:#1f5363!important;font-weight:400!important}
.woocommerce #respond input#submit:hover,.woocommerce a.button:hover,.woocommerce button.button:hover,.woocommerce input.button:hover{background-color:#073c4b!important;border-color:#053341!important}
}
