css:
@-moz-document domain("hardverapro.hu") {
/* OLDAL SZÉLESSÉG */ 

.container {max-width: 1300px !important;}
#center {width: 70% !important;}
#right {width: 30% !important;}

/* KIKAPCSOLT BOXOK */
#nwcm {display: none !important;} /* Percről percre */
.uad-commend-list {display: none !important;} /* Ajánló */
.search-bar-search h3 {display: none !important;} /* Keress Magyarország blablabla .... */

/* HIRDETÉS LISTA */
.uad-list .uad-image {display: none !important;}
.uad-corner-ribbon {display: none !important;}
.uad-list .featured {border-left: 4px solid orange !important; }
.uad-list h1,.uad-price, .uad-light,.uad-ultralight {font-size: 0.8rem !important; line-height: 1rem !important;}
.uad-list .media a:hover {text-decoration: none !important; color: #e54c09 !important;}
.pager-navbar {background: #676767 !important;border-radius: calc(.25rem - 1px) calc(.25rem - 1px) 0 0 !important;}
.pager-navbar .navbar-nav .btn {font-size: 0.7rem !important; }
.uad-list h3 {background: #676767 !important; color: #fff !important; padding: 0.5rem !important;}
.uad-list h3 a {color: #fff !important;}
.uad-list li.media:hover {background: #ececec !important;}

/* HIRDETÉS OLDAL*/
.uad .uad-content {font-size: 0.8rem !important;}
.uad h1 {font-size: 1rem !important; color: #fff !important; }
.uad-content-block {background: #676767 !important; color: #fff !important;}
.uad-content-block h1, .uad-content-block h4 {font-size: 0.8rem !important;}
.uad-time-location {background: #fff !important;color: #000 !important;}
#uad-images-carousel {padding: 0.5rem !important;}
.msg-list .card .card-header .msg-head-user>span>a, .msg-list .card .card-header .list-inline-item>a {color: #fff !important;}

/* HEADER */
#header-sticky {background: #000 !important;}
#header-sticky .nav-item {font-size: 0.7rem !important;}
.network .ph:before, .network .ma:before, .network .it:before, .network .lo:before, .network .ha:before, .network .gp:before, .network .ft:before {border: none !important;}
#header-sticky .nav-link {color: #989898 !important;}
#header-sticky .nav-link:hover {color: #e54c09 !important;text-decoration: none !important;}

/* ITT SZÓLTAM HOZZÁ - KEDVENCEK LISTA */
.card-forum .card-header {background: #676767 !important; color: #fff !important;}
.card-header {font-size: 0.8rem !important;}
.user-thread-list li a {font-size: 0.7rem !important; }
.user-thread-list li a:hover {text-decoration: none !important; color: #e54c09 !important;}

/* Egyebek */

.card-header {background: #676767 !important; color: #fff !important;}
.card {background: #f1efe4 !important;}
.uad-form h2 {font-size: 1rem !important;}
.breadcrumb {background:#fff !important;}
.search-bar .search-bar-cats h1 {font-size: 2rem !important;}
}

@-moz-document url("https://hardverapro.hu/hirdetesfeladas/listaz.php"), url("https://hardverapro.hu/jelentesek/index.html"), url("https://hardverapro.hu/hirdetesfeladas/uj.php"), url-prefix("https://hardverapro.hu/kosar") {
#center {width: 100% !important;}
}

@-moz-document url("https://hardverapro.hu/fiok/beallitasok.php"), url-prefix("https://hardverapro.hu/tag"), url-prefix("https://hardverapro.hu/ertekelesek") {
#left {display: none !important;}
#right {display: none !important;}
#center {width: 100% !important;}
}

@-moz-document url-prefix("https://hardverapro.hu/privat") {
#left {display: none !important; }
}
