css:
@-moz-document domain("youtube.com") {
/* General Parts */
.ytp-chrome-bottom {
	bottom: -23px !important;
	padding-left: 25px !important;
	margin: 2px -37px !important;
}

.ytp-chrome-top,
.ytp-chrome-bottom {
    text-shadow: 0 0 0px rgba(0, 0, 0, .5) !important;
}

.ytp-chrome-controls {
    background: /*[[black-white]]*/
 !important;
    height: 27px !important;
}

.ytp-right-controls {
    height: 100% !important;
    background: /*[[black-white]]*/
 !important;
    margin: 0 -24px !important;
}

.ytp-time-display {
    font-size: 10.4px !important;
    font-family: Roboto, Helvicta, Arial, sans-serif !important;
    padding: 0 0px !important;
    width: 57px !important;
    margin-left: 8px !important;
    line-height: 27px !important;
}

.ytp-time-current {
    color: /*[[Blk-Whe]]*/!important;
}

.ytp-time-separator,
.ytp-time-duration {
    color: /*[[Blk-Whe]]*/ !important;
}
.ytp-gradient-bottom {
    height: 0px !important;
    padding-top: 0px !important;
}
.ytp-settings-button.ytp-hd-quality-badge::after,
.ytp-settings-button.ytp-4k-quality-badge::after,
.ytp-settings-button.ytp-5k-quality-badge::after,
.ytp-settings-button.ytp-8k-quality-badge::after {
    top: 2px !important;
    right: 2px !important;
    background-color: #b50324 !important;
}
.ytp-big-mode .ytp-settings-button.ytp-hd-quality-badge::after {
    content: '___' !important;
    background: url(https://s.ytimg.com/yts/imgbin/player-common-vflbJjZA5.png) !important;
    background-position: 26px 0px !important;
    margin: -12px -16px !important;
    color: #fff0 !important;
    height: 7px !important;
}
.ytp-settings-button.ytp-hd-quality-badge::after {
    background: url(https://s.ytimg.com/yts/imgbin/player-common-vflbJjZA5.png) !important;
    background-position: 26px 0px !important;
}
.ytp-bezel {
    border-radius: 5px !important;
}

/* Scrubber */
.ytp-scrubber-button.ytp-swatch-background-color {
	background-color: #aeaeae !important;
	border: solid 5px #eaeaea !important;
}
.ytp-scrubber-button.ytp-swatch-background-color:hover {
	background-color: #b91f1f !important;
	border: solid 5px #eaeaea !important;
}
.ytp-scrubber-button {
	width: 3px !important;
	padding: 1.5px !important;
	margin: 0px 0px 0px 0 !important;
	border-radius: 36.5px !important;
	height: 3px !important;
}
/* Progress-Bar */
.ytp-progress-bar-container:hover:not([aria-disabled=true]) {
    cursor: pointer !important;
    height: 8px !important;
}

.ytp-swatch-background-color {
    background-color: #b91f1f !important;
}
.ytp-progress-list {
    background: rgb(104, 104, 104) !important;
}
.ytp-progress-list {
    transform: scaleY(0.6) scaleX(1.037) translateX(12.0px) !important;
}
.ytp-progress-bar-container:hover:not([aria-disabled="true"]) .ytp-progress-list {
    transform: scaleX(1.037) translateX(12.0px) !important;
}

/* Play */
.ytp-chrome-controls .ytp-play-button {
    width: 0px !important;
    background: url(/*[[W-B]]*/) !important;
    padding: 0px 27.5px !important;
    background-position: 0 -545px !important;
}

.ytp-chrome-controls .ytp-play-button:hover {
    width: 0px !important;
    background: no-repeat url(/*[[W-B]]*/) -93px -361px !important;
    padding: 0px 27.5px !important;
}


/* Pause */
button.ytp-play-button.ytp-button[title="Pause (k)"] {
    background: no-repeat url(/*[[W-B]]*/) !important;
    background-position: 0 -638px !important;
}

button.ytp-play-button.ytp-button[aria-label="Pause (k)"]:hover {
    background: no-repeat url(/*[[W-B]]*/) !important;
    background-position: -34px -1077px !important;
}


/* Vol */
button.ytp-mute-button.ytp-button {
    background: no-repeat url(/*[[W-B]]*/) !important;
    background-position: -93px -124px !important;
    width: 0px !important;
    margin-left: 2px !important;
    padding: 0 33px 0 2px !important;
}

button.ytp-mute-button.ytp-button:hover {
    background: no-repeat url(/*[[W-B]]*/) !important;
    background-position: -93px -279px !important;
    width: 0px !important;
    margin-left: 2px !important;
    padding: 0 33px 0 2px !important;
}


/* Mute */
button.ytp-mute-button.ytp-button[title="Unmute (m)"] {
    background: no-repeat url(/*[[W-B]]*/) !important;
    background-position: 0px -922px !important;
}

button.ytp-mute-button.ytp-button[title="Unmute (m)"]:hover {
    background: no-repeat url(/*[[W-B]]*/) !important;
    background-position: -127px -809px !important;
}


/* Next */
a.ytp-next-button.ytp-button {
    width: 0px !important;
    background: no-repeat url(/*[[W-B]]*/) 0 -1139px !important;
    padding: 0 15px !important;
}

a.ytp-next-button.ytp-button:hover {
    width: 0px !important;
    background: no-repeat url(/*[[W-B]]*/) 0px -984px !important;
    padding: 0 15px !important;
}


/* Previous */
a.ytp-prev-button.ytp-button {
    background: no-repeat url(/*[[W-B]]*/) -93px -654px !important;
    padding: 0 15px !important;
    width: 0px !important;
}

a.ytp-prev-button.ytp-button:hover {
    background: no-repeat url(/*[[W-B]]*/) 0px 0px !important;
    padding: 0 15px !important;
    width: 0px !important;
}


/* Fullscreen */
button.ytp-fullscreen-button.ytp-button {
    width: 0px !important;
    background: no-repeat url(/*[[W-B]]*/) -93px -423px !important;
    padding: 0px 15px !important;
}

button.ytp-fullscreen-button.ytp-button:hover {
    width: 0px !important;
    background: no-repeat url(/*[[W-B]]*/) -68px 0px !important;
    padding: 0px 15px !important;
}


/* Theater Mode */
button.ytp-size-button.ytp-button {
    width: 0px !important;
    background: no-repeat url(/*[[W-B]]*/) !important;
    background-size: auto !important;
    padding: 0 15px !important;
    background-position: 0 -824px !important;
}

button.ytp-size-button.ytp-button:hover {
    width: 0px !important;
    background: no-repeat url(/*[[W-B]]*/) !important;
    background-size: auto !important;
    padding: 0 15px !important;
    background-position: -34px 0 !important;
}


/* Default Mode */
button.ytp-size-button.ytp-button[title="Default view (t)"] {
    width: 0px !important;
    padding: 0px 15px !important;
    background: no-repeat url(/*[[W-B]]*/) -39px -576px !important;
    background-size: auto !important;
}

button.ytp-size-button.ytp-button[title="Default view (t)"]:focus:hover {
    width: 0px !important;
    padding: 0px 15px !important;
    background: no-repeat url(/*[[W-B]]*/) -127px -392px !important;
    background-size: auto !important;
}


/* Settings */
button.ytp-button.ytp-settings-button {
    width: 0px !important;
    background: no-repeat url(/*[[W-B]]*/) !important;
    padding: 0px 15px !important;
    background-size: auto !important;
    background-position: -55px -1015px !important;
}

button.ytp-button.ytp-settings-button:hover {
    width: 0px !important;
    background: no-repeat url(/*[[W-B]]*/) !important;
    padding: 0px 15px !important;
    background-size: auto !important;
    background-position: -127px -423px !important;
}

button.ytp-button.ytp-settings-button:active {
    width: 0px !important;
    background: no-repeat url(/*[[W-B]]*/) !important;
    padding: 0px 15px !important;
    background-size: auto !important;
    background-position: 0px -247px !important;
}

button.ytp-button.ytp-settings-button[aria-owns="ytp-id-17"]:hover {
    width: 0px !important;
    background: no-repeat url(/*[[W-B]]*/) !important;
    padding: 0px 15px !important;
    margin-left: 0px !important;
    background-size: auto !important;
    background-position: -127px -423px !important;
}

/* Captions */
button.ytp-subtitles-button.ytp-button {
    background: url(/*[[W-B]]*/) !important;
    width: 0px !important;
    padding: 0px 15px !important;
    background-size: auto !important;
    background-position: 0px 137px !important;
}

button.ytp-subtitles-button.ytp-button:hover {
    background: url(/*[[W-B]]*/) !important;
    width: 0px !important;
    padding: 0px 15px !important;
    background-size: auto !important;
    background-position: -93px 405px !important;
}

.ytp-chrome-controls .ytp-button[aria-pressed=true]:after {
    width: 0px !important;
    left: 9px !important;
    -moz-transition: left .25s cubic-bezier(0.0, 0.0, 0.2, 1), width .25s cubic-bezier(0.0, 0.0, 0.2, 1) !important;
    -webkit-transition: left .25s cubic-bezier(0.0, 0.0, 0.2, 1), width .25s cubic-bezier(0.0, 0.0, 0.2, 1) !important;
    transition: left .25s cubic-bezier(0.0, 0.0, 0.2, 1), width .25s cubic-bezier(0.0, 0.0, 0.2, 1) !important;
}

button.ytp-subtitles-button.ytp-button[aria-pressed="true"] {
    background: url(/*[[W-B]]*/) !important;
    width: 0px !important;
    padding: 0px 15px !important;
    background-size: auto !important;
    background-position: -102px 0px !important;
}

button.ytp-subtitles-button.ytp-button[aria-pressed="true"]:hover {
    background: url(/*[[W-B]]*/) !important;
    width: 0px !important;
    padding: 0px 15px !important;
    background-size: auto !important;
    background-position: -59px 443px !important;
}

/* MiniPlayer Button */
button.ytp-miniplayer-button.ytp-button {
    width: 0px !important;
    padding: 0px 15px !important;
    background: no-repeat url(/*[[W-B]]*/) -39px -576px !important;
    background-size: auto !important;
}

button.ytp-miniplayer-button.ytp-button:hover {
    width: 0px !important;
    padding: 0px 15px !important;
    background: no-repeat url(/*[[W-B]]*/) -127px -392px !important;
    background-size: auto !important;
}


/* Volume Slider */
.ytp-volume-slider-handle:before {
    left: -64px !important;
    background: #b91f1f !important;
}

.ytp-volume-slider-handle:after {
    left: 5px !important;
    background: rgba(255, 255, 255, .2) !important;
}

.ytp-volume-slider-handle {
    position: absolute !important;
    top: 50% !important;
    width: 5px !important;
    height: 14px !important;
    border-radius: 0px !important;
    margin-top: -5.9px !important;
    background: #ddd !important;
}

.ytp-volume-slider-active .ytp-volume-panel {
    width: 54px !important;
    margin-right: 3px !important;
}

.ytp-volume-slider-handle {
    margin-left: 9px !important;
}
.ytp-volume-slider {
	min-height: 25px;
}


/* Settings Box */
.ytp-settings-menu {
    right: 12px !important;
    background: rgba(31, 31, 31, .9) !important;
    bottom: 36px !important;
    z-index: 71 !important;
    will-change: width, height !important;
}

.ytp-menuitem[aria-haspopup="true"] .ytp-menuitem-content {
	padding-right: 10px !important;
	background: #e6e6e6 !important;
	color: #555 !important;
	height: 20px;
}

.ytp-menuitem {
	height: 53px !important;
	display: grid;
	margin: 0 0 0 70px;
	width: 100px;
}
.ytp-menuitem[aria-haspopup="true"]:not([aria-disabled=true]):hover {
    background-color: rgba(255, 255, 255, 0) !important;
}

.ytp-menuitem[role="menuitemcheckbox"]:not([aria-disabled=true]):hover {
    background-color: rgba(255, 255, 255, 0) !important;
}

.ytp-menuitem-toggle-checkbox {
    border-radius: 0px !important;
}

.ytp-menuitem-toggle-checkbox:after {
    height: 13.91px !important;
    width: 16.91px !important;
    border-radius: 0px !important;
    margin-top: 0.4px !important;
    background-color: #bdbdbd !important;
    box-shadow: 0 0px 0px 0 rgba(0, 0, 0, 0.6) !important;
    transition: all 0s cubic-bezier(0.4, 0.0, 1, 1) !important;
}

.ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox:after {
    background-color: #636263 !important;
    margin: 0px 3px !important;
    -moz-transform: translateX(16px) !important;
    -ms-transform: translateX(16px) !important;
    -webkit-transform: translateX(16px) !important;
    transform: translateX(16px) !important;
}

.ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox {
    background: #bdbdbd !important;
}

.ytp-popup {
    text-shadow: 0 0 2px rgba(255, 255, 255, 0) !important;
}

.ytp-menu-label-secondary {
    font-size: 91% !important;
    color: #555 !important;
}

.ytp-panel[style="width: 296px; height: 183px;"] {
    position: absolute !important;
    bottom: -5px !important;
    right: 0px !important;
    width: 257px !important;
    height: 110px !important;
    overflow-y: auto !important;
    overflow-x: hidden !important;
}

.ytp-menuitem-icon svg {
	display: block;
	width: 0;
	height: 0;
}
.ytp-panel-menu {
	padding: 15px 0;
	width: 80%;
}

.ytp-menuitem {
	height: 53px !important;
	display: grid;
	margin: 0 0 0 130px;
	width: 100px;
}

.ytp-settings-menu {
	bottom: 30px !important;
	width: 150px !important;
}

/* I think what I did for the previous two are good enough.. off to Fullscreen Mode */
.ytp-big-mode .ytp-chrome-top .ytp-button,
.ytp-big-mode .ytp-chrome-controls .ytp-button {
    width: 0px !important;
}

.ytp-big-mode .ytp-time-display {
    line-height: 27px !important;
}

.ytp-big-mode .ytp-chrome-controls {
    height: 27px !important;
    line-height: 27px !important;
}
.ytp-big-mode .ytp-chrome-bottom {
	height: 54px !important;
	padding-top: 4px !important;
	margin-bottom: -3px !important;
}
.ytp-big-mode .ytp-progress-list {
    transform: scaleY(0.625) scaleX(1.018) translateX(12px) !important;
}
.ytp-big-mode .ytp-progress-bar-container:hover:not([aria-disabled="true"]) .ytp-progress-list {
	transform: translateY(-2.5px) scaleX(1.018) translateX(12px) !important;
}
.ytp-big-mode .ytp-scrubber-button.ytp-swatch-background-color {
	width: 3px !important;
	height: 3px !important;
	background-color: #aeaeae !important;
	border: solid 5px #eaeaea !important;
}
.ytp-big-mode .ytp-scrubber-button.ytp-swatch-background-color:hover {
	width: 3px !important;
	height: 3px !important;
	background-color: #b91f1f !important;
	border: solid 5px #eaeaea !important;
}
.ytp-big-mode .ytp-chrome-controls .ytp-play-button {
	padding: 27px !important;
}

.ytp-big-mode {
	font-size: 11px;
}

.ytp-big-mode .ytp-menuitem-icon svg {
	width: 0px;
	height: 0px;
}

.ytp-big-mode .ytp-volume-slider {
	min-height: 24px;
}

/* Play on TV Button */
button.ytp-button[title="Play on TV"] {
    width: 0px !important;
    background: no-repeat url(/*[[W-B]]*/) -59px -802px !important;
    padding-left: 30px !important;
}

button.ytp-button[style="display: initial;"]:hover {
    width: 0px !important;
    background: no-repeat url(/*[[W-B]]*/) -132px -1062px !important;
    padding-left: 30px !important;
}
/* Watch on YouTube Button */
.ytp-chrome-controls .ytp-button.ytp-youtube-button {
    width: 0px !important;
    padding-right: 52px !important;
    background: no-repeat url(/*[[W-B]]*/) -93px -217px !important;
}
.ytp-chrome-controls .ytp-button.ytp-youtube-button:hover {
    width: 0px !important;
    padding-right: 52px !important;
    background: no-repeat url(/*[[W-B]]*/) -12px -953px !important;
}

/* Spinner */
.ytp-spinner-rotator {
    width: 31px !important;
    height: 31px !important;
    background: url(data:image/png;base64,) !important;
}
.ytp-spinner-container {
    width: 31px !important;
    padding-bottom: 31px !important;
    margin-top: -15px !important;
    margin-left: -15px !important;
    transform: rotate(617688888888grad) !important;
}
.ytp-spinner-left {
    right: 49% !important;
    opacity: 0 !important;
}
.ytp-spinner-right {
    left: 49% !important;
    opacity: 0 !important;
}

/* final fixes */

.ytp-chrome-controls .ytp-button.ytp-mute-button svg, .ytp-right-controls .ytp-button svg {
	width: 0px;
	height: 0px;
}
body:not([v3notplaylist]) button.ytp-mute-button.ytp-button {
	padding-right: 34px !important;
}
}
