css:
@-moz-document domain("www.sololearn.com") {
body {
    background-color: #343a40;
}

#header,
.footerTitle {
    background-color: #24292e;
}

.logo a {
    margin-left: 0;
}

/*Containers*/
.block,
.question.elevated,
#replyBox,
.popupOverlay .popup {
    background-color: #2d3238;
    border: 1px solid #1d2125;
    border-radius: .25rem;
    box-shadow: none;
}

.userProfile .user .details {
    margin-top: 0;
}

.socialOutline {
    display: flex;
}

.fb_iframe_widget {
    width: auto!important;
}

.chart canvas {
    opacity: 0;
}

.userAchievements.full {
    display: flex;
    flex-flow: row wrap;
}

.userAchievements .achievement .details {
    display: flex;
    flex-direction: column;
    padding: 0;
}

.userAchievements.full .achievement {
    align-items: center;
    display: flex;
    flex-basis: 33.333333%;
    min-width: 33.333333%;
}

.userAchievements .achievement .icon {
    align-items: center;
    display: flex;
    height: 64px;
    justify-content: center;
    width: 64px;
}

.userAchievements.full .achievement img {
    height: 48px;
}

.coursesContent {
	display: flex;
    flex-flow: row wrap;
    justify-content: space-around;
}

.courseItem {
    margin: 0;
    margin-bottom: 1.75rem;
    float: none;
}

.question:not(.post) {
    display: flex;
}

.question:not(.post) .detailsWrapper {
    flex-basis: 85%;
    padding-right: 1rem;
}

.question:not(.post) .postStats {
    align-items: center;
    display: flex;
    flex-basis: 15%;
    justify-content: center;
    margin: 0;
    order: 2;
}

#replyBox {
    background-color: #24292e;
}

.leaderboardCourses .course img {
    transition: opacity 100ms ease-in-out;
}

.leaderboardCourses .course img:hover {
    opacity: .65;
}

#leaderboard > .course {
    align-items: center;
    display: flex;
}

#leaderboard > .course .details {
    display: flex;
}

#leaderboard > .course h1 {
    margin-left: .5rem;
}

#leaderboard .content {
    align-items: center;
    display: flex;
}

.rankInfo {
    display: flex;
    flex: 1;
    justify-content: space-between;
}

#leaderboard .name {
    padding: 0;
}

#leaderboard .points {
    margin: 0;
}

/*Sidebar*/
.sidebar .block {
    margin-bottom: 1rem;
}

.sidebar .socialOutline {
    display: flex;
    justify-content: center;
    margin-top: 0;
    padding-left: 0;
}

.socialOutline > div {
    margin-right: .25rem;
    width: auto!important;
}

/*Titles, subtitles*/
h1, h2, h1 a, h1 a:hover,
.userProfile .name,
.codeName a.nameLink,
.switchLabel,
.userCourses .courseXp,
.certificates .title,
.userAchievements .achievement .title,
.footerMenu ul li a,
.loginTitle,
.materialCheckbox.checkbox label,
.courseItem a:hover .courseDescription div,
.courseItem a:hover .courseDescription h1,
.courseDescription div,
.courseDescription h1,
.lessonsCount p,
.courseLessonTitle,
.courseModule p,
.discussionSidebarBlock .discussLink,
#thread h1,
.postDetails .title a,
.postVotes label,
.postAnsewers label,
.replyControls .answersCount,
#repliesOrdering label,
#replyBox .name,
#newPostMessage .name,
.pagingBody .delimiter,
#leaderboard h2,
#leaderboard .leaderboardList a,
.recentArticles p, .archives p,
#publicCodesContainer h1,
.popup p,
#mailingForm label {
    color: #fff;
}

.courseModule,
.discussionSidebarBlock .discussLink,
.discussionSidebarBlock h2,
.recentArticles p,
.archives p,
.articleContent h2,
.codesSidebarBlock h2 {
    align-items: center;
    display: flex;
}

.courseModule::after,
.discussionSidebarBlock .discussLink::after,
.discussionSidebarBlock h2::after,
.recentArticles p::after,
.archives p::after,
.articleContent h2::after,
.codesSidebarBlock h2::after {
    background-color: #fff;
    content: '';
    height: 2px;
    flex: 1;
    margin-left: .5rem;
}

.courseModule {
    background-color: transparent;
    margin: 2rem 0;
    padding: 0;
}

/*Text, descriptions*/
.userProfile .details,
.authorDetails .codeDate,
#userCodes .codeDate,
.certificates .date,
.certificates .download,
.description,
#footer .about p,
.courseDescription p,
.lessonNumber,
.discussionSidebarBlock .list a .text,
.authorDetails .date,
.postDetails .message,
.textareaFeed,
#discussionPost .postVotes p,
.pagingBody a,
#leaderboard .rank,
.articleContent p,
.languageSelector label {
    color: #d7d7d7;
}

.courseCounts ul li p,
.postVotes p,
.postAnsewers p,
.postDetails .title a:hover {
    color: #1bc7a5;
}

/*Rules, borders*/
hr {
    background-color: #1d2125;
}

.question.post,
#replies .answer,
#leaderboard .item {
    border-color: #1d2125;
}

.code,
.courseCounts,
.courseLesson,
.tabs,
.question {
    border-color: #252a2f!important;
}

.lessonNumber,
#replies .answer:last-of-type {
    border: none;
}

/*Buttons*/
.materialButton {
    color: #fff;
}

.materialButton.elevated {
    background-color: #f95752;
    border-radius: .125rem;
    color: #fff;
}

.materialButton.elevated:hover {
    background-color: #ff7874;
}

.materialButton.primary.elevated {
    background-color: #1bc7a5;
}

.materialButton.primary.elevated:hover {
    background-color: #17deb6;
}

.certificates .share .fb,
.certificates .share .linkedin {
    border-radius: .15rem;
}

.navigation-menu ul li {
    border-radius: .125rem!important;
    transition: background-color 100ms ease-in-out;
}

.signin,
.navigation-menu ul li:hover {
    background-color: #1bc7a5!important;
}

.courseStores a {
    background-color: #1bc7a5;
    border-radius: .125rem;
    transition-property: background-color, border-radius;
    transition: 100ms ease-in-out;
}

.courseStores a:hover {
    background-color: #17deb6;
    border-radius: .5rem;
}

.lessonChecks {
    background-color: #3f454a;
    border-radius: .25rem;
    color: #d7d7d7;
    transition: background-color 100ms ease-in-out;
}

.lessonChecks:hover {
    background-color: #343a40;
}

.tabs {
    display: flex!important;
}

.tabs li a {
    border: 1px solid #252a2f;
    border-radius: 0!important;
    border-right: none!important;
    color: #d7d7d7!important;
    margin: 0!important;
    transition: background-color 100ms ease-in-out;
}

.tabs li:first-of-type a {
    border-radius: .25rem 0 0 0!important;
}

.tabs li:last-of-type a {
    border-radius: 0 .25rem 0 0!important;
    border-right: 1px solid #252a2f!important;
}

.tabs li a:hover {
    background-color: #343a40!important;
    border-bottom-color: #252a2f!important;
}

.tabs li.active a {
    background-color: #343a40!important;
    border-color: #252a2f!important;
    border-right: none!important;
    cursor: pointer!important;
}

.tabs li:only-child {
    border-radius: .25rem .25rem 0 0!important;
    border-right: 1px solid #252a2f!important;
}

.pagingBody a {
    background-color: #3f454a;
    border-color: #3f454a;
}

/*Form*/
:focus {
    outline: none;
}

.loginOutline {
    align-items: center;
    display: flex;
    flex-flow: row wrap;
}

.loginOutline .loginTitle {
    width: 100%;
}

#loginForm {
    margin-right: 1rem;
}

#socialLoginList > p {
    display: flex;
    flex-wrap: wrap;
    width: 220px;
}

#socialLoginList button {
    border-radius: .125rem;
}

.formActions {
    display: flex;
    padding: 0;
}

.formActions .materialButton.elevated {
    margin-right: .5rem;
}

.materialInput input {
    border-bottom: 1px solid #252a2f;
}

.underline,
.materialInput .focusedUnderline,
.cursor {
    display: none!important;
}

.codeName .codeForm .nameInput,
textarea,
#mailingForm input[type="text"],
#mailingForm textarea {
 	background-color: #3f454a;
    border: none;
    color: #fff;
    padding: .275rem .4rem;
}

.searchQuestion #tagsInput,
.searchCode #searchInput {
	background-color: #3f454a;
    border-color: #3f454a;
    border-radius: .25rem 0 0 .25rem;
    color: #fff;   
}

.searchQuestion .submitSearch,
.searchCode .submitSearch {
    border-radius: 0 .25rem .25rem 0;
}

.searchQuestion .removeTags {
    transition-property: right, visibility;
    transition: 100ms ease-in-out;
}

.formButton.negative {
    color: #f95752;
}

.formButton.negative:hover {
    background-color: rgba(249, 87, 82, .2);
}

.controls {display: flex}
.controls .formButton.primary {order: 1}
.controls .formButton.negative {order: 2}
.controls .formButton {order: 3}

.formButton,
#loginForm .forgotPasswordLink,
.materialInput input,
#uploadAvatar .primary .setAvatar,
#uploadAvatar .primary .removeAvatar {
    color: #fff;
}

/*Footer*/
#footer {
    background-color: #2d3238;
}

.footerSocial {
    background-color: #24292e;
}

.footerSocial span {
    color: #fff;
    transition: color 100ms ease-in-out;
}

.footerSocial span:hover {
    color: #d7d7d7;
}

#footer .copyright {
    color: #809dbf;
}

/*Hiding Crap*/
.courseLoactions.block {
    display: none!important;
}
}
