css:
@-moz-document domain("hattrick.org") {

body {

 margin:0 0 !important;

 width:auto !important;

 text-align:left !important;

 color: #f47c1c !important;

 background-image: none !important;

 background-color: #000!important;

}

#footer {

  display: none !important;

}

div[class*="ad_"]{display:none!important}

/*a[id*="logo"]{display:none!important}*/

/*Users online - week - date - time*/

div[id="online"] {
   color: #3f7137!important; 
   float:left!important;
  /*margin-left:-110pt!important;*/
  /* margin-top:-70pt!important;*/
}

div[id="time"]{
   color: #3f7137 !important;     
   float:left!important;
   /*margin-left:-10pt!important;*/
   /*margin-top:-70pt!important;*/

}

/*News and team events date*/
.date{
	color: #d52000!important;	
}





#page {
  background-color:#000!important;
  border: 1px solid #333333 !important;
}

#hattrickNoSupporter {
  width:1024px !important ;
}

#teamLinks{
  color: #f47c1c!important;
}



h2.info{

  color:#f47c1c!important;

}

h3 {

  color:#d52000 !important;

}

.subMenuBox ul li {

  color:#f47c1c!important;

}

#sidebar ul li {

  color:#f47c1c !important;

}

.draw {

  color:#f47c1c !important;

}

#ticker div a {

  color:#f47c1c!important;

}
#ticker strong {
   text-decoration:blink !important; 
}

.subMenuBox a, .subMenuBox a, .boxLeft a,.sidebarBox a.active {

  color:#f47c1c!important;

}


.viewActive {

  color:#f47c1c !important;

}

#menu a{

 /* background-color:#282828 !important;*/
  color:#f47c1c!important;

}

.main .boxHead h2, .sidebarBox h2, .subMenuBox h2, .boxLeft h2 {

  background-color:#282828!important;
  color:#f47c1c!important;
}

#menu a:hover {

  background-color:#333333 !important;

}

h1 {

  color: #f47c1c !important;

}

.main .mainBox h2 {

background-color:#282828!important;

border-bottom:2px solid #333333 !important;

}

.main table th {
border-bottom:2px solid #333333 !important;
background-image: none !important;
background-color: #282828 !important;

color: #f47c1c !important;

}

h2 {

  /*background-color:#282828 !important;*/

  background-color:#000000 !important;

  border-bottom:2px solid #282828 !important;

  color:#f47c1c !important ;

}

/* MAINSITE */

#menu {

  background-image: none !important;

  background-color: #353535!important;

}

.sidebarBox .boxBody, .subMenuBox .boxBody, .main .boxBody {

background:#000000 none repeat scroll 0 0 !important;

}

#header {

background-image: none !important;
background-color: #000000!important;

}

#top {

background-image: none !important;

background-color: #000000 !important;

}

#content {

  background-image: none !important;

  background-color: #000000!important;

}

.sidebarBox .boxFooter .boxLeft, .subMenuBox .boxFooter .boxLeft, .main .boxFooter .boxLeft {

  background-image: none !important;
  color: #f47c1c !important;
  background-color: #000000 !important;

}  

.folderHeader {
   background-color: #000000 !important;
}

.folderHeader a{
   color: #d52000!important;
}

#bottom {
  background-image: none !important;
  background-color: #000000 !important;

}

#page {
padding: 0 0 !important;

}

/* LINKS 
a {
  color: #f47c1c;
}*/

table tr.even {
background-image: none !important;
border-top: 1px #333333 solid !important;
background-color: #000000 !important;
}

table tr.odd {
background-image: none !important;
border-top: 1px #333333 solid !important;
background-color: #000000 !important;
}

table tr.topBorder td {
background-color: #000000 !important;
border-top: 2px #333333 solid !important;
background-image: none !important;
}

.alert, .error, .ok {

background-color:#282828!important;

border:1px solid #333333 !important;

}

#shortcuts {

top: 0 !important;

}

/* HATRICK */

.main table, #header table, #sidebar table, .subMenuBox table {

background-color: #000000 !important;

}

/* LINE UP */

.name {
  color: #d52000!important;
}

.ok {
  background-color: #282828 !important;
  border: 1px solid #333333 !important;

}

.user, .national{
  background-color: #282828!important;
}

/* MAIN PAGE BORDERS */

.boxBody, .main .boxBody {

  border-bottom: 0px solid #333333 !important;

  border-left: 0px solid #333333 !important;

  border-right: 0px solid #333333 !important; 

} 
}
