css:
@-moz-document domain("nurture.ai") {

body {
  color: #999;
}

.white-background {
  background-color: #222 !important;
}

#app {
  background-color: #222;
}

/**** Top-most Bar ****/

.top-menu {
  background-color: #222;
}

.form-control {
  background-color: #282828;
  border-color: #333;
}

/*** Left-side Column with 'Home', 'Featured Papers', 'Top Recent Papers',... ***/
.dashboard-tabs__tab {
  color: #999;
}

.dashboard-sidebar > hr {
  border-top-color: #444;
}

.dashboard-tabs__tab--selected {
  background-color: #333;
}

/* 'Get Started' button on bottom-left */
.btn-secondary {
  background-color: #333;
  color: #aaa;
}

/*** 'My Profile' on top-right corner ***/
.top-menu .rightMenus li a {
  color: #999;
}

.nav > li > a:focus, .nav > li > a:hover {
  background-color: #282828;
}

/** Profile Dropdown **/

.nav .open > a, .nav .open > a:focus, .nav .open > a:hover {
  background-color: #282828;
}

.dropdown-menu {
  background-color: #282828;
}

.notifications-header, .notification-item {
  border-bottom-color: #333;
}

.notification-item:hover {
  background-color: #333;
}


/*** Right-side Column list ***/

.landing .papers-showcase .papers-list .list-header {
  border-bottom-color: #666;
}

.landing .papers-showcase .papers-list .list-item {
  border-bottom-color: #444;
}

.dashboard-sidebar hr {
  border-top-color: #444;
}

/* Twitter, Linkedin, Facebook icons */
.fa.fa-facebook-square, .fa.fa-linkedin-square, .fa.fa-twitter-square {
  color: #444;
}


/*** Footer ***/
.dark-footer {
  background-color: #222;
}

/**** Sign in Pop-up pane ****/

.modal-content {
  background-color: #222;
}

.word-wrapper__word {
  background-color: #222;
}

.auth-wrapper__line {
  background-color: #333;
}

/* 'Click here to sign up' */
.btn-default {
  background-color: #222;
}

/* Close button */
.close {
  color: #aaa;
}

/**** Sign up ****/

.form-control, output {
  color: #aaa;
}

/** Just after e-mail verification **/

/* Milestones */
.milestone-incomplete {
  background-color: #666;
}

.bar-limiter > svg .rc-progress-line-trail {
  stroke: #333;
}

/* Questions content text - 'What type of researcher are you ?' */
.text-secondary {
  color: #666;
}

/* 'What are you interested in?' */
.boxtag-primary-1 {
  background-color: #333;
}

.boxtag-primary-1:focus, .boxtag-primary-1:hover {
  background-color: #444;
}

/** Initial Tutorials Tooltip **/

.tutorialize-tooltip {
  background-color: #282828;
  border-color: #333;
  
}

.tutorialize-tooltip-content {
  color: #999;
}

.tutorialize-tooltip-arrow-border {
  border-right-color: #333 !important;
}

.tutorialize-tooltip-arrow-inner {
  border-right-color: #282828 !important;
}


/**** Home - https://nurture.ai/home ****/


/*** Middle Image column ***/

.landing .papers-showcase .papers-selected .selected-item > img {
  opacity: 0.5;
}

/**** Featured Papers - https://nurture.ai/featured-papers ****/

/*** Middle List of papers ***/

.card, .card-1, .card-2, .card-3, .card-base, .card-interactive {
  background-color: #222;
}

/* Image */
.featured-image img {
  opacity: 0.4;
}

/* Description */
.link-primary {
  color: #999;
}

/* Stars */
.star-input-container label {
  color: #999;
}


/**** Top Recent Papers - https://nurture.ai/top-papers ****/

/** Buttons - 'Yesterday', 'Last 3 days', 'Last Week', ... **/
.boxtag-default {
  background-color: #333;
  color: #999;
}

.boxtag-default:focus, .boxtag-default:hover {
  background-color: #444;
  color: #aaa;
}

/** Middle Paper card **/

/* Ratings box */
.ratings-container {
  border-color: #333;
}

.star-header {
  border-bottom-color: #333;
}

/* Author label */
.roundtag-author {
  background-color: #333;
}

.roundtag-author:focus, .roundtag-author:hover {
  background-color: #444;
}

/* Annotations icon */
.hl-icon {
  border-bottom-color: #666;
}


/******* After Login *******/


/**** Settings Page - https://nurture.ai/user/settings/password ****/

.link-secondary, .link-secondary:active, .link-secondary:focus, .link-secondary:hover {
  color: #999;
}

hr {
  border-top-color: #333;
}

/**** Profile Page - https://nurture.ai/users/<user name> ****/

/** Tab - 'Home', 'Annotations', 'Code Implementations',... **/

.react-tabs__tab {
  color: #999;
}

.react-tabs__tab--selected {
  background-color: #333;
}

/**** Newsfeed - https://nurture.ai/newsfeed ****/

/* Tabs */
.react-tabs__tab-list {
  border-top-color: #333;
  border-bottom-color: #333;
}


/*** Feed Item ***/

.newsfeed-item-container .user-name > a {
  color: #999;
}

.newsfeed-item-container .fs-11 {
  color: #999;
}

.newsfeed-item-container .papername {
  color: #1d70f2; /* #0d62e9; */
}

/* Quoted Text */
.newsfeed-item-container .main-content p.quote-highlight-text {
  border-left-color: #282828;
}

.newsfeed-item-container .paper-link {
  color: #999;
}

/* Likes, comments */
.newsfeed-item-container .interactions > span {
  color: #999;
}

/* separator */
.newsfeed-item-container {
  border-bottom-color: #333;
}

/** Comment Box **/

.newsfeed-item-container .bordered-box {
  border-color: #333;
}

/* Quoted Text */
.newsfeed-item-container .sub-content p.quote-highlight-text {
  border-left-color: #282828;
}

}
