/* Design pour Korben / desktop pour ceux qui ne sont pas aveugles et/ou que ça n'amuse pas de scroller toute la journée. */

@media screen and (min-width: 1036px) {

    /* =========================================================== */
    /* ==== Global =============================================== */
    /* =========================================================== */

    html {
        font-size: 82%;
    }
    body {
		background-color: #f5f5f5;
	}
    p {
        line-height: 1.4;
    }
    hr {
        border: none;
		border: solid 1px #eee;
		margin-bottom: 12px;
    }
    .page-header,
    .page-container,
    .inside-container,
    .app-extra,
    .app-header, 
    .app-footer,
    .bothawui-sous-communaute-article,
    .edito-article {
        min-width: 950px;
        max-width: 950px;
    }

    .page-header,
    .page-container,
    .inside-container,
    .app-extra,
    .bothawui-sous-communaute-article,
    .app-header,
    .bothawui-au-dessus-articles-recents-block-pages-articles,
    .app-footer {
        margin-left: auto;
        margin-right: auto;
    }

    .app-header-left,
    .app-header-right,
    .entry-thumbnail,
    .entry-container:before,
    .entry-footer {
        display: none;  
    }

    /* =========================================================== */
    /* ==== Header =============================================== */
    /* =========================================================== */

    .app-header,
    .app-header-container {
        display: flex;
        align-items: flex-end;
    }
    .app-header {
        justify-content: space-between;
        padding: 10px 20px 15px 15px;
        box-shadow: none;
    }
    .app-header-container,
    .brand-large {
        margin: 0;   
    }
    .app-header-center {
        max-height: 136px;
    }
    .brand svg {
        max-height: 120px;
    }
    .nav-primary {
        font-size: 18px;   
    }

    /* =========================================================== */
    /* ==== Footer =============================================== */
    /* =========================================================== */

    .app-footer-container {
        padding: 15px 0 0 0;   
    }
    .app-footer {
        margin: 0 auto 10px auto;   
        padding: 10px 5px;
    }
    
    /* =========================================================== */
    /* ==== Edito ================================================ */
    /* =========================================================== */

    .edito-article {
        padding-top: 10px;
        margin-bottom: 0;
        margin-top: 10px !important;
        border: none !important;
		box-shadow: none !important;
    }
    .edito-close {
        left: 50%;
        margin-left: 450px;
    }
    .edito .entry-title {
        margin-top: -20px;   
		background: none;
		color: #556d8d;
    }

    /* =========================================================== */
    /* ==== Articles ============================================= */
    /* =========================================================== */

    .sidebar-primary .app-main {
        max-width: 650px;
        min-width: 650px;
        margin-top: 12px;
    }
    article:not(.onscreen-post) {
        margin: 0 12px 12px 0;
        box-shadow: 0 0 3px #aaa;
        border-radius: 0 0 0 10px;
    }
    .entry-container {
        padding: 0;
        width: 100%;
        border-radius: 0;
		max-width: 950px !important;
    }
    .entry-content {
		padding: 12px 12px 2px 12px;
	}
    .bothawui-sous-communaute-article .entry-container {
		min-width: 100%;
		max-width: 950px;
    }
    .bothawui-sous-communaute-article .entry-container .entry-content {
		min-width: 100%;
    }
    .entry-container h2 {
        margin-top: 0;
    }
    .entry-meta {
        margin: 10px 0 10px 0;  
    }
    .entry-meta a {
        padding: 2px 4px 2px 4px;
    }
    article .alignnone {
        min-width: 100%;
    }
    .post-categories, .entry-meta a {
        margin-left: 12px;                                    
	}
    .navigation {
        margin: 20px 0 20px 0;   
    }
    .alignwide {
    	margin: 0 !important;
		width: 100%;
     }
    .entry-content a {
		color: #ff9743;
		background: transparent;
		text-decoration: none;
	}
    .entry-title {
		background-color: #556d8d;
		color: #fff;
		padding: 10px;
    }

    /* =========================================================== */
    /* ==== Sidebar ============================================== */
    /* =========================================================== */

    .app-aside {
        max-width: 300px;
        margin-left: 0;
        margin-right: 0;
        margin-top: 12px;
		background-color: #eaeaea;
    }
    .app-aside > * {
        width: 100%;
    }
    .app-aside .widget-action {
        padding: 10px;
        margin: 0 0 12px 0;
    }
    .app-aside-footer {
    	align-self: flex-start;
    }
    .widget_recent_entries h3 {
        margin-top: 10px;
    }
    
    /* =========================================================== */
    /* ==== Contents under article =============================== */
    /* =========================================================== */

    p.lead {
        margin: 0;
    }

    .bothawui-sous-communaute-article > p {
        display: none;
    }

    body.archive .bothawui-sous-communaute-article > article {
        min-width: 100%;
    }

    /* =========================================================== */
    /* ==== Pages ================================================ */
    /* =========================================================== */

    .page-container .page-divider {
        display: none;
    }
    .page-header, .page-container, .inside-container, .app-extra {
        padding: 0;
    }
    .page-header {
        padding: 20px 0 5px 0;   
    }
    .entry-light {
        display: flex;
        flex-direction: column;
        margin: 10px 0 0 0;
    }
    .widget_recent_entries .entry-light {
        min-height: 166px;
        text-align: right;
    }
    .entry-light-header {
        padding: 10px;
    }
    .entry-light-description {
        padding: 10px;
        background-color: #eaeaea;
        flex: 1;
    }

    /* =========================================================== */
    /* ==== Extras =============================================== */
    /* =========================================================== */

    .app-extra, .app-trending {
        padding: 20px 0 10px 0;
        border-top: solid 20px #eef0f3;
    }

    .bothawui-between-gazouillis {
        max-height: 0;   
    }

    .link-content {
        padding: 20px 0 20px 0;   
    }

    .megaphone {
        margin-top: 10px;   
    }

    /* =========================================================== */
    /* ==== Contenus sponsorisés ================================= */
    /* =========================================================== */

	/* Vous ne voulez pas décommenter ce qui suit... */
    
    .widget-discover,
    .app-extra,
    .bothawui-widget,
    .bothawui-nativestream-p1-1,
    .bothawui-nativestream-p1-2,
    .bothawui-nativestream-p1-3,
    .bothawui-nativestream-p1-4,
    .bothawui-nativestream-p1-5 {
        display: none;   
    }

    /* =========================================================== */
    /* ==== Pubs ================================================= */
    /* =========================================================== */

    .bothawui-widget, .bothawui-au-dessus-articles-recents-block-pages-articles, .bothawui-between-gazouillis {
        background-color: #556d8d;
    }

    .app-aside .bothawui-widget {
		/* Photo de chat au cas où les pubs ne chargent pas */
        background-image: url('https://d32xvgr96w2oxp.cloudfront.net/2015/02/blog_yummypets_sauerkraut_2_2015.jpg');
        background-size: cover;
    }

    .bothawui-au-dessus-articles-recents-block-pages-articles {
        margin-bottom: 20px;
        max-width: 950px;
    }

    .widget_recent_entries,
    .app-aside > .bothawui-widget {
        margin-bottom: 10px;   
    }

    body.home .app-main .bothawui-widget {
        max-width: 640px;
        min-width: 640px;
    }
}
